INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Homage to the Triple Gem', 'chapter', 1);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Dedication of Offerings', 'title', 1);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 1, '<h2>Dedication of Offerings</h2><h2>Buddh&#8217;ādi-āmisa-pūjā</h2><hr/><p class="p3"><span class="t5">Yo so</span></p><p class="p3"><span class="t5">Bhagavā arahaṃ sammāsambuddho</span></p><p class="p3"><span class="t1">To the Blessed One the Worthy One who fully attained Perfect Enlightenment</span></p><p class="p3"><span class="t5">Svākkhāto yena bhagavatā dhammo</span></p><p class="p3"><span class="t1">To the Teaching which he expounded so well</span></p><p class="p3"><span class="t5">Supaṭipanno yassa bhagavato sāvaka-saṅgho</span></p><p class="p3"><span class="t1">And to the Blessed One&#8217;s disciples who have practiced well</span></p><p class="p3"><span class="t5">Tam&#8217;mayaṃ bhagavantaṃ sadhammaṃ sasaṅghaṃ</span></p><p class="p3"><span class="t1">To these the Buddha the Dhamma and the Saṅgha</span></p><p class="p3"><span class="t5">Imehi sakkārehi yath&#8217;ārahaṃ āropitehi abhipūjayāma</span></p><p class="p3"><span class="t1">We render with offerings our rightful homage</span></p><p class="p3"><span class="t5">Sādhu no bhante bhagavā sucira-parinibbuto&#8217;pi</span></p><p class="p3"><span class="t1">It is well for us that the Blessed One</span></p><p class="p3"><span class="t1">Having attained liberation</span></p><p class="p3"><span class="t5">Pacchimā-janat&#8217;ānukampa-mānasā</span></p><p class="p3"><span class="t1">Still had compassion for later generations</span></p><p class="p3"><span class="t5">Ime sakkāre duggata-paṇṇākāra-bhūte paṭiggaṇhātu</span></p><p class="p3"><span class="t1">May these simple offerings be accepted</span></p><p class="p3"><span class="t5">Amhākaṃ dīgharattaṃ hitāya sukhāya</span></p><p class="p3"><span class="t1">For our long-lasting benefit and for the happiness it gives us</span></p><p class="p3"><span class="t5">Arahaṃ</span></p><p class="p3"><span class="t5">Sammāsambuddho bhagavā</span></p><p class="p3"><span class="t1">The Worthy One the Perfectly Enlightened and Blessed One</span></p><p class="p3"><span class="t5">Buddhaṃ bhagavantaṃ abhivādemi</span></p><p class="p3"><span class="t1">I render homage to the Buddha the Blessed One </span></p><p class="p3"><span class="t5">Svākkhāto</span></p><p class="p3"><span class="t5">Bhagavatā dhammo</span></p><p class="p3"><span class="t1">The Teaching so completely explained by him</span></p><p class="p3"><span class="t5">Dhammaṃ namassāmi</span></p><p class="p3"><span class="t1">I bow to the Dhamma </span></p><p class="p3"><span class="t5">Supaṭipanno</span></p><p class="p3"><span class="t5">Bhagavato sāvaka-saṅgho</span></p><p class="p3"><span class="t1">The Blessed One&#8217;s disciples who have practiced well</span></p><p class="p3"><span class="t5">Saṅghaṃ namāmi</span></p><p class="p3"><span class="t1">I bow to the Saṅgha </span></p><h2>    <p class="p3"><span class="t5">[Trad]</span></p></h2>', '-1-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Preliminary Homage', 'title', 2);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 2, '<h2>Preliminary Homage</h2><h2>Pubbabhāga-namakāra</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ buddhassa bhagavato pubbabhāga-namakāraṃ karomase</span></p><p class="p3"><span class="t1">Now let us pay preliminary homage to the Buddha</span></p><p class="p3"><span class="t5">Namo tassa bhagavato arahato sammāsambuddhassa </span></p><p class="p3"><span class="t1">I render homage to the Buddha the Blessed One </span></p><p class="p3"><span class="t1">Homage to the Blessed Worthy and Perfectly Enlightened One </span></p><h2><p class="p3"><span class="t5">[DN 21]</span></p></h2>', '-2-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Homage to the Buddha', 'title', 3);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 3, '<h2>Homage to the Buddha</h2><h2>Buddh&#8217;ābhitthuti</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ buddh&#8217;ābhitthutiṃ karomase</span></p><p class="p3"><span class="t1">Now let us recite in praise of the Buddha</span></p><p class="p3"><span class="t5">Yo so tathāgato arahaṃ sammāsambuddho</span></p><p class="p3"><span class="t1">The Tathāgata is the Worthy One the Perfectly Enlightened One</span></p><p class="p3"><span class="t5">Vijjācaraṇa-sampanno</span></p><p class="p3"><span class="t1">He is impeccable in conduct and understanding</span></p><p class="p3"><span class="t5">Sugato</span></p><p class="p3"><span class="t1">The Accomplished One</span></p><p class="p3"><span class="t5">Lokavidū</span></p><p class="p3"><span class="t1">The Knower of the Worlds</span></p><p class="p3"><span class="t5">Anuttaro purisadamma-sārathi</span></p><p class="p3"><span class="t1">Unsurpassed leader of persons to be tamed</span></p><p class="p3"><span class="t5">Satthā deva-manussānaṃ</span></p><p class="p3"><span class="t1">He is teacher of gods and humans</span></p><p class="p3"><span class="t5">Buddho bhagavā</span></p><p class="p3"><span class="t1">He is awake and holy</span></p><p class="p3"><span class="t5">Yo imaṃ lokaṃ sadevakaṃ samārakaṃ sabrahmakaṃ</span></p><p class="p3"><span class="t1">In this world with its gods demons and kind spirits</span></p><p class="p3"><span class="t5">Sassamaṇa-brāhmaṇiṃ pajaṃ sadeva-manussaṃ sayaṃ abhiññā sacchikatvā pavedesi</span></p><p class="p3"><span class="t1">Its seekers and sages celestial and human beings</span></p><p class="p3"><span class="t1">He has by deep insight revealed the truth</span></p><p class="p3"><span class="t5">Yo dhammaṃ desesi ādi-kalyāṇaṃ majjhe-kalyāṇaṃ pariyosāna-kalyāṇaṃ</span></p><p class="p3"><span class="t1">He has pointed out the Dhamma</span></p><p class="p3"><span class="t1">Beautiful in the beginning</span></p><p class="p3"><span class="t1">Beautiful in the middle</span></p><p class="p3"><span class="t1">Beautiful in the end</span></p><p class="p3"><span class="t5">Sātthaṃ sabyañjanaṃ kevala-paripuṇṇaṃ parisuddhaṃ brahmacariyaṃ pakāsesi</span></p><p class="p3"><span class="t1">He has explained the holy life of complete purity</span></p><p class="p3"><span class="t1">In its essence and conventions</span></p><h2><p class="p3"><span class="t5">[SN 55.7]</span></p></h2><p class="p3"><span class="t5">Tam&#8217;ahaṃ bhagavantaṃ abhipūjayāmi tam&#8217;ahaṃ bhagavantaṃ sirasā namāmi</span></p><p class="p3"><span class="t1">I chant my praise to the Blessed One</span></p><p class="p3"><span class="t1">I bow my head to the Blessed One </span></p><h2><p class="p3"><span class="t5">[Thai]</span></p></h2>', '-3-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Homage to the Dhamma', 'title', 4);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 4, '<h2>Homage to the Dhamma</h2><h2>Dhamm&#8217;ābhitthuti</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ dhamm&#8217;ābhitthutiṃ karomase</span></p><p class="p3"><span class="t1">Now let us recite in praise of the Dhamma</span></p><p class="p3"><span class="t5">Yo so svākkhāto bhagavatā dhammo</span></p><p class="p3"><span class="t1">The Dhamma is well-expounded by the Blessed One</span></p><p class="p3"><span class="t5">Sandiṭṭhiko</span></p><p class="p3"><span class="t1">Apparent here and now</span></p><p class="p3"><span class="t5">Akāliko</span></p><p class="p3"><span class="t1">Timeless</span></p><p class="p3"><span class="t5">Ehipassiko</span></p><p class="p3"><span class="t1">Encouraging investigation</span></p><p class="p3"><span class="t5">Opanayiko</span></p><p class="p3"><span class="t1">Leading inwards</span></p><p class="p3"><span class="t5">Paccattaṃ veditabbo viññūhi</span></p><p class="p3"><span class="t1">To be experienced individually by the wise</span></p><h2><p class="p3"><span class="t5">[SN 12.41]</span></p></h2><p class="p3"><span class="t5">Tam&#8217;ahaṃ dhammaṃ abhipūjayāmi tam&#8217;ahaṃ dhammaṃ sirasā namāmi</span></p><p class="p3"><span class="t1">I chant my praise to this teaching</span></p><p class="p3"><span class="t1">I bow my head to this truth </span></p><h2><p class="p3"><span class="t5">[Thai]</span></p></h2>', '-4-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Homage to the Saṅgha', 'title', 5);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 5, '<h2>Homage to the Saṅgha</h2><h2>Saṅgh&#8217;ābhitthuti</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ saṅgh&#8217;ābhitthutiṃ karomase</span></p><p class="p3"><span class="t1">Now let us recite in praise of the Saṅgha</span></p><p class="p3"><span class="t5">Yo so supaṭipanno bhagavato sāvaka-saṅgho</span></p><p class="p3"><span class="t1">They are the Blessed One&#8217;s disciples who have practiced well</span></p><p class="p3"><span class="t5">Uju-paṭipanno bhagavato sāvaka-saṅgho</span></p><p class="p3"><span class="t1">Who have practiced directly</span></p><p class="p3"><span class="t5">Ñāya-paṭipanno bhagavato sāvaka-saṅgho</span></p><p class="p3"><span class="t1">Who have practiced correctly</span></p><p class="p3"><span class="t5">Sāmīci-paṭipanno bhagavato sāvaka-saṅgho</span></p><p class="p3"><span class="t1">Who have practiced properly</span></p><p class="p3"><span class="t5">Yad&#8217;idaṃ cattāri purisa-yugāni aṭṭha purisa-puggalā</span></p><p class="p3"><span class="t1">That is the four pairs the eight kinds of Noble Beings</span></p><p class="p3"><span class="t5">Esa bhagavato sāvaka-saṅgho</span></p><p class="p3"><span class="t1">These are the Blessed One&#8217;s disciples</span></p><p class="p3"><span class="t5">Āhuneyyo</span></p><p class="p3"><span class="t1">Such ones are worthy of gifts</span></p><p class="p3"><span class="t5">Pāhuneyyo</span></p><p class="p3"><span class="t1">Worthy of hospitality</span></p><p class="p3"><span class="t5">Dakkhiṇeyyo</span></p><p class="p3"><span class="t1">Worthy of offerings</span></p><p class="p3"><span class="t5">Añjali-karaṇīyo</span></p><p class="p3"><span class="t1">Worthy of respect</span></p><p class="p3"><span class="t5">Anuttaraṃ puñña-kkhettaṃ lokassa</span></p><p class="p3"><span class="t1">The unsurpassed field of merit for the world</span></p><h2><p class="p3"><span class="t5">[SN 12.41]</span></p></h2><p class="p3"><span class="t5">Tam&#8217;ahaṃ saṅghaṃ abhipūjayāmi tam&#8217;ahaṃ saṅghaṃ sirasā namāmi</span></p><p class="p3"><span class="t1">I chant my praise to this Saṅgha</span></p><p class="p3"><span class="t1">I bow my head to this Saṅgha </span></p><h2><p class="p3"><span class="t5">[Thai]</span></p></h2>', '-5-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Salutation to the Triple Gem', 'title', 6);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 6, '<h2>Salutation to the Triple Gem</h2><h2>Ratanattaya-paṇāma</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ ratanattaya-paṇāma-gāthāyo c&#8217;eva saṃvega-parikittana-pāṭhañ&#8217;ca bhaṇāmase</span></p><p class="p3"><span class="t1">Now let us recite our salutation to the Triple Gem and a passage to arouse urgency</span></p><p class="p3"><span class="t5">Buddho susuddho karuṇā-mah&#8217;aṇṇavo</span></p><p class="p3"><span class="t1">The Buddha absolutely pure with ocean-like compassion</span></p><p class="p3"><span class="t5">Yo&#8217;ccanta-suddhabbara-ñāṇa-locano</span></p><p class="p3"><span class="t1">Possessing the clear sight of wisdom</span></p><p class="p3"><span class="t5">Lokassa pāp&#8217;ūpakilesa-ghātako</span></p><p class="p3"><span class="t1">Destroyer of worldly self-corruption</span></p><p class="p3"><span class="t5">Vandāmi buddhaṃ aham&#8217;ādarena taṃ</span></p><p class="p3"><span class="t1">Devotedly indeed that Buddha I revere</span></p><p class="p3"><span class="t5">Dhammo padīpo viya tassa satthuno</span></p><p class="p3"><span class="t1">The Teaching of the Lord is like a lamp</span></p><p class="p3"><span class="t5">Yo magga-pāk&#8217;āmata-bheda-bhinnako</span></p><p class="p3"><span class="t1">Divided into path and its fruit the Deathless</span></p><p class="p3"><span class="t5">Lok&#8217;uttaro yo ca tad&#8217;attha-dīpano</span></p><p class="p3"><span class="t1">And illuminating that goal which is beyond the conditioned world</span></p><p class="p3"><span class="t5">Vandāmi dhammaṃ aham&#8217;ādarena taṃ</span></p><p class="p3"><span class="t1">Devotedly indeed that Dhamma I revere</span></p><p class="p3"><span class="t5">Saṅgho sukhett&#8217;ābhyati-khetta-saññito</span></p><p class="p3"><span class="t1">The Saṅgha the most fertile ground for cultivation</span></p><p class="p3"><span class="t5">Yo diṭṭha-santo sugat&#8217;ānubodhako</span></p><p class="p3"><span class="t1">Those who have realised peace</span></p><p class="p3"><span class="t1">Awakened after the Accomplished One</span></p><p class="p3"><span class="t5">Lolappahīno ariyo sumedhaso</span></p><p class="p3"><span class="t1">Noble and wise all longing abandoned</span></p><p class="p3"><span class="t5">Vandāmi saṅghaṃ aham&#8217;ādarena taṃ</span></p><p class="p3"><span class="t1">Devotedly indeed that Saṅgha I revere</span></p><p class="p3"><span class="t5">Icc&#8217;evam&#8217;ekant&#8217;abhipūjaneyyakaṃ</span></p><p class="p3"><span class="t5">Vatthu-ttayaṃ vandayat&#8217;ābhisaṅkhataṃ</span></p><p class="p3"><span class="t1">This salutation should be made</span></p><p class="p3"><span class="t1">To that triad which is worthy</span></p><p class="p3"><span class="t5">Puññaṃ mayā yaṃ mama sabb&#8217;upaddavā</span></p><p class="p3"><span class="t1">Through the power of such good action</span></p><p class="p3"><span class="t5">Mā hontu ve tassa pabhāva-siddhiyā</span></p><p class="p3"><span class="t1">May all obstacles disappear</span></p><p class="p3"><span class="t5">Idha tathāgato loke uppanno arahaṃ sammāsambuddho</span></p><p class="p3"><span class="t1">One who knows things as they are has arisen in this world</span></p><p class="p3"><span class="t1">And he is an Arahant a perfectly awakened being</span></p><p class="p3"><span class="t5">Dhammo ca desito niyyāniko upasamiko parinibbāniko sambodhagāmī sugata-ppavedito</span></p><p class="p3"><span class="t1">Teaching the way leading out of delusion</span></p><p class="p3"><span class="t1">Calming and directing to perfect peace</span></p><p class="p3"><span class="t1">And leading to enlightenment</span></p><p class="p3"><span class="t1">This way he has made known</span></p><p class="p3"><span class="t5">Mayan&#8217;taṃ dhammaṃ sutvā evaṃ jānāma</span></p><p class="p3"><span class="t1">Having heard the Teaching we know this</span></p><h2><p class="p3"><span class="t5">[Thai]</span></p></h2><p class="p3"><span class="t5">Jāti&#8217;pi dukkhā</span></p><p class="p3"><span class="t1">Birth is dukkha</span></p><p class="p3"><span class="t5">Jarā&#8217;pi dukkhā</span></p><p class="p3"><span class="t1">Ageing is dukkha</span></p><p class="p3"><span class="t5">Maraṇam&#8217;pi dukkhaṃ</span></p><p class="p3"><span class="t1">And death is dukkha</span></p><p class="p3"><span class="t5">Soka-parideva-dukkha-domanass&#8217;upāyāsā&#8217;pi dukkhā</span></p><p class="p3"><span class="t1">Sorrow lamentation pain displeasure and despair are dukkha</span></p><p class="p3"><span class="t5">Appiyehi sampayogo dukkho</span></p><p class="p3"><span class="t1">Association with the disliked is dukkha</span></p><p class="p3"><span class="t5">Piyehi vippayogo dukkho</span></p><p class="p3"><span class="t1">Separation from the liked is dukkha</span></p><p class="p3"><span class="t5">Yam&#8217;p&#8217;icchaṃ na labhati tam&#8217;pi dukkhaṃ</span></p><p class="p3"><span class="t1">Not attaining one&#8217;s wishes is dukkha</span></p><h2><p class="p3"><span class="t5">[MN 10]</span></p></h2><p class="p3"><span class="t5">Saṅkhittena pañc&#8217;upādāna-kkhandhā dukkhā</span></p><p class="p3"><span class="t1">In brief the five aggregates of clinging are dukkha</span></p><p class="p3"><span class="t5">Seyyath&#8217;īdaṃ</span></p><p class="p3"><span class="t1">These are as follows</span></p><p class="p3"><span class="t5">Rūp&#8217;ūpādāna-kkhandho</span></p><p class="p3"><span class="t1">Attachment to form</span></p><p class="p3"><span class="t5">Vedan&#8217;ūpādāna-kkhandho</span></p><p class="p3"><span class="t1">Attachment to feeling</span></p><p class="p3"><span class="t5">Saññ&#8217;ūpādāna-kkhandho</span></p><p class="p3"><span class="t1">Attachment to perception</span></p><p class="p3"><span class="t5">Saṅkhār&#8217;ūpādāna-kkhandho</span></p><p class="p3"><span class="t1">Attachment to volitional formations</span></p><p class="p3"><span class="t5">Viññāṇ&#8217;ūpādāna-kkhandho</span></p><p class="p3"><span class="t1">Attachment to consciousness</span></p><h2><p class="p3"><span class="t5">[DN 22]</span></p></h2><p class="p3"><span class="t5">Yesaṃ pariññāya</span></p><p class="p3"><span class="t1">For the complete understanding of this</span></p><p class="p3"><span class="t5">Dharamāno so bhagavā</span></p><p class="p3"><span class="t1">The Blessed One in his lifetime</span></p><p class="p3"><span class="t5">Evaṃ bahulaṃ sāvake vineti</span></p><p class="p3"><span class="t1">Frequently instructed his disciples in just this way</span></p><p class="p3"><span class="t5">Evaṃ bhāgā ca pan&#8217;assa bhagavato sāvakesu anusāsanī bahulā pavattati</span></p><p class="p3"><span class="t1">In addition he further instructed</span></p><h2><p class="p3"><span class="t5">[Thai]</span></p></h2><p class="p3"><span class="t5">Rūpaṃ aniccaṃ</span></p><p class="p3"><span class="t1">Form is impermanent</span></p><p class="p3"><span class="t5">Vedanā aniccā</span></p><p class="p3"><span class="t1">Feeling is impermanent</span></p><p class="p3"><span class="t5">Saññā aniccā</span></p><p class="p3"><span class="t1">Perception is impermanent</span></p><p class="p3"><span class="t5">Saṅkhārā aniccā</span></p><p class="p3"><span class="t1">Volitional formations are impermanent</span></p><p class="p3"><span class="t5">Viññāṇaṃ aniccaṃ</span></p><p class="p3"><span class="t1">Consciousness is impermanent</span></p><p class="p3"><span class="t5">Rūpaṃ anattā</span></p><p class="p3"><span class="t1">Form is not-self</span></p><p class="p3"><span class="t5">Vedanā anattā</span></p><p class="p3"><span class="t1">Feeling is not-self</span></p><p class="p3"><span class="t5">Saññā anattā</span></p><p class="p3"><span class="t1">Perception is not-self</span></p><p class="p3"><span class="t5">Saṅkhārā anattā</span></p><p class="p3"><span class="t1">Volitional formations are not-self</span></p><p class="p3"><span class="t5">Viññāṇaṃ anattā</span></p><p class="p3"><span class="t1">Consciousness is not-self</span></p><p class="p3"><span class="t5">Sabbe saṅkhārā aniccā</span></p><p class="p3"><span class="t1">All conditioned things are impermanent</span></p><p class="p3"><span class="t5">Sabbe dhammā anattā&#8217;ti</span></p><p class="p3"><span class="t1">All things are not-self</span></p><h2><p class="p3"><span class="t5">[MN 35]</span></p></h2><p class="p3"><span class="t5">Te mayaṃ otiṇṇ&#8217;āmha jātiyā jarā-maraṇena</span></p><p class="p3"><span class="t1">All of us are affected by birth ageing and death</span></p><p class="p3"><span class="t5">Sokehi paridevehi dukkhehi domanassehi upāyāsehi</span></p><p class="p3"><span class="t1">By sorrow lamentation pain displeasure and despair</span></p><p class="p3"><span class="t5">Dukkh&#8217;otiṇṇā dukkha-paretā</span></p><p class="p3"><span class="t1">Affected by dukkha and afflicted by dukkha</span></p><p class="p3"><span class="t5">App&#8217;eva nām&#8217;imassa kevalassa dukkha-kkhandhassa antakiriyā paññāyethā&#8217;ti</span></p><p class="p3"><span class="t1">Let us all aspire to complete freedom from suffering</span></p><h2><p class="p3"><span class="t5">[SN 22.80]</span></p></h2><p class="p3"><span class="t5">Cira-parinibbutam&#8217;pi taṃ bhagavantaṃ uddissa arahantaṃ sammāsambuddhaṃ</span></p><p class="p3"><span class="t1">Remembering the Blessed One the Worthy One and Perfectly Enlightened One</span></p><p class="p3"><span class="t1">Who long ago attained Parinibbāna</span></p><p class="p3"><span class="t5">Saddhā agārasmā anagāriyaṃ pabbajitā</span></p><p class="p3"><span class="t1">We have gone forth with faith</span></p><p class="p3"><span class="t1">From home to homelessness</span></p><p class="p3"><span class="t5">Tasmiṃ bhagavati brahmacariyaṃ carāma</span></p><p class="p3"><span class="t1">And like the Blessed One we practice the holy life</span></p><p class="p3"><span class="t5">Bhikkhūnaṃ sikkhā-sājīva-samāpannā</span></p><p class="p3"><span class="t1">Possessing the bhikkhus training and way of life</span></p><p class="p3"><span class="t5">Taṃ no brahmacariyaṃ imassa kevalassa dukkha-kkhandhassa antakiriyāya saṃvattatu</span></p><p class="p3"><span class="t1">May this holy life lead us to the end of this whole mass of suffering</span></p><h2><p class="p3"><span class="t5">[Thai]</span></p></h2>', '-6-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Closing Homage', 'title', 7);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 7, '<h2>Closing Homage</h2><h2>Pacchima-vandanā</h2><hr/><p class="p3"><span class="t5">Arahaṃ</span></p><p class="p3"><span class="t5">Sammāsambuddho bhagavā</span></p><p class="p3"><span class="t1">The Worthy One the Perfectly Enlightened and Blessed One</span></p><p class="p3"><span class="t5">Buddhaṃ bhagavantaṃ abhivādemi</span></p><p class="p3"><span class="t1">I render homage to the Buddha the Blessed One </span></p><p class="p3"><span class="t5">Svākkhāto</span></p><p class="p3"><span class="t5">Bhagavatā dhammo</span></p><p class="p3"><span class="t1">The Teaching so completely explained by him</span></p><p class="p3"><span class="t5">Dhammaṃ namassāmi</span></p><p class="p3"><span class="t1">I bow to the Dhamma</span></p><p class="p3"><span class="t5">Supaṭipanno</span></p><p class="p3"><span class="t5">Bhagavato sāvaka-saṅgho</span></p><p class="p3"><span class="t1">The Blessed One&#8217;s disciples who have practiced well</span></p><p class="p3"><span class="t5">Saṅghaṃ namāmi</span></p><p class="p3"><span class="t1">I bow to the Saṅgha</span></p><h2><p class="p3"><span class="t5">[Thai]</span></p></h2>', '-7-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Verses', 'chapter', 8);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Buddhas Final Instruction', 'title', 8);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 8, '<h2>The Buddha&#8217;s Final Instruction</h2><h2>Buddha-pacchima-ovāda</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ buddha-pacchima-ovāda-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Siyā kho tumhākaṃ evam&#8217;assa</span></p><p class="p3"><span class="t1">Now if it occurs to you</span></p><p class="p3"><span class="t5">Atīta-satthukaṃ pāvacanaṃ n&#8217;atthi no satthā&#8217;ti</span></p><p class="p3"><span class="t1"><q>The Teacher&#8217;s word has passed we are without a teacher</q></span></p><p class="p3"><span class="t5">Na kho pan&#8217;etaṃ evaṃ daṭṭhabbaṃ</span></p><p class="p3"><span class="t1">You should not view it this way</span></p><p class="p3"><span class="t5">Yo vo mayā dhammo ca vinayo ca desito paññatto</span></p><p class="p3"><span class="t5">So vo mam&#8217;accayena satthā</span></p><p class="p3"><span class="t1">Whatever Dhamma and Vinaya</span></p><p class="p3"><span class="t1">I have pointed out and formulated for you</span></p><p class="p3"><span class="t1">That will be your teacher when I am gone</span></p><p class="p3"><span class="t5">Handa dāni bhikkhave āmantayāmi vo</span></p><p class="p3"><span class="t1">Now bhikkhus I declare to you</span></p><p class="p3"><span class="t5">Vaya-dhammā saṅkhārā</span></p><p class="p3"><span class="t1">Conditioned things are of ceasing nature</span></p><p class="p3"><span class="t5">Appamādena sampādetha</span></p><p class="p3"><span class="t1">Perfect yourselves not being negligent</span></p><p class="p3"><span class="t5">Ayaṃ tathāgatassa pacchimā vācā</span></p><p class="p3"><span class="t1">These are the Tathāgata&#8217;s final words</span></p><h2><p class="p3"><span class="t5">[DN 16]</span></p></h2>', '-8-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Respect for the Dhamma', 'title', 9);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 9, '<h2>Respect for the Dhamma</h2><h2>Dhamma-gārava</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ dhamma-gārav&#8217;ādi-gāthāyo bhaṇāmase</span></p><p class="p3"><span class="t5">Ye ca atītā sambuddhā – Ye ca buddhā anāgatā</span></p><p class="p3"><span class="t5">Yo c&#8217;eta&#8217;r&#8217;ahi sambuddho – Bahunnaṃ soka-nāsano</span></p><p class="p3"><span class="t1">All the Buddhas of the past</span></p><p class="p3"><span class="t1">All the Buddhas yet to come</span></p><p class="p3"><span class="t1">The Buddha of this current age</span></p><p class="p3"><span class="t1">Dispellers of much sorrow</span></p><p class="p3"><span class="t5">Sabbe saddhamma-garuno – Vihariṃsu viharanti ca</span></p><p class="p3"><span class="t5">Atho&#8217;pi viharissanti – Esā buddhāna dhammatā</span></p><p class="p3"><span class="t1">Those having lived or living now</span></p><p class="p3"><span class="t1">Those living in the future</span></p><p class="p3"><span class="t1">All do revere the True Dhamma</span></p><p class="p3"><span class="t1">That is the nature of all Buddhas</span></p><p class="p3"><span class="t5">Tasmā hi atta-kāmena – Mahattam&#8217;abhikaṅkhatā</span></p><p class="p3"><span class="t5">Saddhammo garu-kātabbo – Saraṃ buddhāna&#8217;sāsanaṃ</span></p><p class="p3"><span class="t1">Therefore desiring one&#8217;s own welfare</span></p><p class="p3"><span class="t1">Pursuing greatest aspirations</span></p><p class="p3"><span class="t1">One should revere the True Dhamma</span></p><p class="p3"><span class="t1">Recollecting the Buddha&#8217;s teaching</span></p><h2><p class="p3"><span class="t5">[SN 6.2]</span></p></h2><p class="p3"><span class="t5">Na hi dhammo adhammo ca – Ubho sama-vipākino</span></p><p class="p3"><span class="t5">Adhammo nirayaṃ neti – Dhammo pāpeti suggatiṃ</span></p><p class="p3"><span class="t1">What is true Dhamma and what&#8217;s not</span></p><p class="p3"><span class="t1">Will never have the same results</span></p><p class="p3"><span class="t1">While wrong Dhamma leads to hell realms</span></p><p class="p3"><span class="t1">True Dhamma takes one on a good course</span></p><p class="p3"><span class="t5">Dhammo have rakkhati dhamma-cāriṃ</span></p><p class="p3"><span class="t5">Dhammo suciṇṇo sukham&#8217;āvahāti</span></p><p class="p3"><span class="t5">Es&#8217;ānisaṃso dhamme suciṇṇe</span></p><p class="p3"><span class="t5">Na duggatiṃ gacchati dhamma-cārī</span></p><p class="p3"><span class="t1">The Dhamma guards those who live in line with it</span></p><p class="p3"><span class="t1">And leads to happiness when practised well</span></p><p class="p3"><span class="t1">This is the blessing of well-practised Dhamma</span></p><p class="p3"><span class="t1">The Dhamma-farer does not go on a bad course</span></p><h2><p class="p3"><span class="t5">[Thag 4.10]</span></p></h2>', '-9-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Going to True and False Refuges', 'title', 10);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 10, '<h2>Going to True and False Refuges</h2><h2>Khem&#8217;ākhema-saraṇa-gamana</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ khem&#8217;ākhema-saraṇa-gamana-paridīpikā-gāthāyo bhaṇāmase</span></p><p class="p3"><span class="t5">Bahuṃ ve saraṇaṃ yanti – Pabbatāni vanāni ca</span></p><p class="p3"><span class="t5">Ārāma-rukkha-cetyāni – Manussā bhaya-tajjitā</span></p><p class="p3"><span class="t1">To many refuges they go</span></p><p class="p3"><span class="t1">To mountain slopes and forest glades</span></p><p class="p3"><span class="t1">To parkland shrines and sacred sites</span></p><p class="p3"><span class="t1">People overcome by fear</span></p><p class="p3"><span class="t5">N&#8217;etaṃ kho saraṇaṃ khemaṃ – N&#8217;etaṃ saraṇam&#8217;uttamaṃ</span></p><p class="p3"><span class="t5">N&#8217;etaṃ saraṇam&#8217;āgamma – Sabba-dukkhā pamuccati</span></p><p class="p3"><span class="t1">Such a refuge is not secure</span></p><p class="p3"><span class="t1">Such a refuge is not supreme</span></p><p class="p3"><span class="t1">Such a refuge does not bring</span></p><p class="p3"><span class="t1">Complete release from all suffering</span></p><p class="p3"><span class="t5">Yo ca buddhañ&#8217;ca dhammañ&#8217;ca – Saṅghañ&#8217;ca saraṇaṃ gato</span></p><p class="p3"><span class="t5">Cattāri ariya-saccāni – Samma-ppaññāya passati</span></p><p class="p3"><span class="t1">Whoever goes for refuge</span></p><p class="p3"><span class="t1">To the Buddha the Dhamma and the Saṅgha</span></p><p class="p3"><span class="t1">Sees with right discernment</span></p><p class="p3"><span class="t1">The Four Noble Truths</span></p><p class="p3"><span class="t5">Dukkhaṃ dukkha-samuppādaṃ – Dukkhassa ca atikkamaṃ</span></p><p class="p3"><span class="t5">Ariyañ&#8217;c&#8217;aṭṭh&#8217;aṅgikaṃ maggaṃ – Dukkh&#8217;ūpasama-gāminaṃ</span></p><p class="p3"><span class="t1">Suffering and its origin</span></p><p class="p3"><span class="t1">And that which lies beyond</span></p><p class="p3"><span class="t1">The Noble Eightfold Path</span></p><p class="p3"><span class="t1">That leads the way to suffering&#8217;s end</span></p><p class="p3"><span class="t5">Etaṃ kho saraṇaṃ khemaṃ – Etaṃ saraṇam&#8217;uttamaṃ</span></p><p class="p3"><span class="t5">Etaṃ saraṇam&#8217;āgamma – Sabba-dukkhā pamuccati</span></p><p class="p3"><span class="t1">Such a refuge is secure</span></p><p class="p3"><span class="t1">Such a refuge is supreme</span></p><p class="p3"><span class="t1">Such a refuge truly brings</span></p><p class="p3"><span class="t1">Complete release from all suffering</span></p><h2><p class="p3"><span class="t5">[Dhp 188-192]</span></p></h2>', '-10-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Pāṭimokkha Exhortation', 'title', 11);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 11, '<h2>The Pāṭimokkha Exhortation</h2><h2>Ovāda-pāṭimokkha-gāthā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ ovāda-pāṭimokkha-gāthāyo bhaṇāmase</span></p><p class="p3"><span class="t5">Sabba-pāpassa akaraṇaṃ</span></p><p class="p3"><span class="t1">Not doing any evil</span></p><p class="p3"><span class="t5">Kusalass&#8217;ūpasampadā</span></p><p class="p3"><span class="t1">To be committed to the good</span></p><p class="p3"><span class="t5">Sacitta-pariyodapanaṃ</span></p><p class="p3"><span class="t1">To purify one&#8217;s mind</span></p><p class="p3"><span class="t5">Etaṃ buddhāna&#8217;sāsanaṃ</span></p><p class="p3"><span class="t1">These are the teachings of all Buddhas</span></p><p class="p3"><span class="t5">Khantī paramaṃ tapo tītikkhā</span></p><p class="p3"><span class="t1">Patient endurance is the highest practice burning out defilements</span></p><p class="p3"><span class="t5">Nibbānaṃ paramaṃ vadanti buddhā</span></p><p class="p3"><span class="t1">The Buddhas say Nibbāna is supreme</span></p><p class="p3"><span class="t5">Na hi pabbajito par&#8217;ūpaghātī</span></p><p class="p3"><span class="t1">Not a renunciant is one who injures others</span></p><p class="p3"><span class="t5">Samaṇo hoti paraṃ viheṭhayanto</span></p><p class="p3"><span class="t1">Whoever troubles others can&#8217;t be called a monk</span></p><p class="p3"><span class="t5">Anūpavādo anūpaghāto</span></p><p class="p3"><span class="t1">Not to insult and not to injure</span></p><p class="p3"><span class="t5">Pāṭimokkhe ca saṃvaro</span></p><p class="p3"><span class="t1">To live restrained by training rules</span></p><p class="p3"><span class="t5">Mattaññutā ca bhattasmiṃ</span></p><p class="p3"><span class="t1">Knowing one&#8217;s measure at the meal</span></p><p class="p3"><span class="t5">Pantañ&#8217;ca sayan&#8217;āsanaṃ</span></p><p class="p3"><span class="t1">Retreating to a lonely place</span></p><p class="p3"><span class="t5">Adhicitte ca āyogo</span></p><p class="p3"><span class="t1">Devotion to the higher mind</span></p><p class="p3"><span class="t5">Etaṃ buddhāna&#8217;sāsanaṃ</span></p><p class="p3"><span class="t1">These are the teachings of all Buddhas</span></p><h2><p class="p3"><span class="t5">[Dhp 183-185]</span></p></h2>', '-11-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Three Characteristics', 'title', 12);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 12, '<h2>The Three Characteristics</h2><h2>Ti-lakkhaṇā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ ti-lakkhaṇ&#8217;ādi-gāthāyo bhaṇāmase</span></p><p class="p3"><span class="t5">Sabbe saṅkhārā aniccā&#8217;ti – Yadā paññāya passati</span></p><p class="p3"><span class="t5">Atha nibbindati dukkhe – Esa maggo visuddhiyā</span></p><p class="p3"><span class="t1"><q>All conditioned things are impermanent</q></span></p><p class="p3"><span class="t1">When with wisdom this is seen</span></p><p class="p3"><span class="t1">One feels weary of all dukkha</span></p><p class="p3"><span class="t1">This is the path to purity</span></p><p class="p3"><span class="t5">Sabbe saṅkhārā dukkhā&#8217;ti – Yadā paññāya passati</span></p><p class="p3"><span class="t5">Atha nibbindati dukkhe – Esa maggo visuddhiyā</span></p><p class="p3"><span class="t1"><q>All conditioned things are dukkha</q></span></p><p class="p3"><span class="t1">When with wisdom this is seen</span></p><p class="p3"><span class="t1">One feels weary of all dukkha</span></p><p class="p3"><span class="t1">This is the path to purity</span></p><p class="p3"><span class="t5">Sabbe dhammā anattā&#8217;ti – Yadā paññāya passati</span></p><p class="p3"><span class="t5">Atha nibbindati dukkhe – Esa maggo visuddhiyā</span></p><p class="p3"><span class="t1"><q>All things are not-self</q></span></p><p class="p3"><span class="t1">When with wisdom this is seen</span></p><p class="p3"><span class="t1">One feels weary of all dukkha</span></p><p class="p3"><span class="t1">This is the path to purity</span></p><h2><p class="p3"><span class="t5">[Dhp 277-279]</span></p></h2><p class="p3"><span class="t5">Appakā te manussesu – Ye janā pāra-gāmino</span></p><p class="p3"><span class="t5">Ath&#8217;āyaṃ itarā pajā – Tīram&#8217;ev&#8217;ānudhāvati</span></p><p class="p3"><span class="t1">Few amongst humankind</span></p><p class="p3"><span class="t1">Are those who go beyond</span></p><p class="p3"><span class="t1">Yet there are the many folks</span></p><p class="p3"><span class="t1">Ever wandering on this shore</span></p><p class="p3"><span class="t5">Ye ca kho samma&#8217;d&#8217;akkhāte – Dhamme dhamm&#8217;ānuvattino</span></p><p class="p3"><span class="t5">Te janā pāram&#8217;essanti – Maccu-dheyyaṃ suduttaraṃ</span></p><p class="p3"><span class="t1">Wherever Dhamma is well-taught</span></p><p class="p3"><span class="t1">Those who train in line with it</span></p><p class="p3"><span class="t1">Are the ones who will cross over</span></p><p class="p3"><span class="t1">The realm of death so hard to flee</span></p><p class="p3"><span class="t5">Kaṇhaṃ dhammaṃ vippahāya – Sukkaṃ bhāvetha paṇḍito</span></p><p class="p3"><span class="t5">Okā anokam&#8217;āgamma – Viveke yattha dūramaṃ</span></p><p class="p3"><span class="t5">Tatr&#8217;ābhiratim&#8217;iccheyya – Hitvā kāme akiñcano</span></p><p class="p3"><span class="t1">Abandoning the darker states</span></p><p class="p3"><span class="t1">The wise pursue the bright</span></p><p class="p3"><span class="t1">Gone from home to homelessness</span></p><p class="p3"><span class="t1">Living withdrawn so hard to enjoy</span></p><p class="p3"><span class="t1">Such rare delight one should desire</span></p><p class="p3"><span class="t1">Sense pleasures cast away</span></p><p class="p3"><span class="t1">Not having anything</span></p><h2><p class="p3"><span class="t5">[Dhp 85-87]</span></p></h2>', '-12-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Burdens', 'title', 13);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 13, '<h2>The Burdens</h2><h2>Bhārā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ bhāra-sutta-gāthāyo bhaṇāmase</span></p><p class="p3"><span class="t5">Bhārā have pañca-kkhandhā – Bhāra-hāro ca puggalo</span></p><p class="p3"><span class="t5">Bhār&#8217;ādānaṃ dukkhaṃ loke – Bhāra-nikkhepanaṃ sukhaṃ</span></p><p class="p3"><span class="t1">The five aggregates indeed are burdens</span></p><p class="p3"><span class="t1">The beast of burden is the person</span></p><p class="p3"><span class="t1">In this world to take up burdens is dukkha</span></p><p class="p3"><span class="t1">Putting them down brings happiness</span></p><p class="p3"><span class="t5">Nikkhipitvā garuṃ bhāraṃ – Aññaṃ bhāraṃ anādiya</span></p><p class="p3"><span class="t5">Samūlaṃ taṇhaṃ abbuyha – Nicchāto parinibbuto</span></p><p class="p3"><span class="t1">A heavy burden cast away</span></p><p class="p3"><span class="t1">Not taking on another load</span></p><p class="p3"><span class="t1">With craving pulled out from the root</span></p><p class="p3"><span class="t1">Desires stilled one is released</span></p><h2><p class="p3"><span class="t5">[SN 22.22]</span></p></h2>', '-13-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'From the Elder Raṭṭhapāla', 'title', 14);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 14, '<h2>From the Elder Raṭṭhapāla</h2><h2>Raṭṭhapāla-thera-gāthā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ raṭṭhapālatthera-gāthāyo bhaṇāmase</span></p><p class="p3"><span class="t5">Passa cittakataṃ bimbaṃ – Arukāyaṃ samussitaṃ</span></p><p class="p3"><span class="t5">Āturaṃ bahu-saṅkappaṃ – Yassa n&#8217;atthi dhuvaṃ ṭhiti</span></p><p class="p3"><span class="t1">See this fancy puppet</span></p><p class="p3"><span class="t1">A body built of sores</span></p><p class="p3"><span class="t1">Diseased obsessed over</span></p><p class="p3"><span class="t1">Which does not last at all</span></p><p class="p3"><span class="t5">Passa cittakataṃ rūpaṃ – Maṇinā kuṇḍalena ca</span></p><p class="p3"><span class="t5">Aṭṭhiṃ tacena onaddhaṃ – Saha vatthehi sobhati</span></p><p class="p3"><span class="t1">See this fancy figure</span></p><p class="p3"><span class="t1">With its gems and earrings</span></p><p class="p3"><span class="t1">It is bones wrapped in skin</span></p><p class="p3"><span class="t1">Made pretty by its clothes</span></p><p class="p3"><span class="t5">Alattaka-katā pādā – Mukhaṃ cuṇṇaka-makkhitaṃ</span></p><p class="p3"><span class="t5">Alaṃ bālassa mohāya – No ca pāragavesino</span></p><p class="p3"><span class="t1">Feet adorned with henna dye</span></p><p class="p3"><span class="t1">And powder smeared upon its face</span></p><p class="p3"><span class="t1">May be enough to beguile a fool</span></p><p class="p3"><span class="t1">But not a seeker of the far shore</span></p><p class="p3"><span class="t5">Aṭṭha-pada-katā kesā – Nettā añjana-makkhitā</span></p><p class="p3"><span class="t5">Alaṃ bālassa mohāya – No ca pāragavesino</span></p><p class="p3"><span class="t1">Hair in eight braids</span></p><p class="p3"><span class="t1">And eyeliner</span></p><p class="p3"><span class="t1">May be enough to beguile a fool</span></p><p class="p3"><span class="t1">But not a seeker of the far shore</span></p><p class="p3"><span class="t5">Añjanī&#8217;va navā cittā – Pūtikāyo alaṅ&#8217;kato</span></p><p class="p3"><span class="t5">Alaṃ bālassa mohāya – No ca pāragavesino</span></p><p class="p3"><span class="t1">A rotting body all adorned</span></p><p class="p3"><span class="t1">Like a freshly painted unguent pot</span></p><p class="p3"><span class="t1">May be enough to beguile a fool</span></p><p class="p3"><span class="t1">But not a seeker of the far shore</span></p><p class="p3"><span class="t5">Passāmi loke sadhane manusse</span></p><p class="p3"><span class="t5">Laddhāna vittaṃ na dadanti mohā</span></p><p class="p3"><span class="t5">Luddhā dhanaṃ sannicayaṃ karonti</span></p><p class="p3"><span class="t5">Bhiyyo&#8217;va kāme abhipatthayanti</span></p><p class="p3"><span class="t1">I see rich people in the world</span></p><p class="p3"><span class="t1">Who from delusion give not the wealth they&#8217;ve earned</span></p><p class="p3"><span class="t1">Greedily they hoard their riches</span></p><p class="p3"><span class="t1">Yearning for ever more sense pleasures</span></p><p class="p3"><span class="t5">Rājā ca aññe ca bahū manussā</span></p><p class="p3"><span class="t5">Avītataṇhā maraṇaṃ upenti</span></p><p class="p3"><span class="t5">Ūnā&#8217;va hutvāna jahanti dehaṃ</span></p><p class="p3"><span class="t5">Kāmehi lokamhi na h&#8217;atthi titti</span></p><p class="p3"><span class="t1">Not just the king but others too</span></p><p class="p3"><span class="t1">Reach death not rid of craving</span></p><p class="p3"><span class="t1">They leave the body still wanting</span></p><p class="p3"><span class="t1">For in this world sense pleasures never satisfy</span></p><p class="p3"><span class="t5">Na dīgham&#8217;āyuṃ labhate dhanena</span></p><p class="p3"><span class="t5">Na c&#8217;āpi vittena jaraṃ vihanti</span></p><p class="p3"><span class="t5">Appaṃ h&#8217;idaṃ jīvitam&#8217;āhu dhīrā</span></p><p class="p3"><span class="t5">Asassataṃ vippariṇāma-dhammaṃ</span></p><p class="p3"><span class="t1">Longevity is not gained by riches</span></p><p class="p3"><span class="t1">Nor does wealth banish ageing</span></p><p class="p3"><span class="t1">For the wise say this life is short</span></p><p class="p3"><span class="t1">Subject to change and not eternal</span></p><p class="p3"><span class="t5">Tasmā hi paññā&#8217;va dhanena seyyā</span></p><p class="p3"><span class="t5">Yāya vosānam&#8217;idh&#8217;ādhigacchati</span></p><p class="p3"><span class="t5">Abyositattā hi bhav&#8217;ābhavesu</span></p><p class="p3"><span class="t5">Pāpāni kammāni karoti mohā</span></p><p class="p3"><span class="t1">Therefore wisdom is much better than wealth</span></p><p class="p3"><span class="t1">By which one reaches perfection in this life</span></p><p class="p3"><span class="t1">People through ignorance do evil deeds</span></p><p class="p3"><span class="t1">Failing to reach the goal from life to life</span></p><p class="p3"><span class="t5">Kāmā hi citrā madhurā manoramā</span></p><p class="p3"><span class="t5">Virūparūpena mathenti cittaṃ</span></p><p class="p3"><span class="t5">Ādīnavaṃ kāmaguṇesu disvā</span></p><p class="p3"><span class="t5">Tasmā ahaṃ pabbajito&#8217;mhi rāja</span></p><p class="p3"><span class="t1">Sense pleasures are diverse sweet delightful</span></p><p class="p3"><span class="t1">Appearing in disguise they disturb the mind</span></p><p class="p3"><span class="t1">Seeing danger in the cords of sense pleasure</span></p><p class="p3"><span class="t1">Therefore I went forth O King</span></p><p class="p3"><span class="t5">Duma-pphalānī&#8217;va patanti māṇavā</span></p><p class="p3"><span class="t5">Daharā ca vuḍḍhā ca sarīrabhedā</span></p><p class="p3"><span class="t5">Etam&#8217;pi disvā pabbajito&#8217;mhi rāja</span></p><p class="p3"><span class="t5">Apaṇṇakaṃ sāmaññam&#8217;eva seyyo</span></p><p class="p3"><span class="t1">As fruits fall from a tree so people fall</span></p><p class="p3"><span class="t1">Young and old when the body breaks up</span></p><p class="p3"><span class="t1">Seeing this too I went forth O King</span></p><p class="p3"><span class="t1">Surely the ascetic life is better</span></p><h2><p class="p3"><span class="t5">[Thag 16.4 / MN 82]</span></p></h2>', '-14-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'From the Elder Pārāpariya', 'title', 15);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 15, '<h2>From the Elder Pārāpariya</h2><h2>Pārāpariya-thera-gāthā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ pārāpariyatthera-gāthāyo bhaṇāmase</span></p><p class="p3"><span class="t5">Aññathā loka-nāthamhi – Tiṭṭhante puris&#8217;uttame</span></p><p class="p3"><span class="t5">Iriyaṃ āsi bhikkhūnaṃ – Aññathā dāni dissati</span></p><p class="p3"><span class="t1">The behavior of the bhikkhus</span></p><p class="p3"><span class="t1">These days seems different</span></p><p class="p3"><span class="t1">From when the Protector of the world</span></p><p class="p3"><span class="t1">The best of men was still here</span></p><p class="p3"><span class="t5">Sīta-vāta-parittāṇaṃ – Hirikopīna-chādanaṃ</span></p><p class="p3"><span class="t5">Matt&#8217;aṭṭhiyaṃ abhuñjiṃsu – Santuṭṭhā itar&#8217;ītare</span></p><p class="p3"><span class="t1">Their robes were just for modesty</span></p><p class="p3"><span class="t1">And protection from cold and wind</span></p><p class="p3"><span class="t1">They ate in moderation</span></p><p class="p3"><span class="t1">Content with whatever they were offered</span></p><p class="p3"><span class="t5">Paṇītaṃ yadi vā lūkhaṃ – Appaṃ vā yadi vā bahuṃ</span></p><p class="p3"><span class="t5">Yāpan&#8217;atthaṃ abhuñjiṃsu – Agiddhā n&#8217;ādhimucchitā</span></p><p class="p3"><span class="t1">Whether food was refined or rough</span></p><p class="p3"><span class="t1">A little or a lot</span></p><p class="p3"><span class="t1">They ate only for sustenance</span></p><p class="p3"><span class="t1">Without greed or gluttony</span></p><p class="p3"><span class="t5">Jīvitānaṃ parikkhāre – Bhesajje atha paccaye</span></p><p class="p3"><span class="t5">Na bāḷhaṃ ussukā āsuṃ – Yathā te āsavakkhaye</span></p><p class="p3"><span class="t1">They were not so eager</span></p><p class="p3"><span class="t1">For the requisites of life</span></p><p class="p3"><span class="t1">Such as tonics and other supplies</span></p><p class="p3"><span class="t1">As they were for the destruction of the taints</span></p><p class="p3"><span class="t5">Araññe rukkha-mūlesu – Kandarāsu guhāsu ca</span></p><p class="p3"><span class="t5">Vivekam&#8217;anubrūhantā – Vihaṃsu tap&#8217;parāyaṇā</span></p><p class="p3"><span class="t1">In the wilderness at the foot of a tree</span></p><p class="p3"><span class="t1">In caves and caverns</span></p><p class="p3"><span class="t1">Fostering seclusion</span></p><p class="p3"><span class="t1">They lived with that as their final goal</span></p><p class="p3"><span class="t5">Nīcā niviṭṭhā subharā – Mudū atthaddha-mānasā</span></p><p class="p3"><span class="t5">Abyāsekā amukharā – Atthacintā vas&#8217;ānugā</span></p><p class="p3"><span class="t1">They were used to simple things easy to look after</span></p><p class="p3"><span class="t1">Gentle not stubborn at heart</span></p><p class="p3"><span class="t1">Unsullied not gossipy</span></p><p class="p3"><span class="t1">Their thoughts were intent on the goal</span></p><p class="p3"><span class="t5">Tato pāsādikaṃ āsi – Gataṃ bhuttaṃ nisevitaṃ</span></p><p class="p3"><span class="t5">Siniddhā teladhārā&#8217;va – Ahosi iriyāpatho</span></p><p class="p3"><span class="t1">That&#8217;s why they inspired confidence</span></p><p class="p3"><span class="t1">In their movements eating and practice</span></p><p class="p3"><span class="t1">Their deportment was as smooth</span></p><p class="p3"><span class="t1">As a stream of oil</span></p><p class="p3"><span class="t5">Yathā kaṇṭaka-ṭṭhānamhi – Careyya anupāhano</span></p><p class="p3"><span class="t5">Satiṃ upaṭṭhapetvāna – Evaṃ gāme munī care</span></p><p class="p3"><span class="t1">When barefoot on a thorny path</span></p><p class="p3"><span class="t1">One would walk</span></p><p class="p3"><span class="t1">Quite mindfully</span></p><p class="p3"><span class="t1">That&#8217;s how a sage should walk in the village</span></p><p class="p3"><span class="t5">Saritvā pubbake yogī – Tesaṃ vattam&#8217;anussaraṃ</span></p><p class="p3"><span class="t5">Kiñ&#8217;c&#8217;āpi pacchimo kālo – Phuseyya amataṃ padaṃ</span></p><p class="p3"><span class="t1">Remembering the meditators of old</span></p><p class="p3"><span class="t1">And recollecting their conduct</span></p><p class="p3"><span class="t1">Even in the latter days</span></p><p class="p3"><span class="t1">The Deathless can still be reached</span></p><h2><p class="p3"><span class="t5">[Thag 16.10]</span></p></h2>', '-15-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'On Protection', 'title', 16);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 16, '<h2>On Protection</h2><h2>Tāyana-gāthā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ tāyana-gāthāyo bhaṇāmase</span></p><p class="p3"><span class="t5">Chinda sotaṃ parakkamma – Kāme panūda brāhmaṇa</span></p><p class="p3"><span class="t5">Nappahāya muni kāme – Nekattam&#8217;upapajjati</span></p><p class="p3"><span class="t1">Exert yourself and cut the stream</span></p><p class="p3"><span class="t1">Discard sense pleasures holy man</span></p><p class="p3"><span class="t1">Not letting sensual pleasures go</span></p><p class="p3"><span class="t1">A sage will not reach unity</span></p><p class="p3"><span class="t5">Kayirā ce kayirāth&#8217;enaṃ – Daḷham&#8217;enaṃ parakkame</span></p><p class="p3"><span class="t5">Sithilo hi paribbājo – Bhiyyo ākirate rajaṃ</span></p><p class="p3"><span class="t1">Vigorously with all one&#8217;s strength</span></p><p class="p3"><span class="t1">It should be done what should be done</span></p><p class="p3"><span class="t1">A lax monastic life stirs up</span></p><p class="p3"><span class="t1">The dust of defilements all the more</span></p><p class="p3"><span class="t5">Akataṃ dukkaṭaṃ seyyo – Pacchā tappati dukkaṭaṃ</span></p><p class="p3"><span class="t5">Katañ&#8217;ca sukataṃ seyyo – Yaṃ katvā n&#8217;ānutappati</span></p><p class="p3"><span class="t1">Better is not to do bad deeds</span></p><p class="p3"><span class="t1">That afterwards would bring remorse</span></p><p class="p3"><span class="t1">It&#8217;s rather good deeds one should do</span></p><p class="p3"><span class="t1">Which having done one won&#8217;t regret</span></p><p class="p3"><span class="t5">Kuso yathā duggahito – Hattham&#8217;ev&#8217;ānukantati</span></p><p class="p3"><span class="t5">Sāmaññaṃ dupparāmaṭṭhaṃ – Nirayāy&#8217;ūpakaḍḍhati</span></p><p class="p3"><span class="t1">As kusa grass when wrongly grasped</span></p><p class="p3"><span class="t1">Will only cut into one&#8217;s hand</span></p><p class="p3"><span class="t1">So does the monk&#8217;s life wrongly led</span></p><p class="p3"><span class="t1">Indeed drag one to hellish states</span></p><p class="p3"><span class="t5">Yaṃ&#8217;kiñci sithilaṃ kammaṃ – Saṅkiliṭṭhañ&#8217;ca yaṃ vataṃ</span></p><p class="p3"><span class="t5">Saṅkassaraṃ brahmacariyaṃ – Na taṃ hoti maha-pphalan&#8217;ti</span></p><p class="p3"><span class="t1">Whatever deed that&#8217;s slackly done</span></p><p class="p3"><span class="t1">Whatever vow corruptly kept</span></p><p class="p3"><span class="t1">The holy life led in doubtful ways</span></p><p class="p3"><span class="t1">All these will never bear great fruits</span></p><h2><p class="p3"><span class="t5">[SN 2.8]</span></p></h2>', '-16-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Miscellaneous Verses', 'title', 17);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 17, '<h2>Miscellaneous Verses</h2><h2>Pakiṇṇaka-gāthā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ pakiṇṇaka-gāthāyo bhaṇāmase</span></p><p class="p3"><span class="t5">Atta-dīpā bhikkhave viharatha atta-saraṇā anañña-saraṇā</span></p><p class="p3"><span class="t5">Dhamma-dīpā dhamma-saraṇā anañña-saraṇā</span></p><p class="p3"><span class="t1">Bhikkhus dwell with yourselves as an island</span></p><p class="p3"><span class="t1">With yourselves as a refuge with no other refuge</span></p><p class="p3"><span class="t1">With the Dhamma as an island with the Dhamma as a refuge</span></p><p class="p3"><span class="t1">With no other refuge</span></p><h2><p class="p3"><span class="t5">[SN 22.43]</span></p></h2><p class="p3"><span class="t5">Appassut&#8217;āyaṃ puriso – Balibaddo&#8217;va jīrati</span></p><p class="p3"><span class="t5">Maṃsāni tassa vaḍḍhanti – Paññā tassa na vaḍḍhati</span></p><p class="p3"><span class="t1">The man of little learning</span></p><p class="p3"><span class="t1">Grows old like an ox</span></p><p class="p3"><span class="t1">He grows only in bulk</span></p><p class="p3"><span class="t1">But his wisdom does not grow</span></p><h2><p class="p3"><span class="t5">[Dhp 152]</span></p></h2><p class="p3"><span class="t5">Uyyuñjanti satīmanto – Na nikete ramanti te</span></p><p class="p3"><span class="t5">Haṃsā&#8217;va pallalaṃ hitvā – Okam&#8217;okaṃ jahanti te</span></p><p class="p3"><span class="t1">The mindful ones exert themselves</span></p><p class="p3"><span class="t1">They are not attached to any home</span></p><p class="p3"><span class="t1">Like swans that abandon the lake</span></p><p class="p3"><span class="t1">They leave home after home behind</span></p><h2><p class="p3"><span class="t5">[Dhp 91]</span></p></h2><p class="p3"><span class="t5">Yaṃ pubbe taṃ visosehi – Pacchā te m&#8217;āhu kiñcanaṃ</span></p><p class="p3"><span class="t5">Majjhe ce no gahessasi – Upasanto carissasi</span></p><p class="p3"><span class="t1">Dry up what pertains to the past</span></p><p class="p3"><span class="t1">Let there be nothing afterward</span></p><p class="p3"><span class="t1">If you do not grasp in the middle</span></p><p class="p3"><span class="t1">You will live at peace</span></p><h2><p class="p3"><span class="t5">[Snp 949]</span></p></h2><p class="p3"><span class="t5">Uṭṭhahatha nisīdatha – Ko attho supitena vo</span></p><p class="p3"><span class="t5">Āturānañ&#8217;hi kā niddā – Salla-viddhāna ruppataṃ</span></p><p class="p3"><span class="t1">Arouse yourselves sit up!</span></p><p class="p3"><span class="t1">What good to you is sleeping?</span></p><p class="p3"><span class="t1">For what sleep can there be for the afflicted</span></p><p class="p3"><span class="t1">For those injured pierced by the dart?</span></p><p class="p3"><span class="t5">Uṭṭhahatha nisīdatha – Daḷhaṃ sikkhatha santiyā</span></p><p class="p3"><span class="t5">Mā vo pamatte viññāya – Maccurājā amohayittha vas&#8217;ānuge</span></p><p class="p3"><span class="t1">Arouse yourselves sit up!</span></p><p class="p3"><span class="t1">Train vigorously for the state of peace</span></p><p class="p3"><span class="t1">Let not the King of Death catch you heedless</span></p><p class="p3"><span class="t1">And delude you when under his control</span></p><p class="p3"><span class="t5">Yāya devā manussā ca – Sitā tiṭṭhanti atthikā</span></p><p class="p3"><span class="t5">Tarath&#8217;etaṃ visattikaṃ – Khaṇo vo mā upaccagā</span></p><p class="p3"><span class="t5">Khaṇ&#8217;ātītā hi socanti – Nirayamhi samappitā</span></p><p class="p3"><span class="t1">Cross over this attachment</span></p><p class="p3"><span class="t1">By which devas and human beings</span></p><p class="p3"><span class="t1">Full of need are held fast</span></p><p class="p3"><span class="t1">Don&#8217;t let the opportunity pass you by</span></p><p class="p3"><span class="t1">For those who have missed the opportunity</span></p><p class="p3"><span class="t1">Sorrow when they arrive in hell</span></p><p class="p3"><span class="t5">Pamādo rajo pamādo – Pamād&#8217;ānupatito rajo</span></p><p class="p3"><span class="t5">Appamādena vijjāya – Abbahe sallam&#8217;attano</span></p><p class="p3"><span class="t1">Heedlessness is dust always</span></p><p class="p3"><span class="t1">Dust follows upon heedlessness</span></p><p class="p3"><span class="t1">By heedfulness by clear knowledge</span></p><p class="p3"><span class="t1">Draw out the dart from yourself</span></p><h2><p class="p3"><span class="t5">[Snp 333-336]</span></p></h2><p class="p3"><span class="t5">Piyato jāyatī soko – Piyato jāyatī bhayaṃ</span></p><p class="p3"><span class="t5">Piyato vippamuttassa – N&#8217;atthi soko kuto bhayaṃ</span></p><p class="p3"><span class="t1">From endearment springs sorrow</span></p><p class="p3"><span class="t1">From endearment springs fear</span></p><p class="p3"><span class="t1">For one who is free from endearment</span></p><p class="p3"><span class="t1">There is no sorrow whence then fear?</span></p><h2><p class="p3"><span class="t5">[Dhp 212]</span></p></h2><p class="p3"><span class="t5">Tiṭṭhat&#8217;eva nibbānaṃ</span></p><p class="p3"><span class="t1">Nibbāna exists</span></p><p class="p3"><span class="t5">Tiṭṭhati nibbānagāmī maggo</span></p><p class="p3"><span class="t1">The path leading to Nibbāna exists</span></p><p class="p3"><span class="t5">Magg&#8217;akkhāyī tathāgato</span></p><p class="p3"><span class="t1">A Tathāgata is one who shows the path</span></p><h2><p class="p3"><span class="t5">[MN 107]</span></p></h2><p class="p3"><span class="t5">Tumhehi kiccam&#8217;ātappaṃ</span></p><p class="p3"><span class="t1">You yourselves must strive</span></p><h2><p class="p3"><span class="t5">[Dhp 276]</span></p></h2><p class="p3"><span class="t5">Yaṃ bhikkhave satthārā karaṇīyaṃ sāvakānaṃ</span></p><p class="p3"><span class="t5">Hit&#8217;esinā anukampakena anukampaṃ upādāya</span></p><p class="p3"><span class="t1">Bhikkhus what should be done for his disciples</span></p><p class="p3"><span class="t1">Out of compassion by a teacher</span></p><p class="p3"><span class="t1">Who seeks their welfare and has compassion for them</span></p><p class="p3"><span class="t5">Kataṃ vo taṃ mayā</span></p><p class="p3"><span class="t1">That I have done for you</span></p><p class="p3"><span class="t5">Etāni bhikkhave rukkha-mūlāni</span></p><p class="p3"><span class="t1">Bhikkhus these are roots of trees</span></p><p class="p3"><span class="t5">Etāni suññ&#8217;āgārāni</span></p><p class="p3"><span class="t1">These are empty huts</span></p><p class="p3"><span class="t5">Jhāyatha bhikkhave mā pamādattha</span></p><p class="p3"><span class="t1">Meditate bhikkhus do not be negligent</span></p><p class="p3"><span class="t5">Mā pacchā vippaṭisārino ahuvattha</span></p><p class="p3"><span class="t1">Lest you regret it later</span></p><p class="p3"><span class="t5">Ayaṃ vo amhākaṃ anusāsanī&#8217;ti</span></p><p class="p3"><span class="t1">This is my instruction to you</span></p><h2><p class="p3"><span class="t5">[MN 19]</span></p></h2>', '-17-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'A Single Excellent Night', 'title', 18);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 18, '<h2>A Single Excellent Night</h2><h2>Bhadd&#8217;eka-ratta</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ bhadd&#8217;eka-ratta-gāthāyo bhaṇāmase</span></p><p class="p3"><span class="t5">Atītaṃ n&#8217;ānvāgameyya – Nappaṭikaṅkhe anāgataṃ</span></p><p class="p3"><span class="t5">Yad&#8217;atītam&#8217;pahīnan&#8217;taṃ – Appattañ&#8217;ca anāgataṃ</span></p><p class="p3"><span class="t1">One should not revive the past</span></p><p class="p3"><span class="t1">Nor speculate on what&#8217;s to come</span></p><p class="p3"><span class="t1">The past is left behind</span></p><p class="p3"><span class="t1">The future is unrealized</span></p><p class="p3"><span class="t5">Paccuppannañ&#8217;ca yo dhammaṃ – Tattha tattha vipassati</span></p><p class="p3"><span class="t5">Asaṃhiraṃ asaṅkuppaṃ – Taṃ vidvā m&#8217;anubrūhaye</span></p><p class="p3"><span class="t1">In every presently arisen state</span></p><p class="p3"><span class="t1">There just there one clearly sees</span></p><p class="p3"><span class="t1">Unmoved unagitated</span></p><p class="p3"><span class="t1">That is what the wise would keep fostering</span></p><p class="p3"><span class="t5">Ajj&#8217;eva kiccam&#8217;ātappaṃ – Ko jaññā maraṇaṃ suve</span></p><p class="p3"><span class="t5">Na hi no saṅgaran&#8217;tena – Mahā-senena maccunā</span></p><p class="p3"><span class="t1">Ardently doing one&#8217;s task today</span></p><p class="p3"><span class="t1">Tomorrow who knows death may come</span></p><p class="p3"><span class="t1">Facing the mighty hordes of death</span></p><p class="p3"><span class="t1">Indeed one cannot strike a deal</span></p><p class="p3"><span class="t5">Evaṃ vihārim&#8217;ātāpiṃ – Aho-rattam&#8217;atanditaṃ</span></p><p class="p3"><span class="t5">Taṃ ve bhadd&#8217;eka-ratto&#8217;ti – Santo ācikkhate muni</span></p><p class="p3"><span class="t1">To dwell with energy aroused</span></p><p class="p3"><span class="t1">Day and night relentlessly</span></p><p class="p3"><span class="t1">That is <q>a single excellent night</q></span></p><p class="p3"><span class="t1">So it was taught by the Peaceful Sage</span></p><h2><p class="p3"><span class="t5">[MN 131]</span></p></h2>', '-18-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Teachings', 'chapter', 19);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Discourse on Setting in Motion the Wheel of Dhamma', 'title', 19);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 19, '<h2>The Discourse on Setting in Motion the Wheel of Dhamma</h2><hr/><p class="p3"><span class="t5">Thus have I heard</span></p><p class="p3"><span class="t5">On one occasion the Blessed One was dwelling at Benares in the Deer Park at Isipatana. There the Blessed One addressed the bhikkhus of the group of five thus:</span></p><p class="p3"><span class="t5">Bhikkhus these two extremes should not be followed by one who has gone forth into homelessness What two? The pursuit of sensual happiness in sensual pleasures which is low vulgar the way of worldlings ignoble unbeneficial and the pursuit of self-mortification which is painful ignoble unbeneficial</span></p><p class="p3"><span class="t5">Without veering towards either of these extremes the Tathāgata has awakened to the middle way which gives rise to vision which gives rise to knowledge which leads to peace to direct knowledge to enlightenment to Nibbāna</span></p><p class="p3"><span class="t5">And what bhikkhus is the Middle Way awakened to by the Tathāgata which gives rise to vision which gives rise to knowledge which leads to peace to direct knowledge to enlightenment to Nibbāna</span></p><p class="p3"><span class="t5">It is this Noble Eightfold Path:</span></p><p class="p3"><span class="t5">That Is Right View Right Intention Right Speech Right Action Right Livelihood Right Effort Right Mindfulness Right Concentration</span></p><p class="p3"><span class="t5">This bhikkhus is that middle way awakened to by the Tathāgata which gives rise to vision which gives rise to knowledge which leads to peace to direct knowledge to enlightenment to Nibbāna</span></p><p class="p3"><span class="t5">Now this bhikkhus is the noble truth of suffering: birth is suffering ageing is suffering illness is suffering death is suffering union with what is displeasing is suffering separation from what is pleasing is suffering not to get what one wants is suffering in brief the five aggregates subject to clinging are suffering</span></p><p class="p3"><span class="t5">Now this bhikkhus is the noble truth of the origin of suffering: it is this craving which leads to renewed existence accompanied by delight and lust seeking delight here and there that is craving for sensual pleasures craving for existence craving for extermination</span></p><p class="p3"><span class="t5">Now this bhikkhus is the noble truth of the cessation of suffering: it is the remainderless fading away and cessation of that same craving the giving up and relinquishing of it freedom from it nonreliance on it</span></p><p class="p3"><span class="t5">Now this bhikkhus is the noble truth of the way leading to the cessation of suffering: It is this Noble Eightfold Path</span></p><p class="p3"><span class="t5">That Is Right View Right Intention Right Speech Right Action Right Livelihood Right Effort Right Mindfulness Right Concentration</span></p><p class="p3"><span class="t5">&#8216;This is the noble truth of suffering&#8217;: thus bhikkhus in regard to things unheard before there arose in me vision knowledge wisdom true knowledge and light</span></p><p class="p3"><span class="t5">&#8216;This noble truth of suffering is to be fully understood: thus bhikkhus in regard to things unheard before there arose in me vision knowledge wisdom true knowledge and light</span></p><p class="p3"><span class="t5">&#8216;This noble truth of suffering has been fully understood: thus bhikkhus in regard to things unheard before there arose in me vision knowledge wisdom true knowledge and light</span></p><p class="p3"><span class="t5">&#8216;This is the noble truth of the origin of suffering&#8217;: thus bhikkhus in regard to things unheard before there arose in me vision knowledge wisdom true knowledge and light</span></p><p class="p3"><span class="t5">&#8216;This noble truth of the origin of suffering is to be abandoned: thus bhikkhus in regard to things unheard before there arose in me vision knowledge wisdom true knowledge and light</span></p><p class="p3"><span class="t5">&#8216;This noble truth of the origin of suffering has been abandoned: thus bhikkhus in regard to things unheard before there arose in me vision knowledge wisdom true knowledge and light</span></p><p class="p3"><span class="t5">&#8216;This is the noble truth of the cessation of suffering&#8217;: thus bhikkhus in regard to things unheard before there arose in me vision knowledge wisdom true knowledge and light</span></p><p class="p3"><span class="t5">&#8216;This noble truth of the cessation of suffering is to be realized: thus bhikkhus in regard to things unheard before there arose in me vision knowledge wisdom true knowledge and light</span></p><p class="p3"><span class="t5">&#8216;This noble truth of the cessation of suffering has been realized: thus bhikkhus in regard to things unheard before there arose in me vision knowledge wisdom true knowledge and light</span></p><p class="p3"><span class="t5">&#8216;This is the noble truth of the way leading to the cessation of suffering&#8217;: thus bhikkhus in regard to things unheard before there arose in me vision knowledge wisdom true knowledge and light</span></p><p class="p3"><span class="t5">&#8216;This noble truth of the way leading to the cessation of suffering is to be developed&#8217;: thus bhikkhus in regard to things unheard before there arose in me vision knowledge wisdom true knowledge and light</span></p><p class="p3"><span class="t5">&#8216;This noble truth of the way leading to the cessation of suffering has been developed&#8217;: thus bhikkhus in regard to things unheard before there arose in me vision knowledge wisdom true knowledge and light</span></p><p class="p3"><span class="t5">So long bhikkhus as my knowledge and vision of these Four Noble Truths as they really are in their three phases and twelve aspects was not thoroughly purified in this way I did not claim to have awakened to the unsurpassed perfect enlightenment in this world with its devas Māra and Brahmā in this generation with its ascetics and brahmins its devas and humans</span></p><p class="p3"><span class="t5">But when my knowledge and vision of these Four Noble Truths as they really are in their three phases and twelve aspects was thoroughly purified in this way then I claimed to have awakened to the unsurpassed perfect enlightenment in this world with its devas Māra and Brahmā in this generation with its ascetics and brahmins its devas and humans</span></p><p class="p3"><span class="t5">This is what the Blessed One said. Elated the bhikkhus of the group of five delighted in the Blessed One&#8217;s statement</span></p><p class="p3"><span class="t5">And while this discourse was being spoken there arose in the Venerable Kondañña the dust-free stainless vision of the Dhamma:</span></p><p class="p3"><span class="t5"><q>Whatever is subject to origination is all subject to cessation</q></span></p><p class="p3"><span class="t5">And when the Wheel of the Dhamma had been set in motion by the Blessed One,&#160;the earth-dwelling devas raised a cry:</span></p><p class="p3"><span class="t5">&#8220;At Benares in the Deer Park at Isipatana this unsurpassed Wheel of the Dhamma has been set in motion by the Blessed One which cannot be stopped by any ascetic or brahmin or deva or Māra or Brahmā or by anyone in the world&#8221;</span></p><p class="p3"><span class="t5">Having heard the cry of the earth-dwelling devas the devas of the realm of the Four Great Kings raised a cry...</span></p><p class="p3"><span class="t5">Having heard the cry of the devas of the realm of the Four Great Kings the Tāvatiṃsa devas raised a cry...</span></p><p class="p3"><span class="t5">Having heard the cry of the Tāvatiṃsa devas the Yāma devas raised a cry...</span></p><p class="p3"><span class="t5">Having heard the cry of the Yāma devas the Tusita devas raised a cry...</span></p><p class="p3"><span class="t5">Having heard the cry of the Tusita devas the Nimmānaratī devas raised a cry...</span></p><p class="p3"><span class="t5">Having heard the cry of the Nimmānaratī devas the Paranimmitavasavattī devas raised a cry...</span></p><p class="p3"><span class="t5">Having heard the cry of the Paranimmitavasavattī devas the devas of Brahmā&#8217;s company raised a cry:</span></p><p class="p3"><span class="t5">&#8220;At Benares in the Deer Park at Isipatana this unsurpassed Wheel of the Dhamma has been set in motion by the Blessed One which cannot be stopped by any ascetic or brahmin or deva or Māra or Brahmā or by anyone in the world&#8221;</span></p><p class="p3"><span class="t5">Thus at that moment at that instant at that second the cry spread as far as the brahmā world and this ten thousandfold world system shook quaked and trembled and an immeasurable glorious radiance appeared in the world surpassing the divine majesty of the devas</span></p><p class="p3"><span class="t5">Then the Blessed One uttered this inspired utterance:</span></p><p class="p3"><span class="t5"><q>Koṇḍañña has indeed understood! Koṇḍañña has indeed understood!</q></span></p><p class="p3"><span class="t5">In this way the Venerable Koṇḍañña acquired the name <q>Aññā Koṇḍañña—Koṇḍañña Who Has Understood</q></span></p><h2><p class="p3"><span class="t5">[SN 56.11]</span></p></h2>', '-19-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Anupubba-sikkhā', 'title', 20);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 20, '<h2>Anupubba-sikkhā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ anupubba-sikkha-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Taṃ dhammaṃ suṇāti gahapati vā gahapati-putto vā aññatarasmiṃ vā kule paccājāto. So taṃ dhammaṃ sutvā tathāgate saddhaṃ paṭilabhati. So tena saddhā-paṭilābhena samannāgato iti paṭisañcikkhati: <q>Sambādho ghar&#8217;āvāso rajopatho abbhokāso pabbajjā. Na&#8217;y&#8217;idaṃ sukaraṃ agāraṃ ajjhāvasatā ek&#8217;anta-paripuṇṇaṃ ek&#8217;anta-parisuddhaṃ sankhalikhitaṃ brahmacariyaṃ carituṃ. Yan&#8217;nūn&#8217;āhaṃ kesamassuṃ ohāretvā kāsāyāni vatthāni acchādetvā agārasmā anagāriyaṃ pabbajeyyan</q>ti. So aparena samayena appaṃ vā bhoga-kkhandhaṃ pahāya mahantaṃ vā bhoga-kkhandhaṃ pahāya appaṃ vā ñāti-parivaṭṭaṃ pahāya mahantaṃ vā ñāti-parivaṭṭaṃ pahāya kesamassuṃ ohāretvā kāsāyāni vatthāni acchādetvā agārasmā anagāriyaṃ pabbajati.</span></p><h2><p class="p3"><span class="t5">[MN 27 / 38 / 51]</span></p></h2><p class="p3"><span class="t5"><q>Sakkā nu kho bho gotama imasmim&#8217;pi dhamma-vinaye evam&#8217;eva anupubba-sikkhā anupubba-kiriyā anupubba-paṭipadā paññapetun</q>ti?</span></p><p class="p3"><span class="t5">Sakkā imasmim&#8217;pi dhamma-vinaye anupubba-sikkhā anupubba-kiriyā anupubba-paṭipadā paññapetuṃ. Tathāgato purisadammaṃ labhitvā paṭhamaṃ evaṃ vineti: <q>Ehi tvaṃ bhikkhu sīlavā hohi pātimokkha-saṃvara-saṃvuto viharāhi ācāra-gocara-sampanno aṇumattesu vajjesu bhayadassāvī samādāya sikkhassu sikkhāpadesū</q>ti.</span></p><h2><p class="p3"><span class="t5">[MN 107]</span></p></h2><p class="p3"><span class="t5">So evaṃ pabbajito samāno bhikkhūnaṃ sikkhā-sājīva-samāpanno pāṇ&#8217;ātipātaṃ pahāya pāṇ&#8217;ātipātā paṭivirato hoti nihitadaṇḍo nihitasattho lajjī day&#8217;āpanno sabba-pāṇa-bhūta-hit&#8217;ānukampī viharati. Adinn&#8217;ādānaṃ pahāya adinn&#8217;ādānā paṭivirato hoti dinn&#8217;ādāyī dinna-pāṭikankhī athenena sucibhūtena attanā viharati. Abrahmacariyaṃ pahāya brahmacārī hoti ārācārī virato methunā gāmadhammā.</span></p><p class="p3"><span class="t5">Musāvādaṃ pahāya musāvādā paṭivirato hoti saccavādī saccasandho theto paccayiko avisaṃvādako lokassa. Pisuṇaṃ vācaṃ pahāya pisuṇāya vācāya paṭivirato hoti ito sutvā na amutra akkhātā imesaṃ bhedāya amutra vā sutvā na imesaṃ akkhātā amūsaṃ bhedāya — iti bhinnānaṃ vā sandhātā sahitānaṃ vā anuppadātā samagg&#8217;ārāmo samagga-rato samagga-nandī samagga-karaṇiṃ vācaṃ bhāsitā hoti. Pharusaṃ vācaṃ pahāya pharusāya vācāya paṭivirato hoti yā sā vācā n&#8217;elā kaṇṇa-sukhā pemanīyā hadayaṅ&#8217;gamā porī bahujana-kantā bahujana-manāpā tathā-rūpiṃ vācaṃ bhāsitā hoti. Sampha-ppalāpaṃ pahāya samphappalāpā paṭivirato hoti kāla-vādī bhūta-vādī attha-vādī dhamma-vādī vinaya-vādī nidhānavatiṃ vācaṃ bhāsitā kālena sāpadesaṃ pariyantavatiṃ attha-saṃhitaṃ.</span></p><p class="p3"><span class="t5">So bījagāma-bhūtagāma-samārambhā paṭivirato hoti ekabhattiko hoti ratt&#8217;ūparato virato vikāla-bhojanā nacca-gīta-vādita-visūka-dassanā paṭivirato hoti mālā-gandha-vilepana-dhāraṇa-maṇḍana-vibhūsana-ṭṭhānā paṭivirato hoti uccāsayana-mahāsayanā paṭivirato hoti jātarūpa-rajata-paṭiggahaṇā paṭivirato hoti āmaka-dhañña-paṭiggahaṇā paṭivirato hoti āmaka-maṃsa-paṭiggahaṇā paṭivirato hoti itthi-kumārika-paṭiggahaṇā paṭivirato hoti dāsidāsa-paṭiggahaṇā paṭivirato hoti aj&#8217;eḷaka-paṭiggahaṇā paṭivirato hoti kukkuṭa-sūkara-paṭiggahaṇā paṭivirato hoti hatthi-gavassa-vaḷava-paṭiggahaṇā paṭivirato hoti khetta-vatthu-paṭiggahaṇā paṭivirato hoti dūteyya-pahiṇa-gaman&#8217;ānuyogā paṭivirato hoti kayavikkayā paṭivirato hoti tulākūṭa-kaṃsakūṭa-mānakūṭā paṭivirato hoti ukkoṭana-vañcana-nikati-sāciyogā paṭivirato hoti chedana-vadha-bandhana viparāmosa-ālopa-sahasākārā paṭivirato hoti.</span></p><p class="p3"><span class="t5">So santuṭṭho hoti kāya-parihārikena cīvarena kucchi-parihārikena piṇḍapātena. So yena yen&#8217;eva pakkamati samādāy&#8217;eva pakkamati. Seyyathā&#8217;pi nāma pakkhī sakuṇo yena yen&#8217;eva ḍeti sapattabhāro&#8217;va ḍeti evam&#8217;eva bhikkhu santuṭṭho hoti kāya-parihārikena cīvarena kucchiparihārikena piṇḍapātena. So yena yen&#8217;eva pakkamati samādāy&#8217;eva pakkamati. So iminā ariyena sīla-kkhandhena samannāgato ajjhattaṃ anavajja-sukhaṃ paṭisaṃvedeti.</span></p><h2><p class="p3"><span class="t5">[MN 51]</span></p></h2><p class="p3"><span class="t5">Tam&#8217;enaṃ tathāgato uttariṃ vineti: <q>Ehi tvaṃ bhikkhu indriyesu gutta-dvāro hohi cakkhunā rūpaṃ disvā mā nimitta-ggāhī hohi m&#8217;ānubyañjana-ggāhī. Yatv&#8217;ādhikaraṇam&#8217;enaṃ cakkhu&#8217;ndriyaṃ asaṃvutaṃ viharantaṃ abhijjhā-domanassā pāpakā akusalā dhammā anvāssaveyyuṃ tassa saṃvarāya paṭipajjāhi rakkhāhi cakkhu&#8217;ndriyaṃ cakkhu&#8217;ndriye saṃvaraṃ āpajjāhi. Sotena saddaṃ sutvā. Ghānena gandhaṃ ghāyitvā. Jivhāya rasaṃ sāyitvā. Kāyena phoṭṭhabbaṃ phusitvā. Manasā dhammaṃ viññāya mā nimitta-ggāhī hohi m&#8217;ānubyañjana-ggāhī. Yatv&#8217;ādhikaraṇam&#8217;enaṃ man&#8217;indriyaṃ asaṃvutaṃ viharantaṃ abhijjhā-domanassā pāpakā akusalā dhammā anvāssaveyyuṃ tassa saṃvarāya paṭipajjāhi rakkhāhi man&#8217;indriyaṃ man&#8217;indriye saṃvaraṃ āpajjāhī</q>ti.</span></p><p class="p3"><span class="t5"><q>Ehi tvaṃ bhikkhu bhojane mattaññū hohi. Paṭisankhā yoniso āhāraṃ āhāreyyāsi n&#8217;eva davāya na madāya na maṇḍanāya na vibhūsanāya yāva&#8217;d&#8217;eva imassa kāyassa ṭhitiyā yāpanāya vihiṃs&#8217;ūparatiyā brahmacariy&#8217;ānuggahāya iti purāṇañ&#8217;ca vedanaṃ paṭihankhāmi navañ&#8217;ca vedanaṃ na uppādessāmi yātrā ca me bhavissati anavajjatā ca phāsuvihāro cā</q>ti.</span></p><p class="p3"><span class="t5"><q>Ehi tvaṃ bhikkhu jāgariyaṃ anuyutto viharāhi divasaṃ caṅkamena nisajjāya āvaraṇīyehi dhammehi cittaṃ parisodhehi rattiyā paṭhamaṃ yāmaṃ caṅkamena nisajjāya āvaraṇīyehi dhammehi cittaṃ parisodhehi rattiyā majjhimaṃ yāmaṃ dakkhiṇena passena sīhaseyyaṃ kappeyyāsi pāde pādaṃ accādhāya sato sampajāno uṭṭhāna-saññaṃ manasikaritvā rattiyā pacchimaṃ yāmaṃ paccuṭṭhāya caṅkamena nisajjāya āvaraṇīyehi dhammehi cittaṃ parisodhehī</q>ti.</span></p><p class="p3"><span class="t5"><q>Ehi tvaṃ bhikkhu sati-sampajaññena samannāgato hohi abhikkante paṭikkante sampajāna-kārī ālokite vilokite sampajāna-kārī samiñjite pasārite sampajāna-kārī sanghāṭi-patta-cīvara-dhāraṇe sampajāna-kārī asite pīte khāyite sāyite sampajāna-kārī uccāra-passāva-kamme sampajāna-kārī gate ṭhite nisinne sutte jāgarite bhāsite tuṇhībhāve sampajāna-kārī</q>ti.</span></p><p class="p3"><span class="t5"><q>Ehi tvaṃ bhikkhu vivittaṃ sen&#8217;āsanaṃ bhajāhi araññaṃ rukkha-mūlaṃ pabbataṃ kandaraṃ giriguhaṃ susānaṃ vana-patthaṃ abbhokāsaṃ palāla-puñjan</q>ti.</span></p><p class="p3"><span class="t5">So pacchābhattaṃ piṇḍapāta-paṭikkanto nisīdati pallankaṃ ābhujitvā ujuṃ kāyaṃ paṇidhāya parimukhaṃ satiṃ upaṭṭhapetvā. So abhijjhaṃ loke pahāya vigat&#8217;ābhijjhena cetasā viharati abhijjhāya cittaṃ parisodheti, byāpāda-padosaṃ pahāya abyāpanna-citto viharati sabba-pāṇa-bhūta-hit&#8217;ānukampī byāpāda-padosā cittaṃ parisodheti, thina-middhaṃ pahāya vigata-thina-middho viharati āloka-saññī sato sampajāno thina-middhā cittaṃ parisodheti, uddhacca-kukkuccaṃ pahāya anuddhato viharati ajjhattaṃ vūpasanta-citto uddhacca-kukkuccā cittaṃ parisodheti, vicikicchaṃ pahāya tiṇṇa-vicikiccho viharati akathaṃkathī kusalesu dhammesu vicikicchāya cittaṃ parisodheti.</span></p><h2><p class="p3"><span class="t5">[MN 107]</span></p></h2><p class="p3"><span class="t5">Bhikkhu yathā iṇaṃ yathā rogaṃ yathā bandhan&#8217;āgāraṃ yathā dāsabyaṃ yathā kantār&#8217;addhāna-maggaṃ ime pañca nīvaraṇe appahīne attani samanupassati. Seyyathā&#8217;pi bhikkhave āṇaṇyaṃ yathā ārogyaṃ yathā bandhanā-mokkhaṃ yathā bhujissaṃ yathā khemanta-bhūmiṃ evam&#8217;eva bhikkhu ime pañca nīvaraṇe pahīne attani samanupassati. So tatonidānaṃ labhetha pāmojjaṃ adhigaccheyya somanassaṃ.</span></p><p class="p3"><span class="t5">So ime pañca nīvaraṇe pahāya cetaso upakkilese paññāya dubbalī-karaṇe paṭhamaṃ jhānaṃ dutiyaṃ jhānaṃ tatiyaṃ jhānaṃ catutthaṃ jhānaṃ upasampajja viharati.</span></p><p class="p3"><span class="t5">So evaṃ samāhite citte parisuddhe pariyodāte anangaṇe vigat&#8217;ūpakkilese mudu-bhūte kammaniye ṭhite āneñjappatte pubbe-nivās&#8217;ānussati-ñāṇāya sattānaṃ cut&#8217;ūpapāta-ñāṇāya āsavānaṃ khaya-ñāṇāya cittaṃ abhininnāmeti.</span></p><p class="p3"><span class="t5">So <q>idaṃ dukkhan</q>ti yathā-bhūtaṃ pajānāti <q>ayaṃ dukkha-samudayo</q>ti yathā-bhūtaṃ pajānāti <q>ayaṃ dukkha-nirodho</q>ti yathā-bhūtaṃ pajānāti <q>ayaṃ dukkha-nirodha-gāminī paṭipadā</q>ti yathā-bhūtaṃ pajānāti.</span></p><p class="p3"><span class="t5"><q>Ime āsavā</q>ti yathā-bhūtaṃ pajānāti <q>ayaṃ āsava-samudayo</q>ti yathā-bhūtaṃ pajānāti <q>ayaṃ āsava-nirodho</q>ti yathā-bhūtaṃ pajānāti <q>ayaṃ āsava-nirodha-gāminī paṭipadā</q>ti yathā-bhūtaṃ pajānāti.</span></p><p class="p3"><span class="t5">Tassa evaṃ jānato evaṃ passato kām&#8217;āsavā&#8217;pi cittaṃ vimuccati bhav&#8217;āsavā&#8217;pi cittaṃ vimuccati avijj&#8217;āsavā&#8217;pi cittaṃ vimuccati. Vimuttasmiṃ vimuttam&#8217;iti ñāṇaṃ hoti: <q>khīṇā jāti vusitaṃ brahmacariyaṃ kataṃ karaṇīyaṃ n&#8217;āparaṃ itthattāyā&#8217;ti pajānātī</q>ti.</span></p><h2><p class="p3"><span class="t5">[MN 39]</span></p></h2>', '-20-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Requisites for Awakening', 'title', 21);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 21, '<h2>Requisites for Awakening</h2><h2>Bodhipakkhiya-dhammā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ bodhipakkhiya-dhamma-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5"></span></p><p class="p3"><span class="t5">Bhikkhave ye te mayā dhammā abhiññā desitā</span></p><p class="p3"><span class="t1">Bhikkhus those things I have taught you from my direct knowledge</span></p><p class="p3"><span class="t5">Te vo sādhukaṃ uggahetvā</span></p><p class="p3"><span class="t1">Having been thoroughly learned by you</span></p><p class="p3"><span class="t5">Āsevitabbā bhāvetabbā bahulī-kātabbā</span></p><p class="p3"><span class="t1">Should be practiced developed and made much of</span></p><p class="p3"><span class="t5">Yatha&#8217;y&#8217;idaṃ brahmacariyaṃ addhaniyaṃ assa cira-ṭṭhitikaṃ</span></p><p class="p3"><span class="t1">So that this holy life may last for a long time</span></p><p class="p3"><span class="t5">Tad&#8217;assa bahujana-hitāya bahujana-sukhāya</span></p><p class="p3"><span class="t1">That would be for the welfare and happiness of many people</span></p><p class="p3"><span class="t5">Lok&#8217;ānukampāya</span></p><p class="p3"><span class="t1">Out of compassion for the world</span></p><p class="p3"><span class="t5">Atthāya hitāya sukhāya deva-manussānaṃ</span></p><p class="p3"><span class="t1">For the benefit welfare and happiness of gods and humans</span></p><p class="p3"><span class="t5">Katame ca te bhikkhave dhammā mayā abhiññā desitā</span></p><p class="p3"><span class="t1">And what bhikkhus are those things I have taught you from my direct knowledge?</span></p><p class="p3"><span class="t5">Seyyath&#8217;īdaṃ</span></p><p class="p3"><span class="t1">They are as follows:</span></p><p class="p3"><span class="t5">Cattāro satipaṭṭhānā</span></p><p class="p3"><span class="t1">The Four Foundations of Mindfulness</span></p><p class="p3"><span class="t5">Cattāro samma-ppadhānā</span></p><p class="p3"><span class="t1">The Four Right Strivings</span></p><p class="p3"><span class="t5">Cattāro iddhipādā</span></p><p class="p3"><span class="t1">The Four Bases of Spiritual Power</span></p><p class="p3"><span class="t5">Pañc&#8217;indriyāni</span></p><p class="p3"><span class="t1">The Five Faculties</span></p><p class="p3"><span class="t5">Pañca balāni</span></p><p class="p3"><span class="t1">The Five Powers</span></p><p class="p3"><span class="t5">Satta bojjh&#8217;aṅgā</span></p><p class="p3"><span class="t1">The Seven Factors of Awakening</span></p><p class="p3"><span class="t5">Ariyo aṭṭh&#8217;aṅgiko maggo</span></p><p class="p3"><span class="t1">The Noble Eightfold Path</span></p><h2><p class="p3"><span class="t5">[DN 16]</span></p></h2>', '-21-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Seven Factors of Awakening', 'title', 22);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 22, '<h2>The Seven Factors of Awakening</h2><h2>Satta-sambojjh&#8217;aṅgā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ satta-sambojjh&#8217;aṅga-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Satt&#8217;ime bhikkhave bojjh&#8217;aṅgā bhāvitā bahulī-katā</span></p><p class="p3"><span class="t1">Bhikkhus when the Seven Factors of Awakening are developed and cultivated</span></p><p class="p3"><span class="t5">Ariyā niyyānikā</span></p><p class="p3"><span class="t1">They are noble and emancipating</span></p><p class="p3"><span class="t5">Nīyanti takkarassa sammā dukkha-kkhayāya</span></p><p class="p3"><span class="t1">Acting them out leads to the complete destruction of suffering</span></p><h2><p class="p3"><span class="t5">[SN 46.19]</span></p></h2><p class="p3"><span class="t5">Ye te bhikkhave bhikkhū</span></p><p class="p3"><span class="t1">Bhikkhus those bhikkhus</span></p><p class="p3"><span class="t5">Sīla-sampannā</span></p><p class="p3"><span class="t1">Who are accomplished in virtue</span></p><p class="p3"><span class="t5">Samādhi-sampannā</span></p><p class="p3"><span class="t1">Accomplished in concentration</span></p><p class="p3"><span class="t5">Ñāṇa-sampannā</span></p><p class="p3"><span class="t1">Accomplished in wisdom</span></p><p class="p3"><span class="t5">Vimutti-sampannā</span></p><p class="p3"><span class="t1">Accomplished in liberation</span></p><p class="p3"><span class="t5">Vimutti-ñāṇa-dassana-sampannā</span></p><p class="p3"><span class="t1">Accomplished in the knowledge and vision of liberation:</span></p><p class="p3"><span class="t5">Dassanam&#8217;p&#8217;āhaṃ bhikkhave tesaṃ bhikkhūnaṃ bahu-kāraṃ vadāmi</span></p><p class="p3"><span class="t1">I say even the sight of those bhikkhus is helpful</span></p><p class="p3"><span class="t5">Savanam&#8217;p&#8217;āhaṃ</span></p><p class="p3"><span class="t1">Even listening to them</span></p><p class="p3"><span class="t5">Upasaṅkamanam&#8217;p&#8217;āhaṃ</span></p><p class="p3"><span class="t1">Even approaching them</span></p><p class="p3"><span class="t5">Payirupāsanam&#8217;p&#8217;āhaṃ</span></p><p class="p3"><span class="t1">Even attending on them</span></p><p class="p3"><span class="t5">Anussatim&#8217;p&#8217;āhaṃ</span></p><p class="p3"><span class="t1">Even recollecting them</span></p><p class="p3"><span class="t5">Anupabbajjam&#8217;p&#8217;āhaṃ</span></p><p class="p3"><span class="t1">Even going forth after them is helpful</span></p><p class="p3"><span class="t5">Taṃ kissa hetu</span></p><p class="p3"><span class="t1">For what reason?</span></p><p class="p3"><span class="t5">Tathā-rūpānaṃ bhikkhave bhikkhūnaṃ dhammaṃ sutvā</span></p><p class="p3"><span class="t1">Because when one has heard the Dhamma from such bhikkhus</span></p><p class="p3"><span class="t5">Dvayena vūpakāsena vūpakaṭṭho viharati</span></p><p class="p3"><span class="t1">One dwells withdrawn by way of two kinds of withdrawal</span></p><p class="p3"><span class="t5">Kāya-vūpakāsena ca citta-vūpakāsena ca</span></p><p class="p3"><span class="t1">Withdrawal of body and withdrawal of mind</span></p><p class="p3"><span class="t5">So tathā vūpakaṭṭho viharanto</span></p><p class="p3"><span class="t1">Dwelling thus withdrawn</span></p><p class="p3"><span class="t5">Taṃ dhammaṃ anussarati anuvitakketi</span></p><p class="p3"><span class="t1">One recollects that Dhamma and thinks it over</span></p><p class="p3"><span class="t5">So tathā sato viharanto</span></p><p class="p3"><span class="t1">Dwelling thus mindfully</span></p><p class="p3"><span class="t5">Taṃ dhammaṃ paññāya pavicinati</span></p><p class="p3"><span class="t1">One discriminates that Dhamma with wisdom</span></p><p class="p3"><span class="t5">Pavicarati parivīmaṃsam&#8217;āpajjati</span></p><p class="p3"><span class="t1">Examines it makes an investigation of it</span></p><p class="p3"><span class="t5">Tassa taṃ dhammaṃ paññāya pavicinato</span></p><p class="p3"><span class="t1">For one who discriminates that Dhamma with wisdom</span></p><p class="p3"><span class="t5">Pavicarato parivīmaṃsam&#8217;āpajjato</span></p><p class="p3"><span class="t1">Examines it makes an investigation of it</span></p><p class="p3"><span class="t5">Āraddhaṃ hoti vīriyaṃ asallīnaṃ</span></p><p class="p3"><span class="t1">One&#8217;s energy is aroused without slackening</span></p><p class="p3"><span class="t5">Āraddha-vīriyassa uppajjati pīti nirāmisā</span></p><p class="p3"><span class="t1">For one who is energetic</span></p><p class="p3"><span class="t1">Spiritual rapture arises</span></p><p class="p3"><span class="t5">Pīti-manassa kāyo&#8217;pi passambhati</span></p><p class="p3"><span class="t1">For one whose mind is uplifted by rapture</span></p><p class="p3"><span class="t1">The body becomes tranquil</span></p><p class="p3"><span class="t5">Cittam&#8217;pi passambhati</span></p><p class="p3"><span class="t1">And the mind becomes tranquil</span></p><p class="p3"><span class="t5">Passaddha-kāyassa sukhino</span></p><p class="p3"><span class="t1">For one whose body is tranquil and who is happy</span></p><p class="p3"><span class="t5">Cittaṃ samādhiyati</span></p><p class="p3"><span class="t1">The mind becomes concentrated</span></p><p class="p3"><span class="t5">So tathā-samāhitaṃ cittaṃ sādhukaṃ ajjhupekkhitā hoti</span></p><p class="p3"><span class="t1">One closely looks on with equanimity</span></p><p class="p3"><span class="t1">At the mind thus concentrated</span></p><h2><p class="p3"><span class="t5">[SN 46.3]</span></p></h2><p class="p3"><span class="t5">Ime kho bhikkhave satta bojjh&#8217;aṅgā&#8217;ti</span></p><p class="p3"><span class="t1">Bhikkhus these are the Seven Factors of Awakening</span></p><h2><p class="p3"><span class="t5">[SN 46.22]</span></p></h2>', '-22-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Noble Eightfold Path', 'title', 23);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 23, '<h2>The Noble Eightfold Path</h2><h2>Ariy&#8217;aṭṭh&#8217;aṅgika-magga</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ ariy&#8217;aṭṭh&#8217;aṅgika-magga-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Ayam&#8217;eva ariyo aṭṭh&#8217;aṅgiko maggo</span></p><p class="p3"><span class="t1">This is the Noble Eightfold Path</span></p><p class="p3"><span class="t5">Seyyath&#8217;īdaṃ</span></p><p class="p3"><span class="t1">Which is as follows</span></p><p class="p3"><span class="t5">Sammā-diṭṭhi</span></p><p class="p3"><span class="t1">Right View</span></p><p class="p3"><span class="t5">Sammā-saṅkappo</span></p><p class="p3"><span class="t1">Right Intention</span></p><p class="p3"><span class="t5">Sammā-vācā</span></p><p class="p3"><span class="t1">Right Speech</span></p><p class="p3"><span class="t5">Sammā-kammanto</span></p><p class="p3"><span class="t1">Right Action</span></p><p class="p3"><span class="t5">Sammā-ājīvo</span></p><p class="p3"><span class="t1">Right Livelihood</span></p><p class="p3"><span class="t5">Sammā-vāyāmo</span></p><p class="p3"><span class="t1">Right Effort</span></p><p class="p3"><span class="t5">Sammā-sati</span></p><p class="p3"><span class="t1">Right Mindfulness</span></p><p class="p3"><span class="t5">Sammā-samādhi</span></p><p class="p3"><span class="t1">Right Concentration</span></p><p class="p3"><span class="t5">Katamā ca bhikkhave sammā-diṭṭhi</span></p><p class="p3"><span class="t1">And what bhikkhus is Right View?</span></p><p class="p3"><span class="t5">Yaṃ kho bhikkhave dukkhe ñāṇaṃ</span></p><p class="p3"><span class="t1">Knowledge of suffering</span></p><p class="p3"><span class="t5">Dukkha-samudaye ñāṇaṃ</span></p><p class="p3"><span class="t1">Knowledge of the origin of suffering</span></p><p class="p3"><span class="t5">Dukkha-nirodhe ñāṇaṃ</span></p><p class="p3"><span class="t1">Knowledge of the cessation of suffering</span></p><p class="p3"><span class="t5">Dukkha-nirodha-gāminiyā paṭipadāya ñāṇaṃ</span></p><p class="p3"><span class="t1">Knowledge of the way of practice</span></p><p class="p3"><span class="t1">Leading to the cessation of suffering</span></p><p class="p3"><span class="t5">Ayaṃ vuccati bhikkhave sammā-diṭṭhi</span></p><p class="p3"><span class="t1">This bhikkhus is called Right View</span></p><p class="p3"><span class="t5">Katamo ca bhikkhave sammā-saṅkappo</span></p><p class="p3"><span class="t1">And what bhikkhus is Right Intention?</span></p><p class="p3"><span class="t5">Nekkhamma-saṅkappo</span></p><p class="p3"><span class="t1">The intention of renunciation</span></p><p class="p3"><span class="t5">Abyāpāda-saṅkappo</span></p><p class="p3"><span class="t1">The intention of non-ill-will</span></p><p class="p3"><span class="t5">Avihiṃsā-saṅkappo</span></p><p class="p3"><span class="t1">The intention of non-cruelty</span></p><p class="p3"><span class="t5">Ayaṃ vuccati bhikkhave sammā-saṅkappo</span></p><p class="p3"><span class="t1">This bhikkhus is called Right Intention</span></p><p class="p3"><span class="t5">Katamā ca bhikkhave sammā-vācā</span></p><p class="p3"><span class="t1">And what bhikkhus is Right Speech?</span></p><p class="p3"><span class="t5">Musāvādā veramaṇī</span></p><p class="p3"><span class="t1">Abstaining from false speech</span></p><p class="p3"><span class="t5">Pisuṇāya vācāya veramaṇī</span></p><p class="p3"><span class="t1">Abstaining from malicious speech</span></p><p class="p3"><span class="t5">Pharusāya vācāya veramaṇī</span></p><p class="p3"><span class="t1">Abstaining from harsh speech</span></p><p class="p3"><span class="t5">Sampha-ppalāpā veramaṇī</span></p><p class="p3"><span class="t1">Abstaining from idle chatter</span></p><p class="p3"><span class="t5">Ayaṃ vuccati bhikkhave sammā-vācā</span></p><p class="p3"><span class="t1">This bhikkhus is called Right Speech</span></p><p class="p3"><span class="t5">Katamo ca bhikkhave sammā-kammanto</span></p><p class="p3"><span class="t1">And what bhikkhus is Right Action?</span></p><p class="p3"><span class="t5">Pāṇ&#8217;ātipātā veramaṇī</span></p><p class="p3"><span class="t1">Abstaining from killing living beings</span></p><p class="p3"><span class="t5">Adinn&#8217;ādānā veramaṇī</span></p><p class="p3"><span class="t1">Abstaining from taking what is not given</span></p><p class="p3"><span class="t5">Kāmesu-micchācārā veramaṇī</span></p><p class="p3"><span class="t1">Abstaining from sexual misconduct</span></p><p class="p3"><span class="t5">Ayaṃ vuccati bhikkhave sammā-kammanto</span></p><p class="p3"><span class="t1">This bhikkhus is called Right Action</span></p><p class="p3"><span class="t5">Katamo ca bhikkhave sammā-ājīvo</span></p><p class="p3"><span class="t1">And what bhikkhus is Right Livelihood?</span></p><p class="p3"><span class="t5">Idha bhikkhave ariya-sāvako</span></p><p class="p3"><span class="t5">Micchā-ājīvaṃ pahāya</span></p><p class="p3"><span class="t5">Sammā-ājīvena jīvitaṃ kappeti</span></p><p class="p3"><span class="t1">Here bhikkhus a noble disciple</span></p><p class="p3"><span class="t1">Having abandoned wrong livelihood</span></p><p class="p3"><span class="t1">Earns his living by right livelihood</span></p><p class="p3"><span class="t5">Ayaṃ vuccati bhikkhave sammā-ājīvo</span></p><p class="p3"><span class="t1">This bhikkhus is called Right Livelihood</span></p><p class="p3"><span class="t5">Katamo ca bhikkhave sammā-vāyāmo</span></p><p class="p3"><span class="t1">And what bhikkhus is Right Effort?</span></p><p class="p3"><span class="t5">Idha bhikkhave bhikkhu</span></p><p class="p3"><span class="t5">Anuppannānaṃ pāpakānaṃ akusalānaṃ dhammānaṃ anuppādāya</span></p><p class="p3"><span class="t5">Chandaṃ janeti</span></p><p class="p3"><span class="t5">Vāyamati</span></p><p class="p3"><span class="t5">Vīriyaṃ ārabhati</span></p><p class="p3"><span class="t5">Cittaṃ paggaṇhāti padahati</span></p><p class="p3"><span class="t1">Here bhikkhus a bhikkhu awakens zeal</span></p><p class="p3"><span class="t1">For the non-arising of unarisen evil unwholesome states</span></p><p class="p3"><span class="t1">He puts forth effort</span></p><p class="p3"><span class="t1">Arouses energy</span></p><p class="p3"><span class="t1">Exerts his mind</span></p><p class="p3"><span class="t1">And strives</span></p><p class="p3"><span class="t5">Uppannānaṃ pāpakānaṃ akusalānaṃ dhammānaṃ pahānāya</span></p><p class="p3"><span class="t5">Chandaṃ janeti</span></p><p class="p3"><span class="t5">Vāyamati</span></p><p class="p3"><span class="t5">Vīriyaṃ ārabhati</span></p><p class="p3"><span class="t5">Cittaṃ paggaṇhāti padahati</span></p><p class="p3"><span class="t1">He awakens zeal for the abandoning of arisen evil unwholesome states</span></p><p class="p3"><span class="t1">He puts forth effort</span></p><p class="p3"><span class="t1">Arouses energy</span></p><p class="p3"><span class="t1">Exerts his mind</span></p><p class="p3"><span class="t1">And strives</span></p><p class="p3"><span class="t5">Anuppannānaṃ kusalānaṃ dhammānaṃ uppādāya</span></p><p class="p3"><span class="t5">Chandaṃ janeti</span></p><p class="p3"><span class="t5">Vāyamati</span></p><p class="p3"><span class="t5">Vīriyaṃ ārabhati</span></p><p class="p3"><span class="t5">Cittaṃ paggaṇhāti padahati</span></p><p class="p3"><span class="t1">He awakens zeal for the arising of unarisen wholesome states</span></p><p class="p3"><span class="t1">He puts forth effort</span></p><p class="p3"><span class="t1">Arouses energy</span></p><p class="p3"><span class="t1">Exerts his mind</span></p><p class="p3"><span class="t1">And strives</span></p><p class="p3"><span class="t5">Uppannānaṃ kusalānaṃ dhammānaṃ ṭhitiyā</span></p><p class="p3"><span class="t5">Asammosāya</span></p><p class="p3"><span class="t5">Bhiyyobhāvāya</span></p><p class="p3"><span class="t5">Vepullāya</span></p><p class="p3"><span class="t5">Bhāvanāya pāripūriyā</span></p><p class="p3"><span class="t5">Chandaṃ janeti</span></p><p class="p3"><span class="t5">Vāyamati</span></p><p class="p3"><span class="t5">Vīriyaṃ ārabhati</span></p><p class="p3"><span class="t5">Cittaṃ paggaṇhāti padahati</span></p><p class="p3"><span class="t1">He awakens zeal for the continuance</span></p><p class="p3"><span class="t1">Non-disappearance</span></p><p class="p3"><span class="t1">Strengthening</span></p><p class="p3"><span class="t1">Increase and fulfillment by development</span></p><p class="p3"><span class="t1">Of arisen wholesome states</span></p><p class="p3"><span class="t1">He puts forth effort</span></p><p class="p3"><span class="t1">Arouses energy</span></p><p class="p3"><span class="t1">Exerts his mind</span></p><p class="p3"><span class="t1">And strives</span></p><p class="p3"><span class="t5">Ayaṃ vuccati bhikkhave sammā-vāyāmo</span></p><p class="p3"><span class="t1">This bhikkhus is called Right Effort</span></p><p class="p3"><span class="t5">Katamā ca bhikkhave sammā-sati</span></p><p class="p3"><span class="t1">And what bhikkhus is Right Mindfulness?</span></p><p class="p3"><span class="t5">Idha bhikkhave bhikkhu kāye kāy&#8217;ānupassī viharati</span></p><p class="p3"><span class="t1">Here bhikkhus a bhikkhu abides</span></p><p class="p3"><span class="t1">Contemplating the body as a body</span></p><p class="p3"><span class="t5">Ātāpī sampajāno satimā</span></p><p class="p3"><span class="t1">Ardent fully aware and mindful</span></p><p class="p3"><span class="t5">Vineyya loke abhijjhā-domanassaṃ</span></p><p class="p3"><span class="t1">Having put away</span></p><p class="p3"><span class="t1">Longing and grief for the world</span></p><p class="p3"><span class="t5">Vedanāsu vedan&#8217;ānupassī viharati</span></p><p class="p3"><span class="t1">He abides contemplating feelings as feelings</span></p><p class="p3"><span class="t5">Ātāpī sampajāno satimā</span></p><p class="p3"><span class="t1">Ardent fully aware and mindful</span></p><p class="p3"><span class="t5">Vineyya loke abhijjhā-domanassaṃ</span></p><p class="p3"><span class="t1">Having put away</span></p><p class="p3"><span class="t1">Longing and grief for the world</span></p><p class="p3"><span class="t5">Citte citt&#8217;ānupassī viharati</span></p><p class="p3"><span class="t1">He abides contemplating mind as mind</span></p><p class="p3"><span class="t5">Ātāpī sampajāno satimā</span></p><p class="p3"><span class="t1">Ardent fully aware and mindful</span></p><p class="p3"><span class="t5">Vineyya loke abhijjhā-domanassaṃ</span></p><p class="p3"><span class="t1">Having put away</span></p><p class="p3"><span class="t1">Longing and grief for the world</span></p><p class="p3"><span class="t5">Dhammesu dhamm&#8217;ānupassī viharati</span></p><p class="p3"><span class="t1">He abides contemplating dhammas as dhammas</span></p><p class="p3"><span class="t5">Ātāpī sampajāno satimā</span></p><p class="p3"><span class="t1">Ardent fully aware and mindful</span></p><p class="p3"><span class="t5">Vineyya loke abhijjhā-domanassaṃ</span></p><p class="p3"><span class="t1">Having put away</span></p><p class="p3"><span class="t1">Longing and grief for the world</span></p><p class="p3"><span class="t5">Ayaṃ vuccati bhikkhave sammā-sati</span></p><p class="p3"><span class="t1">This bhikkhus is called Right Mindfulness</span></p><p class="p3"><span class="t5">Katamo ca bhikkhave sammā-samādhi</span></p><p class="p3"><span class="t1">And what bhikkhus is Right Concentration?</span></p><p class="p3"><span class="t5">Idha bhikkhave bhikkhu</span></p><p class="p3"><span class="t1">Here bhikkhus a bhikkhu</span></p><p class="p3"><span class="t5">Vivicc&#8217;eva kāmehi</span></p><p class="p3"><span class="t1">Quite secluded from sense pleasures</span></p><p class="p3"><span class="t5">Vivicca akusalehi dhammehi</span></p><p class="p3"><span class="t1">Secluded from unwholesome states</span></p><p class="p3"><span class="t5">Savitakkaṃ savicāraṃ viveka-jaṃ pīti-sukhaṃ paṭhamaṃ jhānaṃ upasampajja viharati</span></p><p class="p3"><span class="t1">Enters upon and abides in the first Jhāna</span></p><p class="p3"><span class="t1">Accompanied by thought and examination</span></p><p class="p3"><span class="t1">With rapture and pleasure born of seclusion</span></p><p class="p3"><span class="t5">Vitakka-vicārānaṃ vūpasamā</span></p><p class="p3"><span class="t1">With the stilling of thought and examination</span></p><p class="p3"><span class="t5">Ajjhattaṃ sampasādanaṃ</span></p><p class="p3"><span class="t5">Cetaso ekodibhāvaṃ</span></p><p class="p3"><span class="t5">Avitakkaṃ avicāraṃ samādhi-jaṃ pīti-sukhaṃ dutiyaṃ jhānaṃ upasampajja viharati</span></p><p class="p3"><span class="t1">He enters upon and abides in the second Jhāna</span></p><p class="p3"><span class="t1">Accompanied by self-confidence and singleness of mind</span></p><p class="p3"><span class="t1">Without thought and examination</span></p><p class="p3"><span class="t1">With rapture and pleasure born of concentration</span></p><p class="p3"><span class="t5">Pītiyā ca virāgā</span></p><p class="p3"><span class="t1">With the fading away as well of rapture</span></p><p class="p3"><span class="t5">Upekkhako ca viharati</span></p><p class="p3"><span class="t1">He abides in equanimity</span></p><p class="p3"><span class="t5">Sato ca sampajāno</span></p><p class="p3"><span class="t1">Mindful and fully aware</span></p><p class="p3"><span class="t5">Sukhañ&#8217;ca kāyena paṭisaṃvedeti</span></p><p class="p3"><span class="t1">And experiencing pleasure with the body</span></p><p class="p3"><span class="t5">Yaṃ taṃ ariyā ācikkhanti</span></p><p class="p3"><span class="t5">&#8216;Upekkhako satimā sukha-vihārī&#8217;ti tatiyaṃ jhānaṃ upasampajja viharati</span></p><p class="p3"><span class="t1">He enters upon and abides in the third Jhāna</span></p><p class="p3"><span class="t1">On account of which the Noble Ones announce</span></p><p class="p3"><span class="t1">&#8216;He has a pleasant abiding</span></p><p class="p3"><span class="t1">With equanimity and is mindful&#8217;</span></p><p class="p3"><span class="t5">Sukhassa ca pahānā</span></p><p class="p3"><span class="t1">With the abandoning of pleasure</span></p><p class="p3"><span class="t5">Dukkhassa ca pahānā</span></p><p class="p3"><span class="t1">And the abandoning of pain</span></p><p class="p3"><span class="t5">Pubb&#8217;eva somanassa domanassānaṃ atthaṅ&#8217;gamā</span></p><p class="p3"><span class="t1">With the previous disappearance of joy and displeasure</span></p><p class="p3"><span class="t5">Adukkham&#8217;asukhaṃ upekkhā-sati-pārisuddhiṃ catutthaṃ jhānaṃ upasampajja viharati</span></p><p class="p3"><span class="t1">He enters upon and abides in the fourth Jhāna</span></p><p class="p3"><span class="t1">Accompanied by neither pain nor pleasure</span></p><p class="p3"><span class="t1">And purity of mindfulness</span></p><p class="p3"><span class="t1">Due to equanimity</span></p><p class="p3"><span class="t5">Ayaṃ vuccati bhikkhave sammā-samādhi</span></p><p class="p3"><span class="t1">This bhikkhus is called Right Concentration</span></p><p class="p3"><span class="t5">Ayam&#8217;eva ariyo aṭṭh&#8217;aṅgiko maggo</span></p><p class="p3"><span class="t1">This is the Noble Eightfold Path</span></p><h2><p class="p3"><span class="t5">[SN 45.8]</span></p></h2>', '-23-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Mindfulness of Breathing', 'title', 24);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 24, '<h2>Mindfulness of Breathing</h2><h2>Ānāpānassati</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ ānāpānassati-sutta-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Ānāpānassati bhikkhave bhāvitā bahulī-katā</span></p><p class="p3"><span class="t1">Bhikkhus when mindfulness of breathing is developed and cultivated</span></p><p class="p3"><span class="t5">Maha-pphalā hoti mah&#8217;ānisaṃsā</span></p><p class="p3"><span class="t1">It is of great fruit and great benefit</span></p><p class="p3"><span class="t5">Ānāpānassati bhikkhave bhāvitā bahulī-katā</span></p><p class="p3"><span class="t1">When mindfulness of breathing is developed and cultivated</span></p><p class="p3"><span class="t5">Cattāro satipaṭṭhāne paripūreti</span></p><p class="p3"><span class="t1">It fulfills the Four Foundations of Mindfulness</span></p><p class="p3"><span class="t5">Cattāro satipaṭṭhānā bhāvitā bahulī-katā</span></p><p class="p3"><span class="t1">When the Four Foundations of Mindfulness are developed and cultivated</span></p><p class="p3"><span class="t5">Satta-bojjh&#8217;aṅge paripūrenti</span></p><p class="p3"><span class="t1">They fulfill the Seven Factors of Awakening</span></p><p class="p3"><span class="t5">Satta-bojjh&#8217;aṅgā bhāvitā bahulī-katā</span></p><p class="p3"><span class="t1">When the Seven Factors of Awakening are developed and cultivated</span></p><p class="p3"><span class="t5">Vijjā-vimuttiṃ paripūrenti</span></p><p class="p3"><span class="t1">They fulfill true knowledge and deliverance</span></p><p class="p3"><span class="t5">Kathaṃ bhāvitā ca bhikkhave ānāpānassati kathaṃ bahulī-katā</span></p><p class="p3"><span class="t1">And how bhikkhus is mindfulness of breathing developed and cultivated</span></p><p class="p3"><span class="t5">Maha-pphalā hoti mah&#8217;ānisaṃsā</span></p><p class="p3"><span class="t1">So that it is of great fruit and great benefit?</span></p><p class="p3"><span class="t5">Idha bhikkhave bhikkhu</span></p><p class="p3"><span class="t1">Here bhikkhus a bhikkhu</span></p><p class="p3"><span class="t5">Arañña-gato vā</span></p><p class="p3"><span class="t1">Gone to the forest</span></p><p class="p3"><span class="t5">Rukkha-mūla-gato vā</span></p><p class="p3"><span class="t1">To the foot of a tree</span></p><p class="p3"><span class="t5">Suññ&#8217;āgāra-gato vā</span></p><p class="p3"><span class="t1">Or to an empty hut</span></p><p class="p3"><span class="t5">Nisīdati pallaṅkaṃ ābhujitvā</span></p><p class="p3"><span class="t1">Sits down having crossed his legs</span></p><p class="p3"><span class="t5">Ujuṃ kāyaṃ paṇidhāya parimukhaṃ satiṃ upaṭṭhapetvā</span></p><p class="p3"><span class="t1">Sets his body erect</span></p><p class="p3"><span class="t1">Having established mindfulness in front of him</span></p><p class="p3"><span class="t5">So sato&#8217;va assasati sato&#8217;va passasati</span></p><p class="p3"><span class="t1">Ever mindful he breathes in</span></p><p class="p3"><span class="t1">Mindful he breathes out</span></p><p class="p3"><span class="t5">Dīghaṃ vā assasanto dīghaṃ assasāmī&#8217;ti pajānāti</span></p><p class="p3"><span class="t1">Breathing in long he knows &#8216;I breathe in long&#8217;</span></p><p class="p3"><span class="t5">Dīghaṃ vā passasanto dīghaṃ passasāmī&#8217;ti pajānāti</span></p><p class="p3"><span class="t1">Breathing out long he knows &#8216;I breathe out long&#8217;</span></p><p class="p3"><span class="t5">Rassaṃ vā assasanto rassaṃ assasāmī&#8217;ti pajānāti</span></p><p class="p3"><span class="t1">Breathing in short he knows &#8216;I breathe in short&#8217;</span></p><p class="p3"><span class="t5">Rassaṃ vā passasanto rassaṃ passasāmī&#8217;ti pajānāti</span></p><p class="p3"><span class="t1">Breathing out short he knows &#8216;I breathe out short&#8217;</span></p><p class="p3"><span class="t5">Sabba-kāya-paṭisaṃvedī assasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe in experiencing the whole body&#8217;</span></p><p class="p3"><span class="t5">Sabba-kāya-paṭisaṃvedī passasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe out experiencing the whole body&#8217;</span></p><p class="p3"><span class="t5">Passambhayaṃ kāya-saṅkhāraṃ assasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe in tranquillizing the bodily formation&#8217;</span></p><p class="p3"><span class="t5">Passambhayaṃ kāya-saṅkhāraṃ passasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe out tranquillizing the bodily formation&#8217;</span></p><p class="p3"><span class="t5">Pīti-paṭisaṃvedī assasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe in experiencing rapture&#8217;</span></p><p class="p3"><span class="t5">Pīti-paṭisaṃvedī passasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe out experiencing rapture&#8217;</span></p><p class="p3"><span class="t5">Sukha-paṭisaṃvedī assasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe in experiencing pleasure&#8217;</span></p><p class="p3"><span class="t5">Sukha-paṭisaṃvedī passasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe out experiencing pleasure&#8217;</span></p><p class="p3"><span class="t5">Citta-saṅkhāra-paṭisaṃvedī assasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe in experiencing the mental formation&#8217;</span></p><p class="p3"><span class="t5">Citta-saṅkhāra-paṭisaṃvedī passasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe out experiencing the mental formation&#8217;</span></p><p class="p3"><span class="t5">Passambhayaṃ citta-saṅkhāraṃ assasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe in tranquillizing the mental formation&#8217;</span></p><p class="p3"><span class="t5">Passambhayaṃ citta-saṅkhāraṃ passasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe out tranquillizing the mental formation&#8217;</span></p><p class="p3"><span class="t5">Citta-paṭisaṃvedī assasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe in experiencing the mind&#8217;</span></p><p class="p3"><span class="t5">Citta-paṭisaṃvedī passasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe out experiencing the mind&#8217;</span></p><p class="p3"><span class="t5">Abhippamodayaṃ cittaṃ assasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe in gladdening the mind&#8217;</span></p><p class="p3"><span class="t5">Abhippamodayaṃ cittaṃ passasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe out gladdening the mind&#8217;</span></p><p class="p3"><span class="t5">Samādahaṃ cittaṃ assasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe in concentrating the mind&#8217;</span></p><p class="p3"><span class="t5">Samādahaṃ cittaṃ passasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe out concentrating the mind&#8217;</span></p><p class="p3"><span class="t5">Vimocayaṃ cittaṃ assasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe in liberating the mind&#8217;</span></p><p class="p3"><span class="t5">Vimocayaṃ cittaṃ passasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe out liberating the mind&#8217;</span></p><p class="p3"><span class="t5">Anicc&#8217;ānupassī assasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe in contemplating impermanence&#8217;</span></p><p class="p3"><span class="t5">Anicc&#8217;ānupassī passasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe out contemplating impermanence&#8217;</span></p><p class="p3"><span class="t5">Virāg&#8217;ānupassī assasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe in contemplating the fading away of passions&#8217;</span></p><p class="p3"><span class="t5">Virāg&#8217;ānupassī passasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe out contemplating the fading away of passions&#8217;</span></p><p class="p3"><span class="t5">Nirodh&#8217;ānupassī assasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe in contemplating cessation&#8217;</span></p><p class="p3"><span class="t5">Nirodh&#8217;ānupassī passasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe out contemplating cessation&#8217;</span></p><p class="p3"><span class="t5">Paṭinissagg&#8217;ānupassī assasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe in contemplating relinquishment&#8217;</span></p><p class="p3"><span class="t5">Paṭinissagg&#8217;ānupassī passasissāmī&#8217;ti sikkhati</span></p><p class="p3"><span class="t1">He trains thus:</span></p><p class="p3"><span class="t1">&#8216;I shall breathe out contemplating relinquishment&#8217;</span></p><p class="p3"><span class="t5">Evaṃ bhāvitā kho bhikkhave ānāpānassati evaṃ bahulī-katā</span></p><p class="p3"><span class="t1">Bhikkhus that is how mindfulness of breathing is developed and cultivated</span></p><p class="p3"><span class="t5">Maha-pphalā hoti mah&#8217;ānisaṃsā</span></p><p class="p3"><span class="t1">So that it is of great fruit and great benefit</span></p><h2><p class="p3"><span class="t5">[MN 118]</span></p></h2>', '-24-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Dependent Origination', 'title', 25);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 25, '<h2>Dependent Origination</h2><h2>Paṭicca-samuppāda</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ paṭicca-samuppāda-vibhaṅgaṃ bhaṇāmase</span></p><p class="p3"><span class="t1">Now let us recite the Analysis of Dependent Origination</span></p><p class="p3"><span class="t5">Imasmiṃ sati idaṃ hoti</span></p><p class="p3"><span class="t1">When this exists that is</span></p><p class="p3"><span class="t5">Imass&#8217;uppādā idaṃ uppajjati</span></p><p class="p3"><span class="t1">From the arising of this that arises</span></p><p class="p3"><span class="t5">Imasmiṃ asati idaṃ na hoti</span></p><p class="p3"><span class="t1">When this does not exist that is not</span></p><p class="p3"><span class="t5">Imassa nirodhā idaṃ nirujjhati</span></p><p class="p3"><span class="t1">From the cessation of this that ceases</span></p><h2><p class="p3"><span class="t5">[SN 12.61]</span></p></h2><p class="p3"><span class="t5">Avijjā-paccayā saṅkhārā</span></p><p class="p3"><span class="t1">From ignorance as a condition arise formations</span></p><p class="p3"><span class="t5">Saṅkhāra-paccayā viññāṇaṃ</span></p><p class="p3"><span class="t1">From formations as a condition arises consciousness</span></p><p class="p3"><span class="t5">Viññāṇa-paccayā nāma-rūpaṃ</span></p><p class="p3"><span class="t1">From consciousness as a condition arises name-and-form</span></p><p class="p3"><span class="t5">Nāma-rūpa-paccayā saḷ&#8217;āyatanaṃ</span></p><p class="p3"><span class="t1">From name-and-form as a condition arises the sixfold-sense-base</span></p><p class="p3"><span class="t5">Saḷ&#8217;āyatana-paccayā phasso</span></p><p class="p3"><span class="t1">From the sixfold-sense-base as a condition arises contact</span></p><p class="p3"><span class="t5">Phassa-paccayā vedanā</span></p><p class="p3"><span class="t1">From contact as a condition arises feeling</span></p><p class="p3"><span class="t5">Vedanā-paccayā taṇhā</span></p><p class="p3"><span class="t1">From feeling as a condition arises craving</span></p><p class="p3"><span class="t5">Taṇhā-paccayā upādānaṃ</span></p><p class="p3"><span class="t1">From craving as a condition arises clinging</span></p><p class="p3"><span class="t5">Upādāna-paccayā bhavo</span></p><p class="p3"><span class="t1">From clinging as a condition arises becoming</span></p><p class="p3"><span class="t5">Bhava-paccayā jāti</span></p><p class="p3"><span class="t1">From becoming as a condition arises birth</span></p><p class="p3"><span class="t5">Jāti-paccayā jarā-maraṇaṃ soka-parideva-dukkha-domanss&#8217;upāyāsā sambhavanti</span></p><p class="p3"><span class="t1">From birth as a condition arise ageing-and-death</span></p><p class="p3"><span class="t1">Sorrow lamentation pain displeasure and despair</span></p><p class="p3"><span class="t5">Evam&#8217;etassa kevalassa dukkha-kkhandhassa samudayo hoti</span></p><p class="p3"><span class="t1">Such is the origin of this whole mass of suffering</span></p><p class="p3"><span class="t5">Tattha katamā avijjā</span></p><p class="p3"><span class="t1">Therein what is &#8216;ignorance&#8217;?</span></p><p class="p3"><span class="t5">Dukkhe aññāṇaṃ dukkha-samudaye aññāṇaṃ dukkha-nirodhe aññāṇaṃ dukkha-nirodha-gāminiyā paṭipadāya aññāṇaṃ</span></p><p class="p3"><span class="t1">Not knowing suffering not knowing the origin of suffering not knowing the cessation of suffering not knowing the way of practice leading to the cessation of suffering</span></p><p class="p3"><span class="t5">Ayaṃ vuccati avijjā</span></p><p class="p3"><span class="t1">This is called &#8216;ignorance&#8217;</span></p><p class="p3"><span class="t5">Tattha katame avijjā-paccayā saṅkhārā</span></p><p class="p3"><span class="t1">Therein what are &#8216;formations&#8217; arising from ignorance as a condition?</span></p><p class="p3"><span class="t5">Puññ&#8217;ābhisaṅkhāro apuññ&#8217;ābhisaṅkhāro āneñj&#8217;ābhisaṅkhāro</span></p><p class="p3"><span class="t5">Kāya-saṅkhāro vacī-saṅkhāro citta-saṅkhāro</span></p><p class="p3"><span class="t1">Karmic formation of wholesomeness</span></p><p class="p3"><span class="t1">Karmic formation of unwholesomeness</span></p><p class="p3"><span class="t1">Karmic formation of imperturbability</span></p><p class="p3"><span class="t1">Bodily formation verbal formation mental formation</span></p><p class="p3"><span class="t5">Tattha katamo puññ&#8217;ābhisaṅkhāro</span></p><p class="p3"><span class="t1">Therein what is &#8216;karmic formation of wholesomeness&#8217;?</span></p><p class="p3"><span class="t5">Kusalā cetanā kām&#8217;āvacarā rūp&#8217;āvacarā dānamayā sīlamayā bhāvanāmayā</span></p><p class="p3"><span class="t1">Skillful volition of the sense-sphere of the form-sphere connected with giving connected with virtue connected with meditation</span></p><p class="p3"><span class="t5">Ayaṃ vuccati puññ&#8217;ābhisaṅkhāro</span></p><p class="p3"><span class="t1">This is called &#8216;karmic formation of wholesomeness&#8217;</span></p><p class="p3"><span class="t5">Tattha katamo apuññ&#8217;ābhisaṅkhāro</span></p><p class="p3"><span class="t1">Therein what is &#8216;karmic formation of unwholesomeness&#8217;?</span></p><p class="p3"><span class="t5">Akusalā cetanā kām&#8217;āvacarā</span></p><p class="p3"><span class="t1">Unskillful volition of the sense-sphere</span></p><p class="p3"><span class="t5">Ayaṃ vuccati apuññ&#8217;ābhisaṅkhāro</span></p><p class="p3"><span class="t1">This is called &#8216;karmic formation of unwholesomeness&#8217;</span></p><p class="p3"><span class="t5">Tattha katamo āneñj&#8217;ābhisaṅkhāro</span></p><p class="p3"><span class="t1">Therein what is &#8216;karmic formation of imperturbability&#8217;?</span></p><p class="p3"><span class="t5">Kusalā cetanā arūp&#8217;āvacarā</span></p><p class="p3"><span class="t1">Skillful volition of the formless-sphere</span></p><p class="p3"><span class="t5">Ayaṃ vuccati āneñj&#8217;ābhisaṅkhāro</span></p><p class="p3"><span class="t1">This is called &#8216;karmic formation of imperturbability&#8217;</span></p><p class="p3"><span class="t5">Tattha katamo kāya-saṅkhāro</span></p><p class="p3"><span class="t1">Therein what is &#8216;bodily formation&#8217;?</span></p><p class="p3"><span class="t5">Kāya-sañcetanā kāya-saṅkhāro vacī-sañcetanā vacī-saṅkhāro mano-sañcetanā citta-saṅkhāro</span></p><p class="p3"><span class="t1">Volition associated with the body is bodily formation</span></p><p class="p3"><span class="t1">Volition associated with speech is verbal formation</span></p><p class="p3"><span class="t1">Volition associated with the mind is mental formation</span></p><p class="p3"><span class="t5">Ime vuccanti avijjā-paccayā saṅkhārā</span></p><p class="p3"><span class="t1">These are called &#8216;formations&#8217; arising from ignorance as a condition</span></p><p class="p3"><span class="t5">Tattha katamaṃ saṅkhāra-paccayā viññāṇaṃ</span></p><p class="p3"><span class="t1">Therein what is &#8216;consciousness&#8217; arising from formations as a condition?</span></p><p class="p3"><span class="t5">Cakkhu-viññāṇaṃ sota-viññāṇaṃ ghāna-viññāṇaṃ jivhā-viññāṇaṃ kāya-viññāṇaṃ mano-viññāṇaṃ</span></p><p class="p3"><span class="t1">Eye-consciousness ear-consciousness nose-consciousness tongue-consciousness body-consciousness mind-consciousness</span></p><p class="p3"><span class="t5">Idaṃ vuccati saṅkhāra-paccayā viññāṇaṃ</span></p><p class="p3"><span class="t1">This is called &#8216;consciousness&#8217; arising from formations as a condition</span></p><p class="p3"><span class="t5">Tattha katamaṃ viññāṇa-paccayā nāma-rūpaṃ</span></p><p class="p3"><span class="t1">Therein what is &#8216;name-and-form&#8217; arising from consciousness as a condition?</span></p><p class="p3"><span class="t5">Atthi nāmaṃ atthi rūpaṃ</span></p><p class="p3"><span class="t1">There is name there is form</span></p><p class="p3"><span class="t5">Tattha katamaṃ nāmaṃ</span></p><p class="p3"><span class="t1">Therein what is &#8216;name&#8217;?</span></p><p class="p3"><span class="t5">Vedanā saññā cetanā phasso manasikāro</span></p><p class="p3"><span class="t1">Feeling perception volition contact and attention</span></p><p class="p3"><span class="t5">Idaṃ vuccati nāmaṃ</span></p><p class="p3"><span class="t1">This is called &#8216;name&#8217;</span></p><p class="p3"><span class="t5">Tattha katamaṃ rūpaṃ</span></p><p class="p3"><span class="t1">Therein what is &#8216;form&#8217;?</span></p><p class="p3"><span class="t5">Cattāro mahā-bhūtā catunnañ&#8217;ca mahā-bhūtānaṃ upādāya-rūpaṃ</span></p><p class="p3"><span class="t1">The four great elements and form dependent on the four great elements</span></p><p class="p3"><span class="t5">Idaṃ vuccati rūpaṃ</span></p><p class="p3"><span class="t1">This is called &#8216;form&#8217;</span></p><p class="p3"><span class="t5">Iti idañ&#8217;ca nāmaṃ idañ&#8217;ca rūpaṃ</span></p><p class="p3"><span class="t1">Thus are name and form</span></p><p class="p3"><span class="t5">Idaṃ vuccati viññāṇa-paccayā nāma-rūpaṃ</span></p><p class="p3"><span class="t1">This is called &#8216;name-and-form&#8217; arising from consciousness as a condition</span></p><p class="p3"><span class="t5">Tattha katamaṃ nāma-rūpa-paccayā saḷ&#8217;āyatanaṃ</span></p><p class="p3"><span class="t1">Therein what is &#8216;the sixfold-sense-base&#8217; arising from name-and-form as a condition?</span></p><p class="p3"><span class="t5">Cakkh&#8217;āyatanaṃ sot&#8217;āyatanaṃ ghān&#8217;āyatanaṃ jivh&#8217;āyatanaṃ kāy&#8217;āyatanaṃ man&#8217;āyatanaṃ</span></p><p class="p3"><span class="t1">The eye-base ear-base nose-base tongue-base body-base mind-base</span></p><p class="p3"><span class="t5">Idaṃ vuccati nāma-rūpa-paccayā saḷ&#8217;āyatanaṃ</span></p><p class="p3"><span class="t1">This is called &#8216;the sixfold-sense-base&#8217; arising from name-and-form as a condition</span></p><p class="p3"><span class="t5">Tattha katamo saḷ&#8217;āyatana-paccayā phasso</span></p><p class="p3"><span class="t1">Therein what is &#8216;contact&#8217; arising from the sixfold-sense-base as a condition?</span></p><p class="p3"><span class="t5">Cakkhu-samphasso sota-samphasso ghāna-samphasso jivhā-samphasso kāya-samphasso mano-samphasso</span></p><p class="p3"><span class="t1">Eye-contact ear-contact nose-contact tongue-contact body-contact mind-contact</span></p><p class="p3"><span class="t5">Ayaṃ vuccati saḷ&#8217;āyatana-paccayā phasso</span></p><p class="p3"><span class="t1">This is called &#8216;contact&#8217; arising from the sixfold-sense-base as a condition</span></p><p class="p3"><span class="t5">Tattha katamā phassa-paccayā vedanā</span></p><p class="p3"><span class="t1">Therein what is &#8216;feeling&#8217; arising from contact as a condition?</span></p><p class="p3"><span class="t5">Cakkhu-samphassa-jā vedanā sota-samphassa-jā vedanā ghāna-samphassa-jā vedanā jivhā-samphassa-jā vedanā kāya-samphassa-jā vedanā mano-samphassa-jā vedanā</span></p><p class="p3"><span class="t1">Feeling born of eye-contact feeling born of ear-contact feeling born of nose-contact feeling born of tongue-contact feeling born of body-contact feeling born of mind-contact</span></p><p class="p3"><span class="t5">Ayaṃ vuccati phassa-paccayā vedanā</span></p><p class="p3"><span class="t1">This is called &#8216;feeling&#8217; arising from contact as a condition</span></p><p class="p3"><span class="t5">Tattha katamā vedanā-paccayā taṇhā</span></p><p class="p3"><span class="t1">Therein what is &#8216;craving&#8217; arising from feeling as a condition?</span></p><p class="p3"><span class="t5">Rūpa-taṇhā sadda-taṇhā gandha-taṇhā rasa-taṇhā phoṭṭhabba-taṇhā dhamma-taṇhā</span></p><p class="p3"><span class="t1">Craving for forms craving for sounds craving for odours craving for flavours craving for tangibles craving for mind-objects</span></p><p class="p3"><span class="t5">Ayaṃ vuccati vedanā-paccayā taṇhā</span></p><p class="p3"><span class="t1">This is called &#8216;craving&#8217; arising from feeling as a condition</span></p><p class="p3"><span class="t5">Tattha katamaṃ taṇhā-paccayā upādānaṃ</span></p><p class="p3"><span class="t1">Therein what is &#8216;clinging&#8217; arising from craving as a condition?</span></p><p class="p3"><span class="t5">Kām&#8217;upādānaṃ diṭṭh&#8217;upādānaṃ sīlabbat&#8217;upādānaṃ attavād&#8217;upādānaṃ</span></p><p class="p3"><span class="t1">Clinging to sensuality clinging to views clinging to rules and rituals clinging to a sense of self</span></p><p class="p3"><span class="t5">Idaṃ vuccati taṇhā-paccayā upādānaṃ</span></p><p class="p3"><span class="t1">This is called &#8216;clinging&#8217; arising from craving as a condition</span></p><p class="p3"><span class="t5">Tattha katamo upādāna-paccayā bhavo</span></p><p class="p3"><span class="t1">Therein what is &#8216;becoming&#8217; arising from clinging as a condition?</span></p><p class="p3"><span class="t5">Kāma-bhavo rūpa-bhavo arūpa-bhavo</span></p><p class="p3"><span class="t1">Sense-sphere becoming form-sphere becoming formless-sphere becoming</span></p><p class="p3"><span class="t5">Ayaṃ vuccati upādāna-paccayā bhavo</span></p><p class="p3"><span class="t1">This is called &#8216;becoming&#8217; arising from clinging as a condition</span></p><p class="p3"><span class="t5">Tattha katamā bhava-paccayā jāti</span></p><p class="p3"><span class="t1">Therein what is &#8216;birth&#8217; arising from becoming as a condition?</span></p><p class="p3"><span class="t5">Yā tesaṃ tesaṃ sattānaṃ tamhi tamhi sattanikāye jāti sañjāti okkanti abhinibbatti khandhānaṃ pātubhāvo āyatanānaṃ paṭilābho</span></p><p class="p3"><span class="t1">The birth of various beings among the various classes of beings their being born descent production appearance of the aggregates obtaining of the sense-bases</span></p><p class="p3"><span class="t5">Ayaṃ vuccati bhava-paccayā jāti</span></p><p class="p3"><span class="t1">This is called &#8216;birth&#8217; arising from becoming as a condition</span></p><p class="p3"><span class="t5">Tattha katamaṃ jāti-paccayā jarā-maraṇaṃ</span></p><p class="p3"><span class="t1">Therein what is &#8216;ageing-and-death&#8217; arising from birth as a condition?</span></p><p class="p3"><span class="t5">Atthi jarā atthi maraṇaṃ</span></p><p class="p3"><span class="t1">There is ageing there is death</span></p><p class="p3"><span class="t5">Tattha katamā jarā</span></p><p class="p3"><span class="t1">Therein what is &#8216;ageing&#8217;?</span></p><p class="p3"><span class="t5">Yā tesaṃ tesaṃ sattānaṃ tamhi tamhi sattanikāye jarā jīraṇatā khaṇḍiccaṃ pāliccaṃ valittacatā āyuno saṃhāni indriyānaṃ paripāko</span></p><p class="p3"><span class="t1">The ageing of various beings among the various classes of beings their growing old brokenness of teeth greyness of hair wrinkling of skin decline of vitality decay of faculties</span></p><p class="p3"><span class="t5">Ayaṃ vuccati jarā</span></p><p class="p3"><span class="t1">This is called &#8216;ageing&#8217;</span></p><p class="p3"><span class="t5">Tattha katamaṃ maraṇaṃ</span></p><p class="p3"><span class="t1">Therein what is &#8216;death&#8217;?</span></p><p class="p3"><span class="t5">Yā tesaṃ tesaṃ sattānaṃ tamhā tamhā sattanikāyā cuti cavanatā bhedo antaradhānaṃ maccu maraṇaṃ kālakiriyā khandhānaṃ bhedo kaḷevarassa nikkhepo jīvit&#8217;indriyass&#8217;upacchedo</span></p><p class="p3"><span class="t1">The passing away of various beings from the various classes of beings their perishing breaking up disappearance dying death completion of time breakup of the aggregates laying down of the carcass cutting off the life faculty</span></p><p class="p3"><span class="t5">Idaṃ vuccati maraṇaṃ</span></p><p class="p3"><span class="t1">This is called &#8216;death&#8217;</span></p><p class="p3"><span class="t5">Iti ayañ&#8217;ca jarā idañ&#8217;ca maraṇaṃ</span></p><p class="p3"><span class="t1">Thus are ageing and death</span></p><p class="p3"><span class="t5">Idaṃ vuccati jāti-paccayā jarā-maraṇaṃ</span></p><p class="p3"><span class="t1">This is called &#8216;ageing-and-death&#8217; arising from birth as a condition</span></p><p class="p3"><span class="t5">Tattha katamo soko</span></p><p class="p3"><span class="t1">Therein what is &#8216;sorrow&#8217;?</span></p><p class="p3"><span class="t5">Ñāti-byasanena vā phuṭṭhassa bhoga-byasanena vā phuṭṭhassa roga-byasanena vā phuṭṭhassa sīla-byasanena vā phuṭṭhassa diṭṭhi-byasanena vā phuṭṭhassa aññatar&#8217;aññatarena byasanena samannāgatassa aññatar&#8217;aññatarena dukkha-dhammena phuṭṭhassa soko socanā socitattaṃ antosoko antoparisoko cetaso parijjhāyanā domanassaṃ sokasallaṃ</span></p><p class="p3"><span class="t1">Affected by the loss of relatives or loss of wealth or misfortune of sickness or loss of virtue or loss of right view by whatever misfortune one encounters by whatever painful thing one is affected the sorrow sorrowing sorrowfulness inner sorrow extensive inner sorrow the mind&#8217;s thorough burning displeasure the dart of sorrow</span></p><p class="p3"><span class="t5">Ayaṃ vuccati soko</span></p><p class="p3"><span class="t1">This is called &#8216;sorrow&#8217;</span></p><p class="p3"><span class="t5">Tattha katamo paridevo</span></p><p class="p3"><span class="t1">Therein what is &#8216;lamentation&#8217;?</span></p><p class="p3"><span class="t5">Ñāti-byasanena vā phuṭṭhassa bhoga-byasanena vā phuṭṭhassa roga-byasanena vā phuṭṭhassa sīla-byasanena vā phuṭṭhassa diṭṭhi-byasanena vā phuṭṭhassa aññatar&#8217;aññatarena byasanena samannāgatassa aññatar&#8217;aññatarena dukkha-dhammena phuṭṭhassa ādevo paridevo ādevanā paridevanā ādevitattaṃ paridevitattaṃ vācā palāpo vippalāpo lālappo lālappanā lālappitattaṃ</span></p><p class="p3"><span class="t1">Affected by the loss of relatives or loss of wealth or misfortune of sickness or loss of virtue or loss of right view by whatever misfortune one encounters by whatever painful thing one is affected the wail and lament wailing and lamenting bewailing and lamentation sorrowful talk senseless confused sorrowful murmur sorrowful murmuring sorrowful murmuration</span></p><p class="p3"><span class="t5">Ayaṃ vuccati paridevo</span></p><p class="p3"><span class="t1">This is called &#8216;lamentation&#8217;</span></p><p class="p3"><span class="t5">Tattha katamaṃ dukkhaṃ?</span></p><p class="p3"><span class="t1">Therein what is &#8216;pain&#8217;?</span></p><p class="p3"><span class="t5">Yaṃ kāyikaṃ asātaṃ kāyikaṃ dukkhaṃ kāya-samphassa-jaṃ asātaṃ dukkhaṃ vedayitaṃ kāya-samphassa-jā asātā dukkhā vedanā</span></p><p class="p3"><span class="t1">The bodily discomfort bodily pain what is felt as uncomfortable painful that is born of body-contact the uncomfortable painful feeling that is born of body-contact</span></p><p class="p3"><span class="t5">Idaṃ vuccati dukkhaṃ</span></p><p class="p3"><span class="t1">This is called &#8216;pain&#8217;</span></p><p class="p3"><span class="t5">Tattha katamaṃ domanassaṃ</span></p><p class="p3"><span class="t1">Therein what is &#8216;displeasure&#8217;?</span></p><p class="p3"><span class="t5">Yaṃ cetasikaṃ asātaṃ cetasikaṃ dukkhaṃ ceto-samphassa-jaṃ asātaṃ dukkhaṃ vedayitaṃ ceto-samphassa-jā asātā dukkhā vedanā</span></p><p class="p3"><span class="t1">The mental discomfort mental pain what is felt as uncomfortable painful that is born of mind-contact the uncomfortable painful feeling that is born of mind-contact</span></p><p class="p3"><span class="t5">Idaṃ vuccati domanassaṃ</span></p><p class="p3"><span class="t1">This is called &#8216;displeasure&#8217;</span></p><p class="p3"><span class="t5">Tattha katamo upāyāso</span></p><p class="p3"><span class="t1">Therein what is &#8216;despair&#8217;?</span></p><p class="p3"><span class="t5">Ñāti-byasanena vā phuṭṭhassa bhoga-byasanena vā phuṭṭhassa roga-byasanena vā phuṭṭhassa sīla-byasanena vā phuṭṭhassa diṭṭhi-byasanena vā phuṭṭhassa aññatar&#8217;aññatarena byasanena samannāgatassa aññatar&#8217;aññatarena dukkha-dhammena phuṭṭhassa āyāso upāyāso āyāsitattaṃ upāyāsitattaṃ</span></p><p class="p3"><span class="t1">Affected by the loss of relatives or loss of wealth or misfortune of sickness or loss of virtue or loss of right view by whatever misfortune one encounters by whatever painful thing one is affected the trouble and despair tribulation and desperation</span></p><p class="p3"><span class="t5">Ayaṃ vuccati upāyāso</span></p><p class="p3"><span class="t1">This is called &#8216;despair&#8217;</span></p><p class="p3"><span class="t5">Evam&#8217;etassa kevalassa dukkha-kkhandhassa samudayo hotī&#8217;ti:</span></p><p class="p3"><span class="t1"><q>Such is the origin of this whole mass of suffering</q> means this:</span></p><p class="p3"><span class="t5">Evam&#8217;etassa kevalassa dukkha-kkhandhassa saṅgati hoti samāgamo hoti samodhānaṃ hoti pātubhāvo hoti</span></p><p class="p3"><span class="t1">Such is the combination composition collocation manifestation of this whole mass of suffering</span></p><p class="p3"><span class="t5">Tena vuccati evam&#8217;etassa kevalassa dukkha-kkhandhassa samudayo hotī&#8217;ti</span></p><p class="p3"><span class="t1">Therefore it is called</span></p><p class="p3"><span class="t1"><q>Such is the origin of this whole mass of suffering</q></span></p><h2><p class="p3"><span class="t5">[Vibh 130 / SN 12.2]</span></p></h2>', '-25-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Dhamma in Brief', 'title', 26);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 26, '<h2>The Dhamma in Brief</h2><h2>Saṅkhitta-dhamma</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ saṅkhitta-sutta-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Mahāpajāpatī gotamī yena bhagavā ten&#8217;upasaṅkami upasaṅkamitvā bhagavantaṃ abhivādetvā ekam&#8217;antaṃ aṭṭhāsi. Ekam&#8217;antaṃ ṭhitā kho sā mahāpajāpatī gotamī bhagavantaṃ etad&#8217;avoca:</span></p><p class="p3"><span class="t1">Mahāpajāpatī Gotamī approached the Blessed One paid homage to him then standing to one side she said:</span></p><p class="p3"><span class="t5">Sādhu me bhante bhagavā saṅkhittena dhammaṃ desetu</span></p><p class="p3"><span class="t1">Bhante it would be good if the Blessed One</span></p><p class="p3"><span class="t1">Would teach me the Dhamma in brief</span></p><p class="p3"><span class="t5">Yam&#8217;ahaṃ bhagavato dhammaṃ sutvā</span></p><p class="p3"><span class="t1">Having heard the Dhamma from the Blessed One</span></p><p class="p3"><span class="t5">Ekā vūpakaṭṭhā appamattā ātāpinī pahitattā vihareyyan&#8217;ti</span></p><p class="p3"><span class="t1">I might dwell alone withdrawn heedful ardent and resolute</span></p><p class="p3"><span class="t5">Ye kho tvaṃ gotamī dhamme jāneyyāsi:</span></p><p class="p3"><span class="t1">Gotamī those things of which you might know:</span></p><p class="p3"><span class="t5">Ime dhammā virāgāya saṃvattanti no sarāgāya</span></p><p class="p3"><span class="t1">&#8216;They lead to dispassion not to passion</span></p><p class="p3"><span class="t5">Visaṃyogāya saṃvattanti no saṃyogāya</span></p><p class="p3"><span class="t1">To detachment not to bondage</span></p><p class="p3"><span class="t5">Apacayāya saṃvattanti no ācayāya</span></p><p class="p3"><span class="t1">To dismantling not to building up</span></p><p class="p3"><span class="t5">App&#8217;icchatāya saṃvattanti no mah&#8217;icchatāya</span></p><p class="p3"><span class="t1">To fewness of desires not to strong desires</span></p><p class="p3"><span class="t5">Santuṭṭhiyā saṃvattanti no asantuṭṭhiyā</span></p><p class="p3"><span class="t1">To contentment not to discontent</span></p><p class="p3"><span class="t5">Pavivekāya saṃvattanti no saṅgaṇikāya</span></p><p class="p3"><span class="t1">To solitude not to company</span></p><p class="p3"><span class="t5">Vīriy&#8217;ārambhāya saṃvattanti no kosajjāya</span></p><p class="p3"><span class="t1">To the arousing of energy not to laziness</span></p><p class="p3"><span class="t5">Subharatāya saṃvattanti no dubbharatāyā&#8217;ti</span></p><p class="p3"><span class="t1">To being easy to support not to being difficult to support&#8221;</span></p><p class="p3"><span class="t5">Ek&#8217;aṃsena gotami dhāreyyāsi:</span></p><p class="p3"><span class="t1">Gotamī you should definitely recognize:</span></p><p class="p3"><span class="t5">Eso dhammo eso vinayo etaṃ satthu-sāsanan&#8217;ti</span></p><p class="p3"><span class="t1">&#8216;This is the Dhamma this is the Vinaya</span></p><p class="p3"><span class="t1">This is the Teacher&#8217;s teaching!&#8221;</span></p><h2><p class="p3"><span class="t5">[AN 8.53]</span></p></h2>', '-26-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Four Great References', 'title', 27);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 27, '<h2>The Four Great References</h2><h2>Cattāro mahāpadesā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ mahāpadesa-sutta-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Katame bhikkhave cattāro mahāpadesā</span></p><p class="p3"><span class="t1">What bhikkhus are the four great references?</span></p><p class="p3"><span class="t5">Idha bhikkhave bhikkhu evaṃ vadeyya:</span></p><p class="p3"><span class="t1">Here bhikkhus a bhikkhu might say:</span></p><p class="p3"><span class="t5">Sammukhā me&#8217;taṃ āvuso bhagavato sutaṃ</span></p><p class="p3"><span class="t1">In the presence of the Blessed One I heard this</span></p><p class="p3"><span class="t5">Sammukhā paṭiggahitaṃ</span></p><p class="p3"><span class="t1">In his presence I learned this</span></p><p class="p3"><span class="t5">Asukasmiṃ nāma āvāse</span></p><p class="p3"><span class="t1">Or in such and such a residence</span></p><p class="p3"><span class="t5">Saṅgho viharati sathero sapāmokkho</span></p><p class="p3"><span class="t1">A Saṅgha is dwelling with elders and prominent monks</span></p><p class="p3"><span class="t5">Tassa me saṅghassa sammukhā sutaṃ</span></p><p class="p3"><span class="t1">In the presence of that Saṅgha I heard this</span></p><p class="p3"><span class="t5">Sammukhā paṭiggahitaṃ</span></p><p class="p3"><span class="t1">In its presence I learned this</span></p><p class="p3"><span class="t5">Asukasmiṃ nāma āvāse</span></p><p class="p3"><span class="t1">Or in such and such a residence</span></p><p class="p3"><span class="t5">Sambahulā therā bhikkhū viharanti</span></p><p class="p3"><span class="t1">Many elder bhikkhus are dwelling</span></p><p class="p3"><span class="t5">Bahu-ssutā āgat&#8217;āgamā</span></p><p class="p3"><span class="t1">Who are learned heirs to the heritage</span></p><p class="p3"><span class="t5">Dhamma-dharā vinaya-dharā mātikā-dharā</span></p><p class="p3"><span class="t1">Experts on the Dhamma experts on the Vinaya experts on the outlines</span></p><p class="p3"><span class="t5">Tesaṃ me therānaṃ sammukhā sutaṃ</span></p><p class="p3"><span class="t1">In the presence of those elders I heard this</span></p><p class="p3"><span class="t5">Sammukhā paṭiggahitaṃ</span></p><p class="p3"><span class="t1">In their presence I learned this</span></p><p class="p3"><span class="t5">Asukasmiṃ nāma āvāse</span></p><p class="p3"><span class="t1">Or in such and such a residence</span></p><p class="p3"><span class="t5">Eko thero bhikkhu viharati</span></p><p class="p3"><span class="t1">One elder bhikkhu is dwelling</span></p><p class="p3"><span class="t5">Bahu-ssuto āgat&#8217;āgamo</span></p><p class="p3"><span class="t1">Who is learned an heir to the heritage</span></p><p class="p3"><span class="t5">Dhamma-dharo vinaya-dharo mātikā-dharo</span></p><p class="p3"><span class="t1">An expert on the Dhamma an expert on the Vinaya an expert on the outlines</span></p><p class="p3"><span class="t5">Tassa me therassa sammukhā sutaṃ</span></p><p class="p3"><span class="t1">In the presence of that elder I heard this</span></p><p class="p3"><span class="t5">Sammukhā paṭiggahitaṃ</span></p><p class="p3"><span class="t1">In his presence I learned this</span></p><p class="p3"><span class="t5">Ayaṃ dhammo ayaṃ vinayo idaṃ satthu-sāsanan&#8217;ti</span></p><p class="p3"><span class="t1">&#8220;This is the Dhamma this is the Vinaya</span></p><p class="p3"><span class="t1">This is the Teacher&#8217;s teaching!&#8221;</span></p><p class="p3"><span class="t5">Tassa bhikkhave bhikkhuno bhāsitaṃ</span></p><p class="p3"><span class="t1">That bhikkhu&#8217;s statement</span></p><p class="p3"><span class="t5">N&#8217;eva abhinanditabbaṃ nappaṭikkositabbaṃ</span></p><p class="p3"><span class="t1">Should neither be approved nor rejected</span></p><p class="p3"><span class="t5">Anabhinanditvā appaṭikkositvā</span></p><p class="p3"><span class="t1">Without approving or rejecting it</span></p><p class="p3"><span class="t5">Padabyañjanāni sādhukaṃ uggahetvā</span></p><p class="p3"><span class="t1">Having thoroughly learned those words and phrases</span></p><p class="p3"><span class="t5">Sutte otāretabbāni</span></p><p class="p3"><span class="t1">They ought to be found in the suttas</span></p><p class="p3"><span class="t5">Vinaye sandassetabbāni</span></p><p class="p3"><span class="t1">And seen in the Vinaya</span></p><p class="p3"><span class="t5">Na c&#8217;eva sutte otaranti na vinaye sandissanti</span></p><p class="p3"><span class="t1">If they are neither found in the suttas nor seen in the Vinaya</span></p><p class="p3"><span class="t5">Niṭṭham&#8217;ettha gantabbaṃ:</span></p><p class="p3"><span class="t1">You should draw the conclusion:</span></p><p class="p3"><span class="t5">Addhā idaṃ na c&#8217;eva tassa bhagavato vacanaṃ arahato sammāsambuddhassa</span></p><p class="p3"><span class="t1">Surely this is not the word of the Blessed One</span></p><p class="p3"><span class="t1">The Worthy One the Perfectly Enlightened One</span></p><p class="p3"><span class="t5">Tassa ca therassa duggahitan&#8217;ti</span></p><p class="p3"><span class="t1">It has been badly learned by that elder</span></p><p class="p3"><span class="t5">Iti h&#8217;etaṃ bhikkhave chaḍḍeyyātha</span></p><p class="p3"><span class="t1">Thus you should discard it</span></p><p class="p3"><span class="t5">Sutte c&#8217;eva otaranti vinaye ca sandissanti</span></p><p class="p3"><span class="t1">But if they are found in the suttas and seen in the Vinaya</span></p><p class="p3"><span class="t5">Niṭṭham&#8217;ettha gantabbaṃ</span></p><p class="p3"><span class="t1">You should draw the conclusion:</span></p><p class="p3"><span class="t5">Addhā idaṃ tassa bhagavato vacanaṃ arahato sammāsambuddhassa</span></p><p class="p3"><span class="t1">Surely this is the word of the Blessed One</span></p><p class="p3"><span class="t1">The Worthy One the Perfectly Enlightened One</span></p><p class="p3"><span class="t5">Imassa ca bhikkhuno suggahitaṃ</span></p><p class="p3"><span class="t1">It has been well-learned by that bhikkhu</span></p><p class="p3"><span class="t5">Tassa ca saṅghassa suggahitaṃ</span></p><p class="p3"><span class="t1">It has been well-learned by that Saṅgha</span></p><p class="p3"><span class="t5">Tesañ&#8217;ca therānaṃ suggahitaṃ</span></p><p class="p3"><span class="t1">It has been well-learned by those elders</span></p><p class="p3"><span class="t5">Tassa ca therassa suggahitan&#8217;ti</span></p><p class="p3"><span class="t1">It has been well-learned by that elder</span></p><p class="p3"><span class="t5">Ime kho bhikkhave cattāro mahāpadesā&#8217;ti</span></p><p class="p3"><span class="t1">Bhikkhus these are the four great references</span></p><h2><p class="p3"><span class="t5">[AN 4.180]</span></p></h2>', '-27-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Principles of Cordiality', 'title', 28);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 28, '<h2>Principles of Cordiality</h2><h2>Cha sāraṇīyā-dhammā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ sāraṇīya-dhammā-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Cha&#8217;y&#8217;ime bhikkhave dhammā sāraṇīyā</span></p><p class="p3"><span class="t1">Bhikkhus there are these six principles of cordiality</span></p><p class="p3"><span class="t5">Piyakaraṇā garukaraṇā</span></p><p class="p3"><span class="t1">That create endearment and respect</span></p><p class="p3"><span class="t5">Saṅgahāya</span></p><p class="p3"><span class="t1">And conduce to cohesion</span></p><p class="p3"><span class="t5">Avivādāya</span></p><p class="p3"><span class="t1">To non-dispute</span></p><p class="p3"><span class="t5">Sāmaggiyā ekībhāvāya saṃvattanti</span></p><p class="p3"><span class="t1">To concord and unity</span></p><p class="p3"><span class="t5">Katame cha</span></p><p class="p3"><span class="t1">What are the six?</span></p><p class="p3"><span class="t5">Idha bhikkhave bhikkhuno</span></p><p class="p3"><span class="t1">Here bhikkhus a bhikkhu</span></p><p class="p3"><span class="t5">Mettaṃ kāya-kammaṃ vacī-kammaṃ mano-kammaṃ paccupaṭṭhitaṃ hoti</span></p><p class="p3"><span class="t1">Maintains bodily verbal and mental acts of loving-kindness</span></p><p class="p3"><span class="t5">Sabrahmacārīsu āvi c&#8217;eva raho ca</span></p><p class="p3"><span class="t1">Both in public and in private towards his spiritual companions</span></p><p class="p3"><span class="t5">Bhikkhu ye te lābhā</span></p><p class="p3"><span class="t1">Whatever a bhikkhu gains</span></p><p class="p3"><span class="t5">Dhammikā dhamma-laddhā</span></p><p class="p3"><span class="t1">That accords with the Dhamma and has been righteously obtained</span></p><p class="p3"><span class="t5">Antamaso patta-pariyāpanna-mattam&#8217;pi</span></p><p class="p3"><span class="t1">Even including the mere contents of his bowl</span></p><p class="p3"><span class="t5">Tathā-rūpehi lābhehi appaṭivibhatta-bhogī hoti</span></p><p class="p3"><span class="t1">Such gains he does not use without sharing</span></p><p class="p3"><span class="t5">Sīlavantehi sabrahmacārīhi sādhāraṇa-bhogī</span></p><p class="p3"><span class="t1">But uses them in common with his virtuous spiritual companions</span></p><p class="p3"><span class="t5">Bhikkhu yāni tāni sīlāni</span></p><p class="p3"><span class="t1">A bhikkhu dwells possessing the virtues</span></p><p class="p3"><span class="t5">Akhaṇḍāni acchiddāni asabalāni akammāsāni bhujissāni</span></p><p class="p3"><span class="t1">That are unbroken untorn unblotched unmottled liberating</span></p><p class="p3"><span class="t5">Viññu-ppasatthāni aparāmaṭṭhāni samādhi-saṃvattanikāni</span></p><p class="p3"><span class="t1">Commended by the wise not misapprehended and conducive to concentration</span></p><p class="p3"><span class="t5">Tathā-rūpesu sīlesu sīla-sāmaññagato viharati</span></p><p class="p3"><span class="t1">Endowed with such virtues he dwells</span></p><p class="p3"><span class="t5">Sabrahmacārīsu āvi c&#8217;eva raho ca</span></p><p class="p3"><span class="t1">Both in public and in private towards his spiritual companions</span></p><p class="p3"><span class="t5">Bhikkhu y&#8217;āyaṃ diṭṭhi</span></p><p class="p3"><span class="t1">A bhikkhu dwells possessing a view</span></p><p class="p3"><span class="t5">Ariyā niyyānikā</span></p><p class="p3"><span class="t1">That is noble and emancipating</span></p><p class="p3"><span class="t5">Niyyāti takkarassa sammā dukkha-kkhayāya</span></p><p class="p3"><span class="t1">Acting it out leads to the complete destruction of suffering</span></p><p class="p3"><span class="t5">Tathā-rūpāya diṭṭhiyā diṭṭhi-sāmaññagato viharati</span></p><p class="p3"><span class="t1">Endowed with such a view he dwells</span></p><p class="p3"><span class="t5">Sabrahmacārīsu āvi c&#8217;eva raho ca</span></p><p class="p3"><span class="t1">Both in public and in private towards his spiritual companions</span></p><p class="p3"><span class="t5">Ime kho bhikkhave cha sāraṇīyā dhammā</span></p><p class="p3"><span class="t1">Bhikkhus these are the six principles of cordiality</span></p><p class="p3"><span class="t5">Piyakaraṇā garukaraṇā</span></p><p class="p3"><span class="t1">That create endearment and respect</span></p><p class="p3"><span class="t5">Saṅgahāya</span></p><p class="p3"><span class="t1">And conduce to cohesion</span></p><p class="p3"><span class="t5">Avivādāya</span></p><p class="p3"><span class="t1">To non-dispute</span></p><p class="p3"><span class="t5">Sāmaggiyā ekībhāvāya saṃvattanti</span></p><p class="p3"><span class="t1">To concord and unity</span></p><h2><p class="p3"><span class="t5">[MN 48]</span></p></h2><p class="p3"><span class="t5">Ime ce tumhe cha sāraṇīye dhamme samādāya vatteyyātha</span></p><p class="p3"><span class="t1">If you undertake and maintain these six principles of cordiality</span></p><p class="p3"><span class="t5">Passatha no tumhe taṃ vacana-pathaṃ</span></p><p class="p3"><span class="t1">Do you see any course of speech</span></p><p class="p3"><span class="t5">Aṇuṃ vā thūlaṃ vā yaṃ tumhe n&#8217;ādhivāseyyāthā&#8217;ti</span></p><p class="p3"><span class="t1">Trivial or gross that you could not endure?</span></p><p class="p3"><span class="t5">No h&#8217;etaṃ bhante</span></p><p class="p3"><span class="t1">No venerable sir</span></p><p class="p3"><span class="t5">Tasmā&#8217;t&#8217;iha ime cha sāraṇīye dhamme samādāya vattatha</span></p><p class="p3"><span class="t1">Therefore undertake and maintain these six principles of cordiality</span></p><p class="p3"><span class="t5">Taṃ vo bhavissati dīgharattaṃ hitāya sukhāyā&#8217;ti</span></p><p class="p3"><span class="t1">That will lead to your welfare and happiness for a long time</span></p><h2><p class="p3"><span class="t5">[MN 104]</span></p></h2>', '-28-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Principles of Non-Decline', 'title', 29);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 29, '<h2>Principles of Non-Decline</h2><h2>Aparihāniya-dhammā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ aparihāniya-dhamma-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Katame bhikkhave satta aparihāniyā dhammā</span></p><p class="p3"><span class="t1">What bhikkhus are the seven principles of non-decline?</span></p><p class="p3"><span class="t5">Yāvakīvañ&#8217;ca bhikkhave bhikkhū</span></p><p class="p3"><span class="t1">As long as the bhikkhus</span></p><p class="p3"><span class="t5">Abhiṇhaṃ sannipātā bhavissanti sannipāta-bahulā</span></p><p class="p3"><span class="t1">Assemble often and hold frequent assemblies</span></p><p class="p3"><span class="t5">Vuddhi&#8217;y&#8217;eva bhikkhave bhikkhūnaṃ pāṭikaṅkhā no parihāni</span></p><p class="p3"><span class="t1">Only growth is to be expected for the bhikkhus not decline</span></p><p class="p3"><span class="t5">Yāvakīvañ&#8217;ca bhikkhave bhikkhū</span></p><p class="p3"><span class="t1">As long as the bhikkhus</span></p><p class="p3"><span class="t5">Samaggā sannipatissanti</span></p><p class="p3"><span class="t1">Assemble in harmony</span></p><p class="p3"><span class="t5">Samaggā vuṭṭhahissanti</span></p><p class="p3"><span class="t1">Adjourn in harmony</span></p><p class="p3"><span class="t5">Samaggā saṅgha-karaṇīyāni karissanti</span></p><p class="p3"><span class="t1">And conduct the affairs of the Saṅgha in harmony</span></p><p class="p3"><span class="t5">Vuddhi&#8217;y&#8217;eva bhikkhave bhikkhūnaṃ pāṭikaṅkhā no parihāni</span></p><p class="p3"><span class="t1">Only growth is to be expected for the bhikkhus not decline</span></p><p class="p3"><span class="t5">Yāvakīvañ&#8217;ca bhikkhave bhikkhū</span></p><p class="p3"><span class="t1">As long as the bhikkhus</span></p><p class="p3"><span class="t5">Apaññattaṃ na paññāpessanti</span></p><p class="p3"><span class="t1">Do not decree anything that has not been decreed</span></p><p class="p3"><span class="t5">Paññattaṃ na samucchindissanti</span></p><p class="p3"><span class="t1">Or abolish anything that has already been decreed</span></p><p class="p3"><span class="t5">Yathā-paññattesu sikkhāpadesu samādāya vattissanti</span></p><p class="p3"><span class="t1">But undertake and follow the training rules as they have been decreed</span></p><p class="p3"><span class="t5">Vuddhi&#8217;y&#8217;eva bhikkhave bhikkhūnaṃ pāṭikaṅkhā no parihāni</span></p><p class="p3"><span class="t1">Only growth is to be expected for the bhikkhus not decline</span></p><p class="p3"><span class="t5">Yāvakīvañ&#8217;ca bhikkhave bhikkhū</span></p><p class="p3"><span class="t1">As long as the bhikkhus</span></p><p class="p3"><span class="t5">Ye te bhikkhū therā rattaññū</span></p><p class="p3"><span class="t1">Venerate those bhikkhus who are elders of long standing</span></p><p class="p3"><span class="t5">Cira-pabbajitā</span></p><p class="p3"><span class="t1">Long gone forth</span></p><p class="p3"><span class="t5">Saṅgha-pitaro saṅgha-pariṇāyakā</span></p><p class="p3"><span class="t1">Fathers and guides of the Saṅgha</span></p><p class="p3"><span class="t5">Te sakkarissanti garuṃ karissanti mānessanti pūjessanti</span></p><p class="p3"><span class="t1">Honour respect esteem them</span></p><p class="p3"><span class="t5">Tesañ&#8217;ca sotabbaṃ maññissanti</span></p><p class="p3"><span class="t1">And think they should be heeded</span></p><p class="p3"><span class="t5">Vuddhi&#8217;y&#8217;eva bhikkhave bhikkhūnaṃ pāṭikaṅkhā no parihāni</span></p><p class="p3"><span class="t1">Only growth is to be expected for the bhikkhus not decline</span></p><p class="p3"><span class="t5">Yāvakīvañ&#8217;ca bhikkhave bhikkhū</span></p><p class="p3"><span class="t1">As long as the bhikkhus</span></p><p class="p3"><span class="t5">Uppannāya taṇhāya ponobhavikāya na vasaṃ gacchissanti</span></p><p class="p3"><span class="t1">Do not come under the control of arisen craving that leads to renewed existence</span></p><p class="p3"><span class="t5">Vuddhi&#8217;y&#8217;eva bhikkhave bhikkhūnaṃ pāṭikaṅkhā no parihāni</span></p><p class="p3"><span class="t1">Only growth is to be expected for the bhikkhus not decline</span></p><p class="p3"><span class="t5">Yāvakīvañ&#8217;ca bhikkhave bhikkhū</span></p><p class="p3"><span class="t1">As long as the bhikkhus</span></p><p class="p3"><span class="t5">Āraññakesu sen&#8217;āsanesu sāpekkhā bhavissanti</span></p><p class="p3"><span class="t1">Are intent on forest lodgings</span></p><p class="p3"><span class="t5">Vuddhi&#8217;y&#8217;eva bhikkhave bhikkhūnaṃ pāṭikaṅkhā no parihāni</span></p><p class="p3"><span class="t1">Only growth is to be expected for the bhikkhus not decline</span></p><p class="p3"><span class="t5">Yāvakīvañ&#8217;ca bhikkhave bhikkhū</span></p><p class="p3"><span class="t1">As long as the bhikkhus</span></p><p class="p3"><span class="t5">Paccattaññ&#8217;eva satiṃ upaṭṭhāpessanti:</span></p><p class="p3"><span class="t1">Establish mindfulness within themselves thinking thus:</span></p><p class="p3"><span class="t5">&#8216;Kin&#8217;ti anāgatā ca pesalā sabrahmacārī āgaccheyyuṃ</span></p><p class="p3"><span class="t1">&#8216;How can well-behaved fellow monks come who have not yet come</span></p><p class="p3"><span class="t5">Āgatā ca pesalā sabrahmacārī phāsuṃ vihareyyun&#8217;ti</span></p><p class="p3"><span class="t1">And how can well-behaved fellow monks who are here dwell at ease?&#8217;</span></p><p class="p3"><span class="t5">Vuddhi&#8217;y&#8217;eva bhikkhave bhikkhūnaṃ pāṭikaṅkhā no parihāni</span></p><p class="p3"><span class="t1">Only growth is to be expected for the bhikkhus not decline</span></p><p class="p3"><span class="t5">Yāvakīvañ&#8217;ca bhikkhave ime satta aparihāniyā dhammā bhikkhūsu ṭhassanti</span></p><p class="p3"><span class="t1">Bhikkhus as long as these seven principles of non-decline continue among the bhikkhus</span></p><p class="p3"><span class="t5">Imesu ca sattasu aparihāniyesu dhammesu bhikkhū sandississanti</span></p><p class="p3"><span class="t1">And the bhikkhus are seen established in them</span></p><p class="p3"><span class="t5">Vuddhi&#8217;y&#8217;eva bhikkhave bhikkhūnaṃ pāṭikaṅkhā no parihāni</span></p><p class="p3"><span class="t1">Only growth is to be expected for the bhikkhus not decline</span></p><h2><p class="p3"><span class="t5">[AN 7.23]</span></p></h2><p class="p3"><span class="t5">Yāvakīvañ&#8217;ca bhikkhave bhikkhū</span></p><p class="p3"><span class="t1">As long as the bhikkhus</span></p><p class="p3"><span class="t5">Anicca-saññaṃ bhāvessanti anatta-saññaṃ bhāvessanti</span></p><p class="p3"><span class="t1">Develop the perception of impermanence the perception of not-self</span></p><p class="p3"><span class="t5">Asubha-saññaṃ bhāvessanti ādīnava-saññaṃ bhāvessanti</span></p><p class="p3"><span class="t1">The perception of unattractiveness the perception of danger</span></p><p class="p3"><span class="t5">Pahāna-saññaṃ&#160;bhāvessanti virāga-saññaṃ&#160;bhāvessanti nirodha-saññaṃ&#160;bhāvessanti</span></p><p class="p3"><span class="t1">The perception of abandoning the perception of dispassion the perception of cessation</span></p><p class="p3"><span class="t5">Vuddhi&#8217;y&#8217;eva bhikkhave bhikkhūnaṃ pāṭikaṅkhā no parihāni</span></p><p class="p3"><span class="t1">Only growth is to be expected for the bhikkhus not decline</span></p><p class="p3"><span class="t5">Yāvakīvañ&#8217;ca bhikkhave bhikkhū</span></p><p class="p3"><span class="t1">As long as the bhikkhus</span></p><p class="p3"><span class="t5">Hirimanto bhavissanti ottappino bhavissanti bahu-ssutā bhavissanti</span></p><p class="p3"><span class="t1">Develop moral shame moral dread learnedness</span></p><p class="p3"><span class="t5">Āraddha-vīriyā bhavissanti satimanto bhavissanti paññavanto bhavissanti</span></p><p class="p3"><span class="t1">Become energetic mindful and wise</span></p><p class="p3"><span class="t5">Na oramattakena vises&#8217;ādhigamena antarāvosānaṃ āpajjissanti</span></p><p class="p3"><span class="t1">Do not stop midway on account of some minor achievement of distinction</span></p><p class="p3"><span class="t5">Vuddhi&#8217;y&#8217;eva bhikkhave bhikkhūnaṃ pāṭikaṅkhā no parihāni</span></p><p class="p3"><span class="t1">Only growth is to be expected for the bhikkhus not decline</span></p><h2><p class="p3"><span class="t5">[AN 7.23-27]</span></p></h2><p class="p3"><span class="t5">Ime bhikkhave dhammā sekhassa bhikkhuno aparihānāya saṃvattanti</span></p><p class="p3"><span class="t1">Bhikkhus these qualities lead to the non-decline of a bhikkhu who is a trainee</span></p><p class="p3"><span class="t5">Na kamm&#8217;ārāmatā na bhass&#8217;ārāmatā na nidd&#8217;ārāmatā na saṅgaṇik&#8217;ārāmatā</span></p><p class="p3"><span class="t1">Not taking delight in work in talk in sleep in company</span></p><p class="p3"><span class="t5">Indriyesu gutta-dvāratā bhojane mattaññutā</span></p><p class="p3"><span class="t1">Guarding the doors of the sense faculties moderation in eating</span></p><p class="p3"><span class="t5">Asaṃsagg&#8217;ārāmatā nippapañc&#8217;ārāmatā</span></p><p class="p3"><span class="t1">Not taking delight in bonding not taking delight in proliferation</span></p><p class="p3"><span class="t5">Sovacassatā kalyāṇamittatā</span></p><p class="p3"><span class="t1">Being easy to correct and good friendship</span></p><p class="p3"><span class="t5">Ime kho bhikkhave dhammā sekhassa bhikkhuno aparihānāya saṃvattantī&#8217;ti</span></p><p class="p3"><span class="t1">Bhikkhus these qualities lead to the non-decline of a bhikkhu who is a trainee</span></p><h2><p class="p3"><span class="t5">[AN 6.22 &amp; AN 8.79]</span></p></h2>', '-29-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Striving According to the Dhamma', 'title', 30);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 30, '<h2>Striving According to the Dhamma</h2><h2>Dhamma-pahaṃsāna</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ dhamma-pahaṃsāna-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Evaṃ svākkhāto bhikkhave mayā dhammo</span></p><p class="p3"><span class="t1">Bhikkhus the Dhamma has thus been well-expounded by me</span></p><p class="p3"><span class="t5">Uttāno</span></p><p class="p3"><span class="t1">Elucidated</span></p><p class="p3"><span class="t5">Vivaṭo</span></p><p class="p3"><span class="t1">Disclosed</span></p><p class="p3"><span class="t5">Pakāsito</span></p><p class="p3"><span class="t1">Revealed</span></p><p class="p3"><span class="t5">Chinna-pilotiko</span></p><p class="p3"><span class="t1">And stripped of patchwork</span></p><p class="p3"><span class="t5">Alam&#8217;eva saddhā-pabbajitena kula-puttena vīriyaṃ ārabhituṃ</span></p><p class="p3"><span class="t1">This is enough for a clansman</span></p><p class="p3"><span class="t1">Who has gone forth out of faith</span></p><p class="p3"><span class="t1">To arouse his energy thus</span></p><p class="p3"><span class="t5">Kāmaṃ taco ca nahāru ca aṭṭhi ca avasissatu</span></p><p class="p3"><span class="t1">&#8220;Willingly let only my skin sinews and bones remain</span></p><p class="p3"><span class="t5">Sarīre upasussatu maṃsa-lohitaṃ</span></p><p class="p3"><span class="t1">And let the flesh and blood in this body wither away</span></p><p class="p3"><span class="t5">Yaṃ taṃ purisa-thāmena purisa-vīriyena purisa-parakkamena pattabbaṃ</span></p><p class="p3"><span class="t5">Na taṃ apāpuṇitvā</span></p><p class="p3"><span class="t5">Vīriyassa saṇṭhānaṃ bhavissatī&#8217;ti</span></p><p class="p3"><span class="t1">As long as whatever is to be attained</span></p><p class="p3"><span class="t1">By manly strength</span></p><p class="p3"><span class="t1">By manly energy</span></p><p class="p3"><span class="t1">By manly effort</span></p><p class="p3"><span class="t1">Has not been attained</span></p><p class="p3"><span class="t1">Let not my efforts stand still&#8221;</span></p><p class="p3"><span class="t5">Dukkhaṃ bhikkhave kusīto viharati</span></p><p class="p3"><span class="t1">Bhikkhus the lazy person dwells in suffering</span></p><p class="p3"><span class="t5">Vokiṇṇo pāpakehi akusalehi dhammehi</span></p><p class="p3"><span class="t1">Soiled by evil unwholesome states</span></p><p class="p3"><span class="t5">Mahantañ&#8217;ca sadatthaṃ parihāpeti</span></p><p class="p3"><span class="t1">And great is the personal good that he neglects</span></p><p class="p3"><span class="t5">Āraddha-vīriyo ca kho bhikkhave sukhaṃ viharati</span></p><p class="p3"><span class="t1">The energetic person though dwells happily</span></p><p class="p3"><span class="t5">Pavivitto pāpakehi akusalehi dhammehi</span></p><p class="p3"><span class="t1">Well withdrawn from evil unwholesome states</span></p><p class="p3"><span class="t5">Mahantañ&#8217;ca sadatthaṃ paripūreti</span></p><p class="p3"><span class="t1">And great is the personal good that he achieves</span></p><p class="p3"><span class="t5">Na bhikkhave hīnena aggassa patti hoti</span></p><p class="p3"><span class="t1">Bhikkhus it is not by lower means that the supreme is attained</span></p><p class="p3"><span class="t5">Aggena ca kho bhikkhave aggassa patti hoti</span></p><p class="p3"><span class="t1">But bhikkhus it is by the supreme that the supreme is attained</span></p><p class="p3"><span class="t5">Maṇḍapeyyam&#8217;idaṃ bhikkhave brahmacariyaṃ</span></p><p class="p3"><span class="t1">Bhikkhus this holy life is like cream from milk</span></p><p class="p3"><span class="t5">Satthā sammukhī-bhūto</span></p><p class="p3"><span class="t1">The Teacher is present</span></p><p class="p3"><span class="t5">Tasmā&#8217;t&#8217;iha bhikkhave vīriyaṃ ārabhatha</span></p><p class="p3"><span class="t1">Therefore bhikkhus start to arouse your energy</span></p><p class="p3"><span class="t5">Appattassa pattiyā</span></p><p class="p3"><span class="t1">For the attainment of the as yet unattained</span></p><p class="p3"><span class="t5">Anadhigatassa adhigamāya</span></p><p class="p3"><span class="t1">For the achievement of the as yet unachieved</span></p><p class="p3"><span class="t5">Asacchikatassa sacchikiriyāya</span></p><p class="p3"><span class="t1">For the realization of the as yet unrealized</span></p><p class="p3"><span class="t5">&#8216;Evaṃ no ayaṃ amhākaṃ pabbajjā</span></p><p class="p3"><span class="t5">Avaṅkatā avañjhā bhavissati</span></p><p class="p3"><span class="t1">Thinking thus:</span></p><p class="p3"><span class="t1">&#8220;Our going forth will not be crooked and barren</span></p><p class="p3"><span class="t5">Saphalā saudrayā</span></p><p class="p3"><span class="t1">But will become fruitful and fertile</span></p><p class="p3"><span class="t5">Yesaṃ mayaṃ paribhuñjāma</span></p><p class="p3"><span class="t5">Cīvara-piṇḍapāta</span></p><p class="p3"><span class="t5">Sen&#8217;āsana-gilāna-paccaya bhesajja-parikkhāraṃ</span></p><p class="p3"><span class="t5">Tesaṃ te kārā amhesu</span></p><p class="p3"><span class="t1">And all our use of robes</span></p><p class="p3"><span class="t1">Almsfood</span></p><p class="p3"><span class="t1">Lodgings</span></p><p class="p3"><span class="t1">Supports for the sick and medicinal requisites</span></p><p class="p3"><span class="t1">Given by others for our support</span></p><p class="p3"><span class="t5">Maha-pphalā bhavissanti mah&#8217;ānisaṃsā&#8217;ti</span></p><p class="p3"><span class="t1">Will reward them with great fruit and great benefit&#8221;</span></p><p class="p3"><span class="t5">Evaṃ hi vo bhikkhave sikkhitabbaṃ</span></p><p class="p3"><span class="t1">Bhikkhus you should train yourselves thus</span></p><p class="p3"><span class="t5">Att&#8217;atthaṃ vā hi bhikkhave sampassamānena</span></p><p class="p3"><span class="t1">Considering your own good</span></p><p class="p3"><span class="t5">Alam&#8217;eva appamādena sampādetuṃ</span></p><p class="p3"><span class="t1">It is enough to strive for the goal without negligence</span></p><p class="p3"><span class="t5">Par&#8217;atthaṃ vā hi bhikkhave sampassamānena</span></p><p class="p3"><span class="t1">Bhikkhus considering the good of others</span></p><p class="p3"><span class="t5">Alam&#8217;eva appamādena sampādetuṃ</span></p><p class="p3"><span class="t1">It is enough to strive for the goal without negligence</span></p><p class="p3"><span class="t5">Ubhay&#8217;atthaṃ vā hi bhikkhave sampassamānena</span></p><p class="p3"><span class="t1">Bhikkhus considering the good of both</span></p><p class="p3"><span class="t5">Alam&#8217;eva appamādena sampādetun&#8217;ti</span></p><p class="p3"><span class="t1">It is enough to strive for the goal without negligence</span></p><h2><p class="p3"><span class="t5">[SN 12.22]</span></p></h2>', '-30-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Buddhas Final Instruction', 'title', 31);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 31, '<h2>The Buddha&#8217;s Final Instruction</h2><h2>Buddha-pacchima-ovāda</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ buddha-pacchima-ovāda-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Siyā kho tumhākaṃ evam&#8217;assa</span></p><p class="p3"><span class="t1">Now if it occurs to you</span></p><p class="p3"><span class="t5">Atīta-satthukaṃ pāvacanaṃ n&#8217;atthi no satthā&#8217;ti</span></p><p class="p3"><span class="t1"><q>The Teacher&#8217;s word has passed we are without a teacher</q></span></p><p class="p3"><span class="t5">Na kho pan&#8217;etaṃ evaṃ daṭṭhabbaṃ</span></p><p class="p3"><span class="t1">You should not view it this way</span></p><p class="p3"><span class="t5">Yo vo mayā dhammo ca vinayo ca desito paññatto</span></p><p class="p3"><span class="t5">So vo mam&#8217;accayena satthā</span></p><p class="p3"><span class="t1">Whatever Dhamma and Vinaya</span></p><p class="p3"><span class="t1">I have pointed out and formulated for you</span></p><p class="p3"><span class="t1">That will be your teacher when I am gone</span></p><p class="p3"><span class="t5">Handa dāni bhikkhave āmantayāmi vo</span></p><p class="p3"><span class="t1">Now bhikkhus I declare to you</span></p><p class="p3"><span class="t5">Vaya-dhammā saṅkhārā</span></p><p class="p3"><span class="t1">Conditioned things are of ceasing nature</span></p><p class="p3"><span class="t5">Appamādena sampādetha</span></p><p class="p3"><span class="t1">Perfect yourselves not being negligent</span></p><p class="p3"><span class="t5">Ayaṃ tathāgatassa pacchimā vācā</span></p><p class="p3"><span class="t1">These are the Tathāgata&#8217;s final words</span></p><h2><p class="p3"><span class="t5">[DN 16]</span></p></h2>', '-31-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Reflections', 'chapter', 32);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Four Requisites', 'title', 32);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 32, '<h2>The Four Requisites</h2><h2>Cattāro parikkhārā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ taṅ&#8217;khaṇika-paccavekkhaṇa-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Paṭisaṅkhā yoniso cīvaraṃ paṭisevāmi</span></p><p class="p3"><span class="t5">Yāva&#8217;d&#8217;eva sītassa paṭighātāya</span></p><p class="p3"><span class="t5">Uṇhassa paṭighātāya</span></p><p class="p3"><span class="t5">Ḍaṃsa-makasa-vāt&#8217;ātapa-siriṃsapa-samphassānaṃ paṭighātāya</span></p><p class="p3"><span class="t5">Yāva&#8217;d&#8217;eva hirikopina-paṭicchādan&#8217;atthaṃ</span></p><p class="p3"><span class="t1">Wisely reflecting I use the robe</span></p><p class="p3"><span class="t1">Only to ward off cold to ward off heat to ward off the touch of flies mosquitoes wind burning and creeping things</span></p><p class="p3"><span class="t1">Only for the sake of modesty</span></p><p class="p3"><span class="t5">Paṭisaṅkhā yoniso piṇḍapātaṃ paṭisevāmi</span></p><p class="p3"><span class="t5">N&#8217;eva davāya na madāya na maṇḍanāya na vibhūsanāya</span></p><p class="p3"><span class="t5">Yāva&#8217;d&#8217;eva imassa kāyassa ṭhitiyā yāpanāya vihiṃs&#8217;ūparatiyā brahmacariy&#8217;ānuggahāya</span></p><p class="p3"><span class="t5">Iti purāṇañ&#8217;ca vedanaṃ paṭihaṅkhāmi navañ&#8217;ca vedanaṃ na uppādessāmi</span></p><p class="p3"><span class="t5">Yātrā ca me bhavissati anavajjatā ca phāsuvihāro cā&#8217;ti</span></p><p class="p3"><span class="t1">Wisely reflecting I use almsfood</span></p><p class="p3"><span class="t1">Not for fun not for pleasure not for fattening not for beautification</span></p><p class="p3"><span class="t1">Only for the maintenance and nourishment of this body</span></p><p class="p3"><span class="t1">For keeping it healthy for helping with the holy life</span></p><p class="p3"><span class="t1">Thinking thus: &#8220;I will allay hunger without overeating</span></p><p class="p3"><span class="t1">So that I may continue to live blamelessly and at ease&#8221;</span></p><p class="p3"><span class="t5">Paṭisaṅkhā yoniso sen&#8217;āsanaṃ paṭisevāmi</span></p><p class="p3"><span class="t5">Yāva&#8217;d&#8217;eva sītassa paṭighātāya</span></p><p class="p3"><span class="t5">Uṇhassa paṭighātāya</span></p><p class="p3"><span class="t5">Ḍaṃsa-makasa-vāt&#8217;ātapa-siriṃsapa-samphassānaṃ paṭighātāya</span></p><p class="p3"><span class="t5">Yāva&#8217;d&#8217;eva utuparissaya-vinodanaṃ paṭisallān&#8217;ārām&#8217;atthaṃ</span></p><p class="p3"><span class="t1">Wisely reflecting I use the lodging</span></p><p class="p3"><span class="t1">Only to ward off cold to ward off heat to ward off the touch of flies mosquitoes wind burning and creeping things</span></p><p class="p3"><span class="t1">Only to remove the danger from weather and for living in seclusion</span></p><p class="p3"><span class="t5">Paṭisaṅkhā yoniso gilāna-paccaya-bhesajja-parikkhāraṃ paṭisevāmi</span></p><p class="p3"><span class="t5">Yāva&#8217;d&#8217;eva uppannānaṃ veyyābādhikānaṃ vedanānaṃ paṭighātāya</span></p><p class="p3"><span class="t5">Abyāpajjha-paramatāyā&#8217;ti</span></p><p class="p3"><span class="t1">Wisely reflecting I use supports for the sick and medicinal requisites</span></p><p class="p3"><span class="t1">Only to ward off painful feelings that have arisen</span></p><p class="p3"><span class="t1">For the maximum freedom from disease</span></p><h2><p class="p3"><span class="t5">[MN 2]</span></p></h2>', '-32-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Repulsiveness of Food', 'title', 33);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 33, '<h2>The Repulsiveness of Food</h2><h2>Āhāra-paṭikūla-paccavekkhaṇa-pāṭho</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ āhāra-paṭikūla-paccavekkhaṇa-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Āhāre paṭikūla-saññā-paricitena bhikkhave bhikkhuno cetasā bahulaṃ viharato</span></p><p class="p3"><span class="t1">When a bhikkhu often dwells with a mind</span></p><p class="p3"><span class="t1">Accustomed to the perception of the repulsiveness of food</span></p><p class="p3"><span class="t5">Rasa-taṇhāya cittaṃ patilīyati</span></p><p class="p3"><span class="t1">His mind shrinks away from craving for tastes</span></p><p class="p3"><span class="t5">Patikuṭati pativattati na sampasāriyati</span></p><p class="p3"><span class="t1">Turns back from it</span></p><p class="p3"><span class="t1">Rolls away from it</span></p><p class="p3"><span class="t1">And is not drawn towards it</span></p><p class="p3"><span class="t5">Upekkhā vā pāṭikulyatā vā saṇṭhāti</span></p><p class="p3"><span class="t1">Either equanimity or disgust become settled in him</span></p><h2><p class="p3"><span class="t5">[AN 7.49]</span></p></h2><p class="p3"><span class="t5">Sabbo pan&#8217;āyaṃ piṇḍapāto ajigucchanīyo</span></p><p class="p3"><span class="t1">None of this almsfood is innately repulsive</span></p><p class="p3"><span class="t5">Imaṃ pūti-kāyaṃ patvā</span></p><p class="p3"><span class="t1">But touching this unclean body</span></p><p class="p3"><span class="t5">Ativiya jigucchanīyo jāyati</span></p><p class="p3"><span class="t1">It becomes disgusting indeed</span></p><h2><p class="p3"><span class="t5">[Trad]</span></p></h2>', '-33-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Universal Well-Being', 'title', 34);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 34, '<h2>Universal Well-Being</h2><h2>Mettā-pharaṇa</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ mettāpharaṇaṃ karomase</span></p><p class="p3"><span class="t5">Ahaṃ sukhito homi</span></p><p class="p3"><span class="t5">Niddukkho homi</span></p><p class="p3"><span class="t5">Avero homi</span></p><p class="p3"><span class="t5">Abyāpajjho homi</span></p><p class="p3"><span class="t5">Anīgho homi</span></p><p class="p3"><span class="t5">Sukhī attānaṃ pariharāmi</span></p><p class="p3"><span class="t5">Sabbe sattā sukhitā hontu</span></p><p class="p3"><span class="t5">Sabbe sattā averā hontu</span></p><p class="p3"><span class="t5">Sabbe sattā abyāpajjhā hontu</span></p><p class="p3"><span class="t5">Sabbe sattā anīghā hontu</span></p><p class="p3"><span class="t5">Sabbe sattā sukhī attānaṃ pariharantu</span></p><p class="p3"><span class="t5">Sabbe sattā sabba-dukkhā pamuccantu</span></p><p class="p3"><span class="t5">Sabbe sattā laddha-sampattito mā vigacchantu</span></p><br/><p class="p3"><span class="t5">Sabbe sattā kamma-ssakā kamma-dāy&#8217;ādā kamma-yonī kamma-bandhū kamma-paṭisaraṇā</span></p><p class="p3"><span class="t5">Yaṃ kammaṃ karissanti</span></p><p class="p3"><span class="t5">Kalyāṇaṃ vā pāpakaṃ vā</span></p><p class="p3"><span class="t5">Tassa dāy&#8217;ādā bhavissanti</span></p><p class="p3"><span class="t5">Now let us recite the reflections on universal well-being</span></p><p class="p3"><span class="t1">May I abide in well-being</span></p><p class="p3"><span class="t1">In freedom from affliction</span></p><p class="p3"><span class="t1">In freedom from hostility</span></p><p class="p3"><span class="t1">In freedom from ill-will</span></p><p class="p3"><span class="t1">In freedom from anxiety</span></p><p class="p3"><span class="t1">And may I maintain well-being in myself</span></p><p class="p3"><span class="t1">May everyone abide in well-being</span></p><p class="p3"><span class="t1">In freedom from hostility</span></p><p class="p3"><span class="t1">In freedom from ill-will</span></p><p class="p3"><span class="t1">In freedom from anxiety</span></p><p class="p3"><span class="t1">And may they maintain well-being in themselves</span></p><p class="p3"><span class="t1">May all beings be released from all suffering</span></p><p class="p3"><span class="t1">And may they not be parted from the good fortune they have attained</span></p><p class="p3"><span class="t1">All beings are the owners of their kamma</span></p><p class="p3"><span class="t1">Heirs to their kamma</span></p><p class="p3"><span class="t1">Born of their kamma</span></p><p class="p3"><span class="t1">Related to their kamma</span></p><p class="p3"><span class="t1">Abide supported by their kamma</span></p><p class="p3"><span class="t1">Whatever kamma they shall do</span></p><p class="p3"><span class="t1">Either skillful or harmful</span></p><p class="p3"><span class="t1">Of such acts they will be the heirs</span></p><h2><p class="p3"><span class="t5">[AN 3.65 &amp; AN 5.57]</span></p></h2>', '-34-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Divine Abidings', 'title', 35);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 35, '<h2>The Divine Abidings</h2><h2>Brahmavihārā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ caturappamaññā obhāsanaṃ karomase</span></p><p class="p3"><span class="t5">Mettā-sahagatena cetasā ekaṃ disaṃ pharitvā viharati tathā dutiyaṃ tathā tatiyaṃ tathā catutthaṃ iti uddham&#8217;adho tiriyaṃ sabbadhi sabb&#8217;attatāya sabbāvantaṃ lokaṃ mettā-sahagatena cetasā vipulena mahaggatena appamāṇena averena abyāpajjhena pharitvā viharati</span></p><p class="p3"><span class="t5">Karuṇā-sahagatena cetasā ekaṃ disaṃ pharitvā viharati tathā dutiyaṃ tathā tatiyaṃ tathā catutthaṃ iti uddham&#8217;adho tiriyaṃ sabbadhi sabb&#8217;attatāya sabbāvantaṃ lokaṃ karuṇā-sahagatena cetasā vipulena mahaggatena appamāṇena averena abyāpajjhena pharitvā viharati</span></p><p class="p3"><span class="t5">Muditā-sahagatena cetasā ekaṃ disaṃ pharitvā viharati tathā dutiyaṃ tathā tatiyaṃ tathā catutthaṃ iti uddham&#8217;adho tiriyaṃ sabbadhi sabb&#8217;attatāya sabbāvantaṃ lokaṃ muditā-sahagatena cetasā vipulena mahaggatena appamāṇena averena abyāpajjhena pharitvā viharati</span></p><p class="p3"><span class="t5">Upekkhā-sahagatena cetasā ekaṃ disaṃ pharitvā viharati tathā dutiyaṃ tathā tatiyaṃ tathā catutthaṃ iti uddham&#8217;adho tiriyaṃ sabbadhi sabb&#8217;attatāya sabbāvantaṃ lokaṃ upekkhā-sahagatena cetasā vipulena mahaggatena appamāṇena averena abyāpajjhena pharitvā viharatī&#8217;ti</span></p><p class="p3"><span class="t5">Now let us make the Four Boundless Qualities shine forth</span></p><p class="p3"><span class="t1">I will abide pervading one quarter with a heart imbued with loving-kindness<br/></span></p><p class="p3"><span class="t1">Likewise the second likewise the third likewise the fourth</span></p><p class="p3"><span class="t1">So above and below around and everywhere and to all as to myself</span></p><p class="p3"><span class="t1">I will abide pervading the all-encompassing world with a heart imbued with loving-kindness</span></p><p class="p3"><span class="t1">Abundant exalted immeasurable without hostility and without ill-will</span></p><p class="p3"><span class="t1">I will abide pervading one quarter with a heart imbued with compassion</span></p><p class="p3"><span class="t1">Likewise the second likewise the third likewise the fourth</span></p><p class="p3"><span class="t1">So above and below around and everywhere and to all as to myself</span></p><p class="p3"><span class="t1">I will abide pervading the all-encompassing world with a heart imbued with compassion</span></p><p class="p3"><span class="t1">Abundant exalted immeasurable without hostility and without ill-will</span></p><p class="p3"><span class="t1">I will abide pervading one quarter with a heart imbued with empathetic joy</span></p><p class="p3"><span class="t1">Likewise the second likewise the third likewise the fourth</span></p><p class="p3"><span class="t1">So above and below around and everywhere and to all as to myself</span></p><p class="p3"><span class="t1">I will abide pervading the all-encompassing world with a heart imbued with empathetic joy</span></p><p class="p3"><span class="t1">Abundant exalted immeasurable without hostility and without ill-will</span></p><p class="p3"><span class="t1">I will abide pervading one quarter with a heart imbued with equanimity</span></p><p class="p3"><span class="t1">Likewise the second likewise the third likewise the fourth</span></p><p class="p3"><span class="t1">So above and below around and everywhere and to all as to myself</span></p><p class="p3"><span class="t1">I will abide pervading the all-encompassing world with a heart imbued with equanimity</span></p><p class="p3"><span class="t1">Abundant exalted immeasurable without hostility and without ill-will</span></p><h2><p class="p3"><span class="t5">[DN 13]</span></p></h2>', '-35-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Five Subjects for Frequent Reflection', 'title', 36);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 36, '<h2>Five Subjects for Frequent Reflection</h2><h2>Pañca-abhiṇha-paccavekkhaṇā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ abhiṇha-paccavekkhaṇa-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Jarā-dhammo&#8217;mhi jaraṃ anatīto</span></p><p class="p3"><span class="t1">I am of the nature to age</span></p><p class="p3"><span class="t1">I have not gone beyond ageing</span></p><p class="p3"><span class="t5">Byādhi-dhammo&#8217;mhi byādhiṃ anatīto</span></p><p class="p3"><span class="t1">I am of the nature to sicken</span></p><p class="p3"><span class="t1">I have not gone beyond sickness</span></p><p class="p3"><span class="t5">Maraṇa-dhammo&#8217;mhi maraṇaṃ anatīto</span></p><p class="p3"><span class="t1">I am of the nature to die</span></p><p class="p3"><span class="t1">I have not gone beyond dying</span></p><p class="p3"><span class="t5">Sabbehi me piyehi manāpehi nānābhāvo vinābhāvo</span></p><p class="p3"><span class="t1">All that is mine beloved and pleasing</span></p><p class="p3"><span class="t1">Will become otherwise</span></p><p class="p3"><span class="t1">Will become separated from me</span></p><p class="p3"><span class="t5">Kamma-ssako&#8217;mhi kamma-dāy&#8217;ādo kamma-yoni kamma-bandhu kamma-paṭisaraṇo</span></p><p class="p3"><span class="t5">Yaṃ kammaṃ karissāmi</span></p><p class="p3"><span class="t5">Kalyāṇaṃ vā pāpakaṃ vā</span></p><p class="p3"><span class="t5">Tassa dāy&#8217;ādo bhavissāmi</span></p><p class="p3"><span class="t1">I am the owner of my kamma</span></p><p class="p3"><span class="t1">Heir to my kamma</span></p><p class="p3"><span class="t1">Born of my kamma</span></p><p class="p3"><span class="t1">Related to my kamma</span></p><p class="p3"><span class="t1">Abide supported by my kamma</span></p><p class="p3"><span class="t1">Whatever kamma I shall do</span></p><p class="p3"><span class="t1">Either skillful or harmful</span></p><p class="p3"><span class="t1">Of such acts I will be the heir</span></p><p class="p3"><span class="t5">Evaṃ amhehi abhiṇhaṃ paccavekkhitabbaṃ</span></p><p class="p3"><span class="t1">Thus we should frequently reflect</span></p><h2><p class="p3"><span class="t5">[AN 5.57]</span></p></h2>', '-36-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Ten Subjects for Frequent Reflection by One Gone Forth', 'title', 37);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 37, '<h2>Ten Subjects for Frequent Reflection by One Gone Forth</h2><h2>Dasadhammā pabbajita-abhiṇha-paccavekkhaṇā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ pabbajita-abhiṇha-paccavekkhaṇa-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Dasa ime bhikkhave dhammā</span></p><p class="p3"><span class="t5">Pabbajitena abhiṇhaṃ paccavekkhitabbā</span></p><p class="p3"><span class="t5">Katame dasa</span></p><p class="p3"><span class="t1">Bhikkhus there are these ten dhammas which should be reflected upon again and again by one who has gone forth</span></p><p class="p3"><span class="t1">What are these ten?</span></p><p class="p3"><span class="t5">Vevaṇṇiy&#8217;amhi ajjhūpagato&#8217;ti</span></p><p class="p3"><span class="t5">Pabbajitena abhiṇhaṃ paccavekkhitabbaṃ</span></p><p class="p3"><span class="t1"><q>I have reached a state of castelessness</q></span></p><p class="p3"><span class="t1">This should be reflected upon again and again by one who has gone forth</span></p><p class="p3"><span class="t5">Para-paṭibaddhā me jīvikā&#8217;ti</span></p><p class="p3"><span class="t5">Pabbajitena abhiṇhaṃ paccavekkhitabbaṃ</span></p><p class="p3"><span class="t1"><q>My very life is sustained through the gifts of others</q></span></p><p class="p3"><span class="t1">This should be reflected upon again and again by one who has gone forth</span></p><p class="p3"><span class="t5">Añño me ākappo karaṇīyo&#8217;ti</span></p><p class="p3"><span class="t5">Pabbajitena abhiṇhaṃ paccavekkhitabbaṃ</span></p><p class="p3"><span class="t1"><q>Now my conduct should be different from before</q></span></p><p class="p3"><span class="t1">This should be reflected upon again and again by one who has gone forth</span></p><p class="p3"><span class="t5">Kacci nu kho me attā sīlato na upavadatī&#8217;ti</span></p><p class="p3"><span class="t5">Pabbajitena abhiṇhaṃ paccavekkhitabbaṃ</span></p><p class="p3"><span class="t1"><q>Does regret over my conduct arise in my mind?</q></span></p><p class="p3"><span class="t1">This should be reflected upon again and again by one who has gone forth</span></p><p class="p3"><span class="t5">Kacci nu kho maṃ anuvicca viññū sabrahmacārī sīlato na upavadantī&#8217;ti</span></p><p class="p3"><span class="t5">Pabbajitena abhiṇhaṃ paccavekkhitabbaṃ</span></p><p class="p3"><span class="t1"><q>Could my spiritual companions find fault with my conduct?</q></span></p><p class="p3"><span class="t1">This should be reflected upon again and again by one who has gone forth</span></p><p class="p3"><span class="t5">Sabbehi me piyehi manāpehi nānābhāvo vinābhāvo&#8217;ti</span></p><p class="p3"><span class="t5">Pabbajitena abhiṇhaṃ paccavekkhitabbaṃ</span></p><p class="p3"><span class="t1">&#8220;All that is mine beloved and pleasing</span></p><p class="p3"><span class="t1">Will become otherwise</span></p><p class="p3"><span class="t1">Will become separated from me&#8221;</span></p><p class="p3"><span class="t1">This should be reflected upon again and again by one who has gone forth</span></p><p class="p3"><span class="t5">Kamma-ssako&#8217;mhi kamma-dāy&#8217;ādo kamma-yoni kamma-bandhu kamma-paṭisaraṇo</span></p><p class="p3"><span class="t5">Yaṃ kammaṃ karissāmi</span></p><p class="p3"><span class="t5">Kalyāṇaṃ vā pāpakaṃ vā</span></p><p class="p3"><span class="t5">Tassa dāy&#8217;ādo bhavissāmī&#8217;ti</span></p><p class="p3"><span class="t5">Pabbajitena abhiṇhaṃ paccavekkhitabbaṃ</span></p><p class="p3"><span class="t1">&#8220;I am the owner of my kamma</span></p><p class="p3"><span class="t1">Heir to my kamma</span></p><p class="p3"><span class="t1">Born of my kamma</span></p><p class="p3"><span class="t1">Related to my kamma</span></p><p class="p3"><span class="t1">Abide supported by my kamma</span></p><p class="p3"><span class="t1">Whatever kamma I shall do</span></p><p class="p3"><span class="t1">Either skilful or harmful</span></p><p class="p3"><span class="t1">Of such acts I will be the heir&#8221;</span></p><p class="p3"><span class="t1">This should be reflected upon again and again by one who has gone forth</span></p><p class="p3"><span class="t5">&#8216;Katham&#8217;bhūtassa me rattin&#8217;divā vītipatantī&#8217;ti</span></p><p class="p3"><span class="t5">Pabbajitena abhiṇhaṃ paccavekkhitabbaṃ</span></p><p class="p3"><span class="t1">&#8220;The days and nights are relentlessly passing</span></p><p class="p3"><span class="t1">How well am I spending my time?&#8221;</span></p><p class="p3"><span class="t1">This should be reflected upon again and again by one who has gone forth</span></p><p class="p3"><span class="t5">Kacci nu kho&#8217;haṃ suññ&#8217;āgāre abhiramāmī&#8217;ti</span></p><p class="p3"><span class="t5">Pabbajitena abhiṇhaṃ paccavekkhitabbaṃ</span></p><p class="p3"><span class="t1"><q>Do I delight in solitude or not?</q></span></p><p class="p3"><span class="t1">This should be reflected upon again and again by one who has gone forth</span></p><p class="p3"><span class="t5">Atthi nu kho me uttari-manussa-dhammā alam&#8217;ariya-ñāṇa-dassana-viseso adhigato</span></p><p class="p3"><span class="t5">So&#8217;haṃ pacchime kāle sabrahmacārīhi puṭṭho na maṅku bhavissāmī&#8217;ti</span></p><p class="p3"><span class="t5">Pabbajitena abhiṇhaṃ paccavekkhitabbaṃ</span></p><p class="p3"><span class="t1">&#8220;Has my practice borne fruit with freedom or insight</span></p><p class="p3"><span class="t1">So that at the end of my life I need not feel ashamed when questioned by my spiritual companions?&#8221;</span></p><p class="p3"><span class="t1">This should be reflected upon again and again by one who has gone forth</span></p><p class="p3"><span class="t5">Ime kho bhikkhave dasa dhammā</span></p><p class="p3"><span class="t5">Pabbajitena abhiṇhaṃ paccavekkhitabbā&#8217;ti</span></p><p class="p3"><span class="t1">Bhikkhus these are the ten dhammas  which should be reflected upon again and again by one who has gone forth</span></p><h2><p class="p3"><span class="t5">[AN 10.48]</span></p></h2>', '-37-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Thirty-Two Parts', 'title', 38);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 38, '<h2>The Thirty-Two Parts</h2><h2>Dvattiṃs&#8217;ākāra-paccavekkhaṇa</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ dvattiṃs&#8217;ākāra-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Ayaṃ kho me kāyo uddhaṃ pādatalā adho kesa-matthakā taca-pariyanto pūro nāna-ppakārassa asucino</span></p><p class="p3"><span class="t1">This which is my body</span></p><p class="p3"><span class="t1">From the soles of the feet up</span></p><p class="p3"><span class="t1">And down from the crown of the head</span></p><p class="p3"><span class="t1">Is a sealed bag of skin</span></p><p class="p3"><span class="t1">Filled with unattractive things</span></p><p class="p3"><span class="t5">Atthi imasmiṃ kāye</span></p><p class="p3"><span class="t1">In this body there are</span></p><p class="p3"><span class="t5"> kesā lomā nakhā dantā taco, maṃsaṃ nhāru aṭṭhi aṭṭhimiñjaṃ vakkaṃ, hadayaṃ yakanaṃ kilomakaṃ pihakaṃ papphāsaṃ, antaṃ antaguṇaṃ udariyaṃ karīsaṃ ( matthaluṅgaṃ ), pittaṃ semhaṃ pubbo lohitaṃ sedo medo, assu vasā kheḷo siṅghāṇikā lasikā muttan’ti.</span></p><p class="p3"><span class="t1">head-hairs, body-hairs, nails, teeth, skin, flesh, sinews, bones, bone-marrow, kidneys, heart, liver, pleura, spleen, lungs, mesentery, bowels, stomach, excrement, ( brain ), bile, phlegm, pus, blood, sweat, fat, tears, tallow, saliva, snot, synovic fluid, urine.</span></p><p class="p3"><span class="t5">Evam&#8217;ayaṃ me kāyo uddhaṃ pādatalā adho kesa-matthakā taca-pariyanto pūro nāna-ppakārassa asucino</span></p><p class="p3"><span class="t1">This then which is my body</span></p><p class="p3"><span class="t1">From the soles of the feet up</span></p><p class="p3"><span class="t1">And down from the crown of the head</span></p><p class="p3"><span class="t1">Is a sealed bag of skin</span></p><p class="p3"><span class="t1">Filled with unattractive things</span></p><h2><p class="p3"><span class="t5">[DN 22]</span></p></h2>', '-38-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Recollection of Impermanence', 'title', 39);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 39, '<h2>Recollection of Impermanence</h2><h2>Anicc&#8217;ānussati</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ anicc&#8217;ānussati-pāṭhaṃ bhaṇāmase</span></p><p class="p3"><span class="t5">Sabbe saṅkhārā aniccā</span></p><p class="p3"><span class="t1">All conditioned things are impermanent</span></p><p class="p3"><span class="t5">Sabbe saṅkhārā dukkhā</span></p><p class="p3"><span class="t1">All conditioned things are dukkha</span></p><p class="p3"><span class="t5">Sabbe dhammā anattā</span></p><p class="p3"><span class="t1">All things are not-self</span></p><h2><p class="p3"><span class="t5">[Dhp 277-279]</span></p></h2><p class="p3"><span class="t5">Addhuvaṃ jīvitaṃ</span></p><p class="p3"><span class="t1">Life is not for sure</span></p><p class="p3"><span class="t5">Dhuvaṃ maraṇaṃ</span></p><p class="p3"><span class="t1">Death is for sure</span></p><p class="p3"><span class="t5">Avassaṃ mayā maritabbaṃ</span></p><p class="p3"><span class="t1">It is inevitable that I&#8217;ll die</span></p><p class="p3"><span class="t5">Maraṇa-pariyosānaṃ me jīvitaṃ</span></p><p class="p3"><span class="t1">Death is the culmination of my life</span></p><p class="p3"><span class="t5">Jīvitaṃ me aniyataṃ</span></p><p class="p3"><span class="t1">My life is uncertain</span></p><p class="p3"><span class="t5">Maraṇaṃ me niyataṃ</span></p><p class="p3"><span class="t1">My death is certain</span></p><h2><p class="p3"><span class="t5">[Dhp A]</span></p></h2><p class="p3"><span class="t5">Vata</span></p><p class="p3"><span class="t1">Indeed</span></p><p class="p3"><span class="t5">Ayaṃ kāyo</span></p><p class="p3"><span class="t1">This body</span></p><p class="p3"><span class="t5">Aciraṃ</span></p><p class="p3"><span class="t1">Will soon</span></p><p class="p3"><span class="t5">Apeta-viññāṇo</span></p><p class="p3"><span class="t1">Be void of consciousness</span></p><p class="p3"><span class="t5">Chuḍḍho</span></p><p class="p3"><span class="t1">And cast away</span></p><p class="p3"><span class="t5">Adhisessati</span></p><p class="p3"><span class="t1">It will lie</span></p><p class="p3"><span class="t5">Paṭhaviṃ</span></p><p class="p3"><span class="t1">On the ground</span></p><p class="p3"><span class="t5">Kaliṅgaraṃ iva</span></p><p class="p3"><span class="t1">Just like a rotten log</span></p><p class="p3"><span class="t5">Niratthaṃ</span></p><p class="p3"><span class="t1">Useless</span></p><h2><p class="p3"><span class="t5">[Dhp 41]</span></p></h2><p class="p3"><span class="t5">Aniccā vata saṅkhārā</span></p><p class="p3"><span class="t1">Indeed conditioned things cannot last</span></p><p class="p3"><span class="t5">Uppāda-vaya-dhammino</span></p><p class="p3"><span class="t1">Their nature is to rise and cease</span></p><p class="p3"><span class="t5">Uppajjitvā nirujjhanti</span></p><p class="p3"><span class="t1">Having arisen things must cease</span></p><p class="p3"><span class="t5">Tesaṃ vūpasamo sukho</span></p><p class="p3"><span class="t1">Their stilling is true happiness</span></p><h2><p class="p3"><span class="t5">[Trad]</span></p></h2>', '-39-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Cardinal Suttas', 'chapter', 40);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Dhammacakkappavattana-sutta', 'title', 40);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 40, '<h2>Dhammacakkappavattana-sutta</h2><hr/><p class="p3"><span class="t5">Evaṃ me sutaṃ</span></p><p class="p3"><span class="t5">Ekaṃ samayaṃ bhagavā bārāṇasiyaṃ viharati isipatane migadāye. Tatra kho bhagavā pañca-vaggiye bhikkhū āmantesi:</span></p><p class="p3"><span class="t5">Dve&#8217;me bhikkhave antā pabbajitena na sevitabbā: yo c&#8217;āyaṃ kāmesu kāma-sukhallik&#8217;ānuyogo hīno gammo pothujjaniko anariyo anattha-sañhito yo c&#8217;āyaṃ atta-kilamath&#8217;ānuyogo dukkho anariyo anattha-sañhito</span></p><p class="p3"><span class="t5">Ete te bhikkhave ubho ante anupagamma majjhimā paṭipadā tathāgatena abhisambuddhā cakkhu-karaṇī ñāṇa-karaṇī upasamāya abhiññāya sambodhāya nibbānāya saṃvattati</span></p><p class="p3"><span class="t5">Katamā ca sā bhikkhave majjhimā paṭipadā tathāgatena abhisambuddhā cakkhu-karaṇī ñāṇa-karaṇī upasamāya abhiññāya sambodhāya nibbānāya saṃvattati?</span></p><p class="p3"><span class="t5">Ayam&#8217;eva ariyo aṭṭh&#8217;aṅgiko maggo seyyath&#8217;īdaṃ:</span></p><p class="p3"><span class="t5">Sammā-diṭṭhi sammā-saṅkappo sammā-vācā sammā-kammanto sammā-ājīvo sammā-vāyāmo sammā-sati sammā-samādhi.</span></p><p class="p3"><span class="t5">Ayaṃ kho sā bhikkhave majjhimā paṭipadā tathāgatena abhisambuddhā cakkhu-karaṇī ñāṇa-karaṇī upasamāya abhiññāya sambodhāya nibbānāya saṃvattati</span></p><p class="p3"><span class="t5">Idaṃ kho pana bhikkhave dukkhaṃ ariyasaccaṃ:</span></p><p class="p3"><span class="t5">Jāti&#8217;pi dukkhā jarā&#8217;pi dukkhā byādhi&#8217;pi dukkho maraṇam&#8217;pi dukkhaṃ soka-parideva-dukkha-domanass&#8217;upāyāsā&#8217;pi dukkhā appiyehi sampayogo dukkho piyehi vippayogo dukkho yam&#8217;picchaṃ na labhati tam&#8217;pi dukkhaṃ saṅkhittena pañc&#8217;upādānakkhandā dukkhā</span></p><p class="p3"><span class="t5">Idaṃ kho pana bhikkhave dukkha-samudayo ariyasaccaṃ:</span></p><p class="p3"><span class="t5">Y&#8217;āyaṃ taṇhā ponobbhavikā nandi-rāga-sahagatā tatra-tatr&#8217;ābhinandinī seyyath&#8217;īdaṃ: kāma-taṇhā bhava-taṇhā vibhava-taṇhā</span></p><p class="p3"><span class="t5">Idaṃ kho pana bhikkhave dukkha-nirodho ariyasaccaṃ:</span></p><p class="p3"><span class="t5">Yo tassā yeva taṇhāya asesa-virāga-nirodho cāgo paṭinissaggo mutti anālayo</span></p><p class="p3"><span class="t5">Idaṃ kho pana bhikkhave dukkha-nirodha-gāminī paṭipadā ariyasaccaṃ:</span></p><p class="p3"><span class="t5">Ayam&#8217;eva ariyo aṭṭh&#8217;aṅgiko maggo seyyath&#8217;īdaṃ: sammā-diṭṭhi sammā-saṅkappo sammā-vācā sammā-kammanto sammā-ājīvo sammā-vāyāmo sammā-sati sammā-samādhi</span></p><p class="p3"><span class="t5">Idaṃ dukkhaṃ ariyasaccan&#8217;ti me bhikkhave pubbe ananussutesu dhammesu cakkhuṃ udapādi ñāṇaṃ udapādi paññā udapādi vijjā udapādi āloko udapādi</span></p><p class="p3"><span class="t5">Taṃ kho panidaṃ dukkhaṃ ariyasaccaṃ pariññeyyan&#8217;ti me bhikkhave pubbe ananussutesu dhammesu cakkhuṃ udapādi ñāṇaṃ udapādi paññā udapādi vijjā udapādi āloko udapādi</span></p><p class="p3"><span class="t5">Taṃ kho panidaṃ dukkhaṃ ariyasaccaṃ pariññātan&#8217;ti me bhikkhave pubbe ananussutesu dhammesu cakkhuṃ udapādi ñāṇaṃ udapādi paññā udapādi vijjā udapādi āloko udapādi</span></p><p class="p3"><span class="t5">Idaṃ dukkha-samudayo ariyasaccan&#8217;ti me bhikkhave pubbe ananussutesu dhammesu cakkhuṃ udapādi ñāṇaṃ udapādi paññā udapādi vijjā udapādi āloko udapādi</span></p><p class="p3"><span class="t5">Taṃ kho panidaṃ dukkha-samudayo ariyasaccaṃ pahātabban&#8217;ti me bhikkhave pubbe ananussutesu dhammesu cakkhuṃ udapādi ñāṇaṃ udapādi paññā udapādi vijjā udapādi āloko udapādi</span></p><p class="p3"><span class="t5">Taṃ kho panidaṃ dukkha-samudayo ariyasaccaṃ pahīnan&#8217;ti me bhikkhave pubbe ananussutesu dhammesu cakkhuṃ udapādi ñāṇaṃ udapādi paññā udapādi vijjā udapādi āloko udapādi</span></p><p class="p3"><span class="t5">Idaṃ dukkha-nirodho ariyasaccan&#8217;ti me bhikkhave pubbe ananussutesu dhammesu cakkhuṃ udapādi ñāṇaṃ udapādi paññā udapādi vijjā udapādi āloko udapādi</span></p><p class="p3"><span class="t5">Taṃ kho panidaṃ dukkha-nirodho ariyasaccaṃ sacchikātabban&#8217;ti me bhikkhave pubbe ananussutesu dhammesu cakkhuṃ udapādi ñāṇaṃ udapādi paññā udapādi vijjā udapādi āloko udapādi</span></p><p class="p3"><span class="t5">Taṃ kho panidaṃ dukkha-nirodho ariyasaccaṃ sacchikatan&#8217;ti me bhikkhave pubbe ananussutesu dhammesu cakkhuṃ udapādi ñāṇaṃ udapādi paññā udapādi vijjā udapādi āloko udapādi</span></p><p class="p3"><span class="t5">Idaṃ dukkha-nirodha-gāminī paṭipadā ariyasaccan&#8217;ti me bhikkhave pubbe ananussutesu dhammesu cakkhuṃ udapādi ñāṇaṃ udapādi paññā udapādi vijjā udapādi āloko udapādi</span></p><p class="p3"><span class="t5">Taṃ kho panidaṃ dukkha-nirodha-gāminī paṭipadā ariyasaccaṃ bhāvetabban&#8217;ti me bhikkhave pubbe ananussutesu dhammesu cakkhuṃ udapādi ñāṇaṃ udapādi paññā udapādi vijjā udapādi āloko udapādi</span></p><p class="p3"><span class="t5">Taṃ kho panidaṃ dukkha-nirodha-gāminī paṭipadā ariyasaccaṃ bhāvitan&#8217;ti me bhikkhave pubbe ananussutesu dhammesu cakkhuṃ udapādi ñāṇaṃ udapādi paññā udapādi vijjā udapādi āloko udapādi</span></p><p class="p3"><span class="t5">Yāvakīvañ&#8217;ca me bhikkhave imesu catūsu ariyasaccesu evan&#8217;tiparivaṭṭaṃ dvādas&#8217;ākāraṃ yathā-bhūtaṃ ñāṇa-dassanaṃ na suvisuddhaṃ ahosi n&#8217;eva tāv&#8217;āhaṃ bhikkhave sadevake loke samārake sabrahmake sassamaṇa-brāhmaṇiyā pajāya sadeva-manussāya anuttaraṃ sammāsambodhiṃ abhisambuddho paccaññāsiṃ</span></p><p class="p3"><span class="t5">Yato ca kho me bhikkhave imesu catūsu ariyasaccesu evan&#8217;tiparivaṭṭaṃ dvādas&#8217;ākāraṃ yathā-bhūtaṃ ñāṇa-dassanaṃ suvisuddhaṃ ahosi ath&#8217;āham bhikkhave sadevake loke samārake sabrahmake sassamaṇa-brāhmaṇiyā pajāya sadeva-manussāya anuttaraṃ sammāsambodhiṃ abhisambuddho paccaññāsiṃ</span></p><p class="p3"><span class="t5">Ñāṇañ&#8217;ca pana me dassanaṃ udapādi</span></p><p class="p3"><span class="t5">"Akuppā me vimutti ayam&#8217;antimā jāti n&#8217;atthi-dāni punabbhavo"ti</span></p><p class="p3"><span class="t5">Idam&#8217;avoca bhagavā attamanā pañca-vaggiyā bhikkhu bhagavato bhāsitaṃ abhinanduṃ</span></p><p class="p3"><span class="t5">Imasmiñ&#8217;ca pana veyyākaraṇasmiṃ bhaññamāne āyasmato koṇḍaññassa virajaṃ vītamalaṃ dhammacakkhuṃ udapādi:</span></p><p class="p3"><span class="t5">"Yaṃ kiñ&#8217;ci samudaya-dhammaṃ sabbaṃ taṃ nirodha-dhamman"ti</span></p><p class="p3"><span class="t5">Pavattite ca bhagavatā dhamma-cakke bhummā devā saddam&#8217;anussāvesuṃ:</span></p><p class="p3"><span class="t5">"Etaṃ bhagavatā bārāṇasiyaṃ isipatane migadāye anuttaraṃ dhamma-cakkaṃ pavattitaṃ appaṭivattiyaṃ samaṇena vā brāhmaṇena vā devena vā mārena vā brahmunā vā kenaci vā lokasmin"ti</span></p><p class="p3"><span class="t5">Bhummānaṃ devānaṃ saddaṃ sutvā cātu-mmahā-rājikā devā saddam&#8217;anussāvesuṃ...</span></p><p class="p3"><span class="t5">Cātu-mmahā-rājikānaṃ devānaṃ saddaṃ sutvā tāvatiṃsā devā saddam&#8217;anussāvesuṃ...</span></p><p class="p3"><span class="t5">Tāvatiṃsānaṃ devānaṃ saddaṃ sutvā yāmā devā saddam&#8217;anussāvesuṃ...</span></p><p class="p3"><span class="t5">Yāmānaṃ devānaṃ saddaṃ sutvā tusitā devā saddam&#8217;anussāvesuṃ...</span></p><p class="p3"><span class="t5">Tusitānaṃ devānaṃ saddaṃ sutvā nimmānaratī devā saddam&#8217;anussāvesuṃ...</span></p><p class="p3"><span class="t5">Nimmāna-ratīnaṃ devānaṃ saddaṃ sutvā paranimmitavasavattī devā saddam&#8217;anussāvesuṃ...</span></p><p class="p3"><span class="t5">Para-nimmita-vasa-vattīnaṃ devānaṃ saddaṃ sutvā brahmakāyikā devā saddam&#8217;anussāvesuṃ:</span></p><p class="p3"><span class="t5">"Etaṃ bhagavatā bārāṇasiyaṃ isipatane migadāye anuttaraṃ dhamma-cakkaṃ pavattitaṃ appaṭivattiyaṃ samaṇena vā brāhmaṇena vā devena vā mārena vā brahmunā vā kenaci vā lokasmin"ti</span></p><p class="p3"><span class="t5">Itiha tena khaṇena tena muhuttena yāva brahmalokā saddo abbhuggacchi. Ayañ&#8217;ca dasasahassī lokadhātu saṅkam&#8217;pi sampakam&#8217;pi sampavedhi appamāṇo ca oḷāro obhāso loke pāturahosi atikkamm&#8217;eva devānaṃ dev&#8217;ānubhāvaṃ</span></p><p class="p3"><span class="t5">Atha kho bhagavā udānaṃ udānesi:</span></p><p class="p3"><span class="t5">"Aññāsi vata bho koṇḍañño aññāsi vata bho koṇḍañño"ti Iti h&#8217;idaṃ āyasmato koṇḍaññassa aññākoṇḍañño tv&#8217;eva nāmaṃ ahosī&#8217;ti</span></p><h2><p class="p3"><span class="t5">[SN 56.11]</span></p></h2>', '-40-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Anatta-lakkhaṇa-sutta', 'title', 41);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 41, '<h2>Anatta-lakkhaṇa-sutta</h2><hr/><p class="p3"><span class="t5">Evaṃ me sutaṃ</span></p><p class="p3"><span class="t5">Ekaṃ samayaṃ bhagavā bārāṇasiyaṃ viharati isipatane migadāye Tatra kho bhagavā pañca-vaggiye bhikkhū āmantesi: <q>Bhikkhavo</q>ti <q>Bhadante</q>ti te bhikkhū bhagavato paccassosuṃ Bhagavā etad&#8217;avoca</span></p><p class="p3"><span class="t5">Rūpaṃ bhikkhave anattā rūpañ&#8217;ca h&#8217;idaṃ bhikkhave attā abhavissa na&#8217;y&#8217;idaṃ rūpaṃ ābādhāya saṃvatteyya labbhetha ca rūpe <q>Evaṃ me rūpaṃ hotu evaṃ me rūpaṃ mā ahosī</q>ti</span></p><p class="p3"><span class="t5">Yasmā ca kho bhikkhave rūpaṃ anattā tasmā rūpaṃ ābādhāya saṃvattati na ca labbhati rūpe <q>Evaṃ me rūpaṃ hotu evaṃ me rūpaṃ mā ahosī</q>ti</span></p><p class="p3"><span class="t5">Vedanā anattā vedanā ca h&#8217;idaṃ bhikkhave attā abhavissa na&#8217;y&#8217;idaṃ vedanā ābādhāya saṃvatteyya labbhetha ca vedanāya <q>Evaṃ me vedanā hotu evaṃ me vedanā mā ahosī</q>ti</span></p><p class="p3"><span class="t5">Yasmā ca kho bhikkhave vedanā anattā tasmā vedanā ābādhāya saṃvattati na ca labbhati vedanāya <q>Evaṃ me vedanā hotu evaṃ me vedanā mā ahosī</q>ti</span></p><p class="p3"><span class="t5">Saññā anattā saññā ca h&#8217;idaṃ bhikkhave attā abhavissa na&#8217;y&#8217;idaṃ saññā ābādhāya saṃvatteyya labbhetha ca saññāya <q>Evaṃ me saññā hotu evaṃ me saññā mā ahosī</q>ti</span></p><p class="p3"><span class="t5">Yasmā ca kho bhikkhave saññā anattā tasmā saññā ābādhāya saṃvattati na ca labbhati saññāya <q>Evaṃ me saññā hotu evaṃ me saññā mā ahosī</q>ti</span></p><p class="p3"><span class="t5">Saṅkhārā anattā saṅkhārā ca h&#8217;idaṃ bhikkhave attā abhavissaṃsu na&#8217;y&#8217;idaṃ saṅkhārā ābādhāya saṃvatteyyuṃ labbhetha ca saṅkhāresu <q>Evaṃ me saṅkhārā hontu evaṃ me saṅkhārā mā ahesun</q>ti</span></p><p class="p3"><span class="t5">Yasmā ca kho bhikkhave saṅkhārā anattā tasmā saṅkhārā ābādhāya saṃvattanti na ca labbhati saṅkhāresu <q>Evaṃ me saṅkhārā hontu evaṃ me saṅkhārā mā ahesun</q>ti</span></p><p class="p3"><span class="t5">Viññāṇaṃ anattā viññāṇañ&#8217;ca h&#8217;idaṃ bhikkhave attā abhavissa na&#8217;y&#8217;idaṃ viññāṇaṃ ābādhāya saṃvatteyya labbhetha ca viññāṇe <q>Evaṃ me viññāṇaṃ hotu evaṃ me viññāṇaṃ mā ahosī</q>ti</span></p><p class="p3"><span class="t5">Yasmā ca kho bhikkhave viññāṇaṃ anattā tasmā viññāṇaṃ ābādhāya saṃvattati na ca labbhati viññāṇe <q>Evaṃ me viññāṇaṃ hotu evaṃ me viññāṇaṃ mā ahosī</q>ti</span></p><p class="p3"><span class="t5">Taṃ kiṃ maññatha bhikkhave</span></p><p class="p3"><span class="t5">Rūpaṃ niccaṃ vā aniccaṃ vā&#8217;ti?</span></p><p class="p3"><span class="t5">Aniccaṃ bhante</span></p><p class="p3"><span class="t5">Yam&#8217;pan&#8217;āniccaṃ dukkhaṃ vā taṃ sukhaṃ vā&#8217;ti?</span></p><p class="p3"><span class="t5">Dukkhaṃ bhante</span></p><p class="p3"><span class="t5">Yam&#8217;pan&#8217;āniccaṃ dukkhaṃ vipariṇāma-dhammaṃ kallaṃ nu taṃ samanupassituṃ <q>Etaṃ mama eso&#8217;ham&#8217;asmi eso me attā</q>ti?</span></p><p class="p3"><span class="t5">No h&#8217;etaṃ bhante</span></p><p class="p3"><span class="t5">Taṃ kiṃ maññatha bhikkhave vedanā niccā vā aniccā vā&#8217;ti?</span></p><p class="p3"><span class="t5">Aniccā bhante</span></p><p class="p3"><span class="t5">Yam&#8217;pan&#8217;āniccaṃ dukkhaṃ vā taṃ sukhaṃ vā&#8217;ti?</span></p><p class="p3"><span class="t5">Dukkhaṃ bhante</span></p><p class="p3"><span class="t5">Yam&#8217;pan&#8217;āniccaṃ dukkhaṃ vipariṇāma-dhammaṃ kallaṃ nu taṃ samanupassituṃ <q>Etaṃ mama eso&#8217;ham&#8217;asmi eso me attā</q>ti?</span></p><p class="p3"><span class="t5">No h&#8217;etaṃ bhante</span></p><p class="p3"><span class="t5">Taṃ kiṃ maññatha bhikkhave saññā niccā vā aniccā vā&#8217;ti?</span></p><p class="p3"><span class="t5">Aniccā bhante</span></p><p class="p3"><span class="t5">Yam&#8217;pan&#8217;āniccaṃ dukkhaṃ vā taṃ sukhaṃ vā&#8217;ti?</span></p><p class="p3"><span class="t5">Dukkhaṃ bhante</span></p><p class="p3"><span class="t5">Yam&#8217;pan&#8217;āniccaṃ dukkhaṃ vipariṇāma-dhammaṃ kallaṃ nu taṃ samanupassituṃ <q>Etaṃ mama eso&#8217;ham&#8217;asmi eso me attā</q>ti?</span></p><p class="p3"><span class="t5">No h&#8217;etaṃ bhante</span></p><p class="p3"><span class="t5">Taṃ kiṃ maññatha bhikkhave saṅkhārā niccā vā aniccā vā&#8217;ti?</span></p><p class="p3"><span class="t5">Aniccā bhante</span></p><p class="p3"><span class="t5">Yam&#8217;pan&#8217;āniccaṃ dukkhaṃ vā taṃ sukhaṃ vā&#8217;ti?</span></p><p class="p3"><span class="t5">Dukkhaṃ bhante</span></p><p class="p3"><span class="t5">Yam&#8217;pan&#8217;āniccaṃ dukkhaṃ vipariṇāma-dhammaṃ kallaṃ nu taṃ samanupassituṃ <q>Etaṃ mama eso&#8217;ham&#8217;asmi eso me attā</q>ti?</span></p><p class="p3"><span class="t5">No h&#8217;etaṃ bhante</span></p><p class="p3"><span class="t5">Taṃ kiṃ maññatha bhikkhave viññāṇaṃ niccaṃ vā aniccaṃ vā&#8217;ti?</span></p><p class="p3"><span class="t5">Aniccaṃ bhante</span></p><p class="p3"><span class="t5">Yam&#8217;pan&#8217;āniccaṃ dukkhaṃ vā taṃ sukhaṃ vā&#8217;ti?</span></p><p class="p3"><span class="t5">Dukkhaṃ bhante</span></p><p class="p3"><span class="t5">Yam&#8217;pan&#8217;āniccaṃ dukkhaṃ vipariṇāma-dhammaṃ kallaṃ nu taṃ samanupassituṃ <q>Etaṃ mama eso&#8217;ham&#8217;asmi eso me attā</q>ti?</span></p><p class="p3"><span class="t5">No h&#8217;etaṃ bhante</span></p><p class="p3"><span class="t5">Tasmā&#8217;t&#8217;iha bhikkhave</span></p><p class="p3"><span class="t5">Yaṅ&#8217;kiñci rūpaṃ atīt&#8217;ānāgata-paccuppannaṃ ajjhattaṃ vā bahiddhā vā oḷārikaṃ vā sukhumaṃ vā hīnaṃ vā paṇītaṃ vā yaṃ dūre santike vā sabbaṃ rūpaṃ: <q>N&#8217;etaṃ mama n&#8217;eso&#8217;ham&#8217;asmi na m&#8217;eso attā</q>ti evam&#8217;etaṃ yathā-bhūtaṃ sammappaññāya daṭṭhabbaṃ</span></p><p class="p3"><span class="t5">Yā kāci vedanā atīt&#8217;ānāgata-paccuppannā ajjhattā vā bahiddhā vā oḷārikā vā sukhumā vā hīnā vā paṇītā vā yā dūre santike vā sabbā vedanā: <q>N&#8217;etaṃ mama n&#8217;eso&#8217;ham&#8217;asmi na m&#8217;eso attā</q>ti evam&#8217;etaṃ yathā-bhūtaṃ sammappaññāya daṭṭhabbaṃ</span></p><p class="p3"><span class="t5">Yā kāci saññā atīt&#8217;ānāgata-paccuppannā ajjhattā vā bahiddhā vā oḷārikā vā sukhumā vā hīnā vā paṇītā vā yā dūre santike vā sabbā saññā: <q>N&#8217;etaṃ mama n&#8217;eso&#8217;ham&#8217;asmi na m&#8217;eso attā</q>ti evam&#8217;etaṃ yathā-bhūtaṃ sammappaññāya daṭṭhabbaṃ</span></p><p class="p3"><span class="t5">Ye keci saṅkhārā atīt&#8217;ānāgata-paccuppannā ajjhattā vā bahiddhā vā oḷārikā vā sukhumā vā hīnā vā paṇītā vā ye dūre santike vā sabbe saṅkhārā: <q>N&#8217;etaṃ mama n&#8217;eso&#8217;ham&#8217;asmi na m&#8217;eso attā</q>ti evam&#8217;etaṃ yathā-bhūtaṃ sammappaññāya daṭṭhabbaṃ</span></p><p class="p3"><span class="t5">Yaṅ&#8217;kiñci viññāṇaṃ atīt&#8217;ānāgata-paccuppannaṃ ajjhattaṃ vā bahiddhā vā oḷārikaṃ vā sukhumaṃ vā hīnaṃ vā paṇītaṃ vā yaṃ dūre santike vā sabbaṃ viññāṇaṃ: <q>N&#8217;etaṃ mama n&#8217;eso&#8217;ham&#8217;asmi na m&#8217;eso attā</q>ti evam&#8217;etaṃ yathā-bhūtaṃ sammappaññāya daṭṭhabbaṃ</span></p><p class="p3"><span class="t5">Evaṃ passaṃ bhikkhave</span></p><p class="p3"><span class="t5">Sutvā ariyasāvako rūpasmim&#8217;pi nibbindati vedanāya&#8217;pi nibbindati saññāya&#8217;pi nibbindati saṅkhāresu&#8217;pi nibbindati viññāṇasmim&#8217;pi nibbindati nibbindaṃ virajjati virāgā vimuccati vimuttasmiṃ &#8216;vimuttam&#8217;iti ñāṇaṃ hoti <q>Khīṇā jāti vusitaṃ brahmacariyaṃ kataṃ karaṇīyaṃ n&#8217;āparaṃ itthattāyā</q>ti pajānātī&#8217;ti</span></p><p class="p3"><span class="t5">Idam&#8217;avoca bhagavā Attamanā pañca-vaggiyā bhikkhū bhagavato bhāsitaṃ abhinanduṃ Imasmiñ&#8217;ca pana veyyākaraṇasmiṃ bhaññamāne pañca-vaggiyānaṃ bhikkhūnaṃ anupādāya āsavehi cittāni vimucciṃsū&#8217;ti</span></p><h2><p class="p3"><span class="t5">[SN 22.59]</span></p></h2>', '-41-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Āditta-pariyāya-sutta', 'title', 42);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 42, '<h2>Āditta-pariyāya-sutta</h2><hr/><p class="p3"><span class="t5">Evaṃ me sutaṃ</span></p><p class="p3"><span class="t5">Ekaṃ samayaṃ bhagavā gayāyaṃ viharati gayāsīse saddhiṃ bhikkhu-sahassena Tatra kho bhagavā bhikkhū āmantesi:</span></p><p class="p3"><span class="t5">Sabbaṃ bhikkhave ādittaṃ!</span></p><p class="p3"><span class="t5">Kiñ&#8217;ca bhikkhave sabbaṃ ādittaṃ?</span></p><p class="p3"><span class="t5">Cakkhuṃ bhikkhave ādittaṃ rūpā ādittā cakkhu-viññāṇaṃ ādittaṃ cakkhu-samphasso āditto yam&#8217;p&#8217;idaṃ cakkhu-samphassa-paccayā uppajjati vedayitaṃ sukhaṃ vā dukkhaṃ vā adukkham&#8217;asukhaṃ vā tam&#8217;pi ādittaṃ</span></p><p class="p3"><span class="t5">Kena ādittaṃ?</span></p><p class="p3"><span class="t5">Ādittaṃ rāg&#8217;agginā dos&#8217;agginā moh&#8217;agginā ādittaṃ jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi ādittan&#8217;ti vadāmi</span></p><p class="p3"><span class="t5">Sotaṃ ādittaṃ saddā ādittā sota-viññāṇaṃ ādittaṃ sota-samphasso āditto yam&#8217;p&#8217;idaṃ sota-samphassa-paccayā uppajjati vedayitaṃ sukhaṃ vā dukkhaṃ vā adukkham&#8217;asukhaṃ vā tam&#8217;pi ādittaṃ</span></p><p class="p3"><span class="t5">Kena ādittaṃ?</span></p><p class="p3"><span class="t5">Ādittaṃ rāg&#8217;agginā dos&#8217;agginā moh&#8217;agginā ādittaṃ jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi ādittan&#8217;ti vadāmi</span></p><p class="p3"><span class="t5">Ghānaṃ ādittaṃ gandhā ādittā ghāna-viññāṇaṃ ādittaṃ ghāna-samphasso āditto yam&#8217;p&#8217;idaṃ ghāna-samphassa-paccayā uppajjati vedayitaṃ sukhaṃ vā dukkhaṃ vā adukkham&#8217;asukhaṃ vā tam&#8217;pi ādittaṃ</span></p><p class="p3"><span class="t5">Kena ādittaṃ?</span></p><p class="p3"><span class="t5">Ādittaṃ rāg&#8217;agginā dos&#8217;agginā moh&#8217;agginā ādittaṃ jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi ādittan&#8217;ti vadāmi</span></p><p class="p3"><span class="t5">Jivhā ādittā rasā ādittā jivhā-viññāṇaṃ ādittaṃ jivhā-samphasso āditto yam&#8217;p&#8217;idaṃ jivhā-samphassa-paccayā uppajjati vedayitaṃ sukhaṃ vā dukkhaṃ vā adukkham&#8217;asukhaṃ vā tam&#8217;pi ādittaṃ</span></p><p class="p3"><span class="t5">Kena ādittaṃ?</span></p><p class="p3"><span class="t5">Ādittaṃ rāg&#8217;agginā dos&#8217;agginā moh&#8217;agginā ādittaṃ jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi ādittan&#8217;ti vadāmi</span></p><p class="p3"><span class="t5">Kāyo āditto phoṭṭhabbā ādittā kāya-viññāṇaṃ ādittaṃ kāya-samphasso āditto yam&#8217;p&#8217;idaṃ kāya-samphassa-paccayā uppajjati vedayitaṃ sukhaṃ vā dukkhaṃ vā adukkham&#8217;asukhaṃ vā tam&#8217;pi ādittaṃ</span></p><p class="p3"><span class="t5">Kena ādittaṃ?</span></p><p class="p3"><span class="t5">Ādittaṃ rāg&#8217;agginā dos&#8217;agginā moh&#8217;agginā ādittaṃ jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi ādittan&#8217;ti vadāmi</span></p><p class="p3"><span class="t5">Mano āditto dhammā ādittā mano-viññāṇaṃ ādittaṃ mano-samphasso āditto yam&#8217;p&#8217;idaṃ mano-samphassa-paccayā uppajjati vedayitaṃ sukhaṃ vā dukkhaṃ vā adukkham&#8217;asukhaṃ vā tam&#8217;pi ādittaṃ</span></p><p class="p3"><span class="t5">Kena ādittaṃ?</span></p><p class="p3"><span class="t5">Ādittaṃ rāg&#8217;agginā dos&#8217;agginā moh&#8217;agginā ādittaṃ jātiyā jarāya maraṇena sokehi paridevehi dukkhehi domanassehi upāyāsehi ādittan&#8217;ti vadāmi</span></p><p class="p3"><span class="t5">Evaṃ passaṃ bhikkhave</span></p><p class="p3"><span class="t5">Sutvā ariyasāvako cakkhusmim&#8217;pi nibbindati rūpesu&#8217;pi nibbindati cakkhu-viññāṇe&#8217;pi nibbindati cakkhu-samphasse&#8217;pi nibbindati yam&#8217;p&#8217;idaṃ cakkhu-samphassa-paccayā uppajjati vedayitaṃ sukhaṃ vā dukkhaṃ vā adukkham&#8217;asukhaṃ vā tasmim&#8217;pi nibbindati</span></p><p class="p3"><span class="t5">Sotasmim&#8217;pi nibbindati saddesu&#8217;pi nibbindati sota-viññāṇe&#8217;pi nibbindati sota-samphasse&#8217;pi nibbindati yam&#8217;p&#8217;idaṃ sota-samphassa-paccayā uppajjati vedayitaṃ sukhaṃ vā dukkhaṃ vā adukkham&#8217;asukhaṃ vā tasmim&#8217;pi nibbindati</span></p><p class="p3"><span class="t5">Ghānasmim&#8217;pi nibbindati gandhesu&#8217;pi nibbindati ghāna-viññāṇe&#8217;pi nibbindati ghāna-samphasse&#8217;pi nibbindati yam&#8217;p&#8217;idaṃ ghāna-samphassa-paccayā uppajjati vedayitaṃ sukhaṃ vā dukkhaṃ vā adukkham&#8217;asukhaṃ vā tasmim&#8217;pi nibbindati</span></p><p class="p3"><span class="t5">Jivhāya&#8217;pi nibbindati rasesu&#8217;pi nibbindati jivhā-viññāṇe&#8217;pi nibbindati jivhā-samphasse&#8217;pi nibbindati yam&#8217;p&#8217;idaṃ jivhā-samphassa-paccayā uppajjati vedayitaṃ sukhaṃ vā dukkhaṃ vā adukkham&#8217;asukhaṃ vā tasmim&#8217;pi nibbindati</span></p><p class="p3"><span class="t5">Kāyasmim&#8217;pi nibbindati phoṭṭhabbesu&#8217;pi nibbindati kāya-viññāṇe&#8217;pi nibbindati kāya-samphasse&#8217;pi nibbindati yam&#8217;p&#8217;idaṃ kāya-samphassa-paccayā uppajjati vedayitaṃ sukhaṃ vā dukkhaṃ vā adukkham&#8217;asukhaṃ vā tasmim&#8217;pi nibbindati</span></p><p class="p3"><span class="t5">Manasmim&#8217;pi nibbindati dhammesu&#8217;pi nibbindati mano-viññāṇe&#8217;pi nibbindati mano-samphasse&#8217;pi nibbindati yam&#8217;p&#8217;idaṃ mano-samphassa-paccayā uppajjati vedayitaṃ sukhaṃ vā dukkhaṃ vā adukkham&#8217;asukhaṃ vā tasmim&#8217;pi nibbindati</span></p><p class="p3"><span class="t5">Nibbindaṃ virajjati virāgā vimuccati vimuttasmiṃ &#8216;vimuttam&#8217;iti ñāṇaṃ hoti:</span></p><p class="p3"><span class="t5">&#8216;Khīṇā jāti vusitaṃ brahmacariyaṃ kataṃ karaṇīyaṃ n&#8217;āparaṃ itthattāyā&#8217;ti pajānātī&#8217;ti</span></p><p class="p3"><span class="t5">Idam&#8217;avoca bhagavā Attamanā te bhikkhū bhagavato bhāsitaṃ abhinanduṃ Imasmiñ&#8217;ca pana veyyākaraṇasmiṃ bhaññamāne tassa bhikkhu-sahassassa anupādāya āsavehi cittāni vimucciṃsū&#8217;ti</span></p><h2><p class="p3"><span class="t5">[SN 35.28]</span></p></h2>', '-42-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Thanksgiving Recitations', 'chapter', 43);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Yathā vāri-vahā pūrā', 'title', 43);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 43, '<h2>Yathā vāri-vahā pūrā</h2><p class="p3"><span class="t5">Yathā vāri-vahā pūrā paripūrenti sāgaraṃ evam&#8217;eva ito dinnaṃ petānaṃ upakappati icchitaṃ patthitaṃ tumhaṃ khippam&#8217;eva samijjhatu sabbe pūrentu saṅkappā cando paṇṇa-raso yathā maṇi joti-raso yathā</span></p><h2><p class="p3"><span class="t5">[Khp 1.7]</span></p></h2><p class="p3"><span class="t5">Sabb&#8217;ītiyo vivajjantu</span></p><p class="p3"><span class="t5">Sabba-rogo vinassatu</span></p><p class="p3"><span class="t5">Mā te bhavatv&#8217;antarāyo</span></p><p class="p3"><span class="t5">Sukhī dīgh&#8217;āyuko bhava</span></p><h2><p class="p3"><span class="t5">[Dhp A]</span></p></h2><p class="p3"><span class="t5">Abhivādana-sīlissa</span></p><p class="p3"><span class="t5">Niccaṃ vuḍḍh&#8217;āpacāyino</span></p><p class="p3"><span class="t5">Cattāro dhammā vaḍḍhanti</span></p><p class="p3"><span class="t5">Āyu vaṇṇo sukhaṃ balaṃ</span></p><h2><p class="p3"><span class="t5">[Dhp 109]</span></p></h2><h2>Ratanattay&#8217;ānubhāv&#8217;ādi-gāthā</h2><p class="p3"><span class="t5">Ratanattay&#8217;ānubhāvena</span></p><p class="p3"><span class="t5">Ratanattaya-tejasā</span></p><p class="p3"><span class="t5">Dukkha-roga-bhayā verā – Sokā sattu c&#8217;upaddavā</span></p><p class="p3"><span class="t5">Anekā antarāyā&#8217;pi – Vinassantu asesato</span></p><p class="p3"><span class="t5">Jaya-siddhi dhanaṃ lābhaṃ – Sotthi bhāgyaṃ sukhaṃ balaṃ</span></p><p class="p3"><span class="t5">Siri āyu ca vaṇṇo ca – Bhogaṃ vuḍḍhī ca yasavā</span></p><p class="p3"><span class="t5">Sata-vassā ca āyū ca – Jīva-siddhī bhavantu te</span></p><p class="p3"><span class="t1">Through the power &amp; through the radiant energy of the (Triple) Gem,</span></p><p class="p3"><span class="t1">May suffering, disease, fear, animosity,</span></p><p class="p3"><span class="t1">Sorrow, adversity, misfortune</span></p><p class="p3"><span class="t1">Obstacles without number vanish without a trace.</span></p><p class="p3"><span class="t1">Triumph, success, wealth, &amp; gain,</span></p><p class="p3"><span class="t1">Safety, luck, happiness, strength,</span></p><p class="p3"><span class="t1">Glory, long life, &amp; beauty, fortune, increase, &amp; status,</span></p><p class="p3"><span class="t1">A lifespan of 100 years, and success in your livelihood:</span></p><p class="p3"><span class="t1">May they be yours.</span></p><h2><p class="p3"><span class="t5">[Thai]</span></p></h2><h2>Bhojana-dān&#8217;ānumodanā</h2><p class="p3"><span class="t5">Yo yassa bhojanaṃ deti</span></p><p class="p3"><span class="t5">So tassa deti pañca&#8217;pi</span></p><p class="p3"><span class="t5">Āyuṃ balaṃ sukhaṃ vaṇṇaṃ</span></p><p class="p3"><span class="t5">Paṭibhānañ&#8217;ca pañcamaṃ</span></p><p class="p3"><span class="t5">Āyu-do bala-do dhīro</span></p><p class="p3"><span class="t5">Vaṇṇa-do paṭibhāna-do</span></p><p class="p3"><span class="t5">Sukhassa dātā medhāvī</span></p><p class="p3"><span class="t5">Sukhaṃ so adhigacchati</span></p><p class="p3"><span class="t5">Āyuṃ datvā balaṃ vaṇṇaṃ</span></p><p class="p3"><span class="t5">Sukhañ&#8217;ca paṭibhānakaṃ</span></p><p class="p3"><span class="t5">Dīgh&#8217;āyu yasavā hoti</span></p><p class="p3"><span class="t5">Yattha yatth&#8217;ūpapajjati</span></p><p class="p3"><span class="t5">Abhivādana-sīlissa</span></p><p class="p3"><span class="t5">Niccaṃ vuḍḍh&#8217;āpacāyino</span></p><p class="p3"><span class="t5">Cattāro dhammā vaḍḍhanti</span></p><p class="p3"><span class="t5">Āyu vaṇṇo sukhaṃ balaṃ</span></p><p class="p3"><span class="t5">Padakkhiṇaṃ kāya-kammaṃ</span></p><p class="p3"><span class="t5">Vācākammaṃ padakkhiṇaṃ</span></p><p class="p3"><span class="t5">Padakkhiṇaṃ mano-kammaṃ</span></p><p class="p3"><span class="t5">Paṇīdhi te padakkhiṇe</span></p><p class="p3"><span class="t5">Padakkhiṇāni katvāna</span></p><p class="p3"><span class="t5">Labhant&#8217;atthe padakkhiṇe</span></p><p class="p3"><span class="t5">Te atthaladdhā sukhitā</span></p><p class="p3"><span class="t5">Virūḷhā buddhasāsane</span></p><p class="p3"><span class="t5">Arogā sukhitā hotha</span></p><p class="p3"><span class="t5">Saha sabbehi ñātibhi</span></p><p class="p3"><span class="t1">One who gives food to another</span></p><p class="p3"><span class="t1">Gives to the other five things too</span></p><p class="p3"><span class="t1">Long-life, strength, happiness, beauty</span></p><p class="p3"><span class="t1">And intelligence as the fifth.</span></p><p class="p3"><span class="t1">The wise life-giver, strength-giver</span></p><p class="p3"><span class="t1">Beauty-giver, wit-giver</span></p><p class="p3"><span class="t1">Wise giver of happiness</span></p><p class="p3"><span class="t1">Attains happiness.</span></p><p class="p3"><span class="t1">Having given life, strength and beauty</span></p><p class="p3"><span class="t1">Happiness and wit</span></p><p class="p3"><span class="t1">One is long-lived and glorious</span></p><p class="p3"><span class="t1">Wherever one is reborn.</span></p><p class="p3"><span class="t1">For one who often pays homage</span></p><p class="p3"><span class="t1">And always respects elders</span></p><p class="p3"><span class="t1">Four things increase:</span></p><p class="p3"><span class="t1">Long-life, beauty</span></p><p class="p3"><span class="t1">Happiness and strength.</span></p><p class="p3"><span class="t1">Felicitous is bodily kamma</span></p><p class="p3"><span class="t1">Verbal kamma is felicitous</span></p><p class="p3"><span class="t1">Felicitous is mental kamma</span></p><p class="p3"><span class="t1">When aspiring for felicity.</span></p><p class="p3"><span class="t1">Having done the felicitous</span></p><p class="p3"><span class="t1">They get felicitous rewards</span></p><p class="p3"><span class="t1">They are happy who get such rewards</span></p><p class="p3"><span class="t1">And grow in the Buddhasāsana.</span></p><p class="p3"><span class="t1">May you all be healthy and happy</span></p><p class="p3"><span class="t1">Together with all your relatives</span></p><h2><p class="p3"><span class="t5">[AN 5.37 / Dhp 109 / AN 3.155]</span></p></h2><h2>Culla-maṅgala-cakka-vāḷa</h2><p class="p3"><span class="t5">Sabba-buddh&#8217;ānubhāvena</span></p><p class="p3"><span class="t5">Sabba-dhamm&#8217;ānubhāvena</span></p><p class="p3"><span class="t5">Sabba-saṅgh&#8217;ānubhāvena</span></p><p class="p3"><span class="t5">Buddha-ratanaṃ dhamma-ratanaṃ saṅgha-ratanaṃ</span></p><p class="p3"><span class="t5">Tiṇṇaṃ ratanānaṃ ānubhāvena</span></p><p class="p3"><span class="t5">Catur-āsīti-sahassa-dhamma-kkhandh&#8217;ānubhāvena</span></p><p class="p3"><span class="t5">Piṭakattay&#8217;ānubhāvena</span></p><p class="p3"><span class="t5">Jina-sāvak&#8217;ānubhāvena</span></p><p class="p3"><span class="t5">Sabbe te rogā</span></p><p class="p3"><span class="t5">Sabbe te bhayā</span></p><p class="p3"><span class="t5">Sabbe te antarāyā</span></p><p class="p3"><span class="t5">Sabbe te upaddavā</span></p><p class="p3"><span class="t5">Sabbe te dunnimittā</span></p><p class="p3"><span class="t5">Sabbe te avamaṅgalā vinassantu</span></p><p class="p3"><span class="t5">Āyu-vaḍḍhako</span></p><p class="p3"><span class="t5">Dhana-vaḍḍhako</span></p><p class="p3"><span class="t5">Siri-vaḍḍhako</span></p><p class="p3"><span class="t5">Yasa-vaḍḍhako</span></p><p class="p3"><span class="t5">Bala-vaḍḍhako</span></p><p class="p3"><span class="t5">Vaṇṇa-vaḍḍhako</span></p><p class="p3"><span class="t5">Sukha-vaḍḍhako</span></p><p class="p3"><span class="t5">Hotu sabbadā</span></p><p class="p3"><span class="t5">Dukkha-roga-bhayā-verā</span></p><p class="p3"><span class="t5">Sokā sattu c&#8217;upaddavā</span></p><p class="p3"><span class="t5">Anekā antarāyā&#8217;pi</span></p><p class="p3"><span class="t5">Vinassantu ca tejasā</span></p><p class="p3"><span class="t5">Jaya-siddhi dhanaṃ lābhaṃ</span></p><p class="p3"><span class="t5">Sotthi bhāgyaṃ sukhaṃ balaṃ</span></p><p class="p3"><span class="t5">Siri āyu ca vaṇṇo ca</span></p><p class="p3"><span class="t5">Bhogaṃ vuḍḍhī ca yasavā</span></p><p class="p3"><span class="t5">Sata-vassā ca āyū ca</span></p><p class="p3"><span class="t5">Jīva-siddhī bhavantu te</span></p><p class="p3"><span class="t1">Through the power of all the Buddhas, the power of all the Dhamma, the power of all the Saṅgha, the gem of the Buddha, the gem of the Dhamma, the gem of the Saṅgha, the power of the Triple Gem: the power of the 84,000 Dhamma aggregates, the power of the Tripitaka, the power of the Victor&#8217;s disciples: May all your diseases, all your fears, all your obstacles, all your dangers, all your bad visions, all your bad omens be destroyed. May there always be an increase of long life, wealth, glory, status, strength, beauty and happiness.</span></p><h2><p class="p3"><span class="t5">[MJG]</span></p></h2><h2>Aggappasāda-sutta-gāthā</h2><p class="p3"><span class="t5">Aggato ve pasannānaṃ</span></p><p class="p3"><span class="t5">Aggaṃ dhammaṃ vijānataṃ</span></p><p class="p3"><span class="t5">Agge buddhe pasannānaṃ</span></p><p class="p3"><span class="t5">Dakkhiṇeyye anuttare</span></p><p class="p3"><span class="t1">For one with confidence,</span></p><p class="p3"><span class="t1">Realizing the supreme Dhamma to be supreme,</span></p><p class="p3"><span class="t1">With confidence in the supreme Buddha,</span></p><p class="p3"><span class="t1">Unsurpassed in deserving offerings,</span></p><p class="p3"><span class="t5">Agge dhamme pasannānaṃ</span></p><p class="p3"><span class="t5">Virāg&#8217;ūpasame sukhe</span></p><p class="p3"><span class="t5">Agge saṅghe pasannānaṃ</span></p><p class="p3"><span class="t5">Puññakkhette anuttare</span></p><p class="p3"><span class="t1">With confidence in the supreme Dhamma,</span></p><p class="p3"><span class="t1">The happiness of dispassion &amp; calm,</span></p><p class="p3"><span class="t1">With confidence in the supreme Saṅgha,</span></p><p class="p3"><span class="t1">Unsurpassed as a field of merit,</span></p><p class="p3"><span class="t5">Aggasmiṃ dānaṃ dadataṃ</span></p><p class="p3"><span class="t5">Aggaṃ puññaṃ pavaḍḍhati</span></p><p class="p3"><span class="t5">Aggaṃ āyu ca vaṇṇo ca</span></p><p class="p3"><span class="t5">Yaso kitti sukhaṃ balaṃ</span></p><p class="p3"><span class="t1">Having given gifts to the supreme,</span></p><p class="p3"><span class="t1">One develops supreme merit,</span></p><p class="p3"><span class="t1">Supreme long life &amp; beauty,</span></p><p class="p3"><span class="t1">Status, honour, happiness, strength.</span></p><p class="p3"><span class="t5">Aggassa dātā medhāvī</span></p><p class="p3"><span class="t5">Agga-dhamma-samāhito</span></p><p class="p3"><span class="t5">Deva-bhūto manusso vā</span></p><p class="p3"><span class="t5">Aggappatto pamodatī&#8217;ti</span></p><p class="p3"><span class="t1">Having given to the supreme, the intelligent person,</span></p><p class="p3"><span class="t1">Firm in the supreme Dhamma,</span></p><p class="p3"><span class="t1">Whether becoming a deva or a human being,</span></p><p class="p3"><span class="t1">Rejoices, having attained the supreme.</span></p><h2><p class="p3"><span class="t5">[AN 5.32]</span></p></h2><h2>Kāla-dāna-sutta-gāthā</h2><p class="p3"><span class="t5">Kāle dadanti sapaññā</span></p><p class="p3"><span class="t5">Vadaññū vīta-maccharā</span></p><p class="p3"><span class="t5">Kālena dinnaṃ ariyesu</span></p><p class="p3"><span class="t5">Uju-bhūtesu tādisu</span></p><p class="p3"><span class="t5">Vippasanna-manā tassa</span></p><p class="p3"><span class="t5">Vipulā hoti dakkhiṇā</span></p><p class="p3"><span class="t1">Those with discernment, responsive, free from stinginess, give in the proper season.</span></p><p class="p3"><span class="t1">Having given in the proper season</span></p><p class="p3"><span class="t1">With hearts inspired by the Noble Ones,</span></p><p class="p3"><span class="t1">Straightened,</span></p><p class="p3"><span class="t1">Their offering bears an abundance.</span></p><p class="p3"><span class="t5">Ye tattha anumodanti</span></p><p class="p3"><span class="t5">Veyyāvaccaṃ karonti vā</span></p><p class="p3"><span class="t5">Na tena dakkhiṇā onā</span></p><p class="p3"><span class="t5">Te&#8217;pi puññassa bhāgino</span></p><p class="p3"><span class="t1">Those who rejoice in that gift,</span></p><p class="p3"><span class="t1">Or give assistance,</span></p><p class="p3"><span class="t1">They too have a share of the merit,</span></p><p class="p3"><span class="t1">And the offering is not depleted by that.</span></p><p class="p3"><span class="t5">Tasmā dade appaṭivāna-citto</span></p><p class="p3"><span class="t5">Yattha dinnaṃ maha-pphalaṃ</span></p><p class="p3"><span class="t5">Puññāni para-lokasmiṃ</span></p><p class="p3"><span class="t5">Patiṭṭhā honti pāṇinan&#8217;ti</span></p><p class="p3"><span class="t1">Therefore, with an unhesitant mind, one should give</span></p><p class="p3"><span class="t1">Where the gift bears great fruit.</span></p><p class="p3"><span class="t1">Merit is what establishes</span></p><p class="p3"><span class="t1">Living beings in the next life.</span></p><h2><p class="p3"><span class="t5">[AN 5.36]</span></p></h2><h2>So attha-laddho</h2><p class="p3"><span class="t5">So attha-laddho sukhito</span></p><p class="p3"><span class="t5">Viruḷho buddha-sāsane</span></p><p class="p3"><span class="t5">Arogo sukhito hohi</span></p><p class="p3"><span class="t5">Saha sabbehi ñātibhi</span></p><p class="p3"><span class="t1">May he gain benefits and happiness</span></p><p class="p3"><span class="t1">And grow in Buddha&#8217;s religion,</span></p><p class="p3"><span class="t1">Without disease and happy</span></p><p class="p3"><span class="t1">May he be together with all his relatives.</span></p><p class="p3"><span class="t5">Sā attha-laddhā sukhitā</span></p><p class="p3"><span class="t5">Viruḷhā buddha-sāsane</span></p><p class="p3"><span class="t5">Arogā sukhitā hohi</span></p><p class="p3"><span class="t5">Saha sabbehi ñātibhi</span></p><p class="p3"><span class="t1">May she gain benefits and happiness</span></p><p class="p3"><span class="t1">And grow in Buddha&#8217;s religion,</span></p><p class="p3"><span class="t1">Without disease and happy</span></p><p class="p3"><span class="t1">May she be together with all her relatives.</span></p><p class="p3"><span class="t5">Te attha-laddhā sukhitā</span></p><p class="p3"><span class="t5">Viruḷhā buddha-sāsane</span></p><p class="p3"><span class="t5">Arogā sukhitā hotha</span></p><p class="p3"><span class="t5">Saha sabbehi ñātibhi</span></p><p class="p3"><span class="t1">May they gain benefits and happiness</span></p><p class="p3"><span class="t1">And grow in Buddha&#8217;s religion,</span></p><p class="p3"><span class="t1">Without disease and happy</span></p><p class="p3"><span class="t1">May they be together with all their relatives.</span></p><h2><p class="p3"><span class="t5">[AN 3.155]</span></p></h2>', '-43-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Protective Recitations', 'chapter', 44);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Devā-ārādhanā', 'title', 44);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 44, '<h2>Devā-ārādhanā</h2><p class="p3"><span class="t5">Pharitvāna mettaṃ samettā bhadantā<br/> Avikkhitta-cittā parittaṃ bhaṇantu<br/> Sagge kāme ca rūpe giri-sikhara-taṭe c&#8217;antalikkhe vimāne<br/> Dīpe raṭṭhe ca gāme taru-vana-gahane geha-vatthumhi khette<br/> Bhummā c&#8217;āyantu devā jala-thala-visame yakkha-gandhabba-nāgā<br/> Tiṭṭhantā santike yaṃ muni-vara-vacanaṃ sādhavo me suṇantu<br/> Buddha-dassana-kālo ayam&#8217;bhadantā<br/> Dhamma-ssavana-kālo ayam&#8217;bhadantā<br/> Saṅgha-payirupāsana-kālo ayam&#8217;bhadantā</span></p><br/><p class="p3"><span class="t1">Kind, venerable sirs: having spread thoughts of good will,</span></p><p class="p3"><span class="t1">Recite the protective chant with undistracted mind.</span></p><p class="p3"><span class="t1">Those in the heavens of sensuality &amp; form,</span></p><p class="p3"><span class="t1">On peaks &amp; mountain precipices, in palaces floating in the sky,</span></p><p class="p3"><span class="t1">In islands, countries, &amp; towns,</span></p><p class="p3"><span class="t1">In groves of trees &amp; thickets,</span></p><p class="p3"><span class="t1">Around homesites &amp; fields.</span></p><p class="p3"><span class="t1">And the earth-devas, spirits, heavenly minstrels, &amp; nāgas who are nearby,</span></p><p class="p3"><span class="t1">In different waters and lands:</span></p><p class="p3"><span class="t1">May they come &amp; listen with approval as I recite the word of the excellent sage.</span></p><p class="p3"><span class="t1">This is the time to see the Buddha, venerable sirs. This is the time to listen to the Dhamma, venerable sirs.</span></p><p class="p3"><span class="t1">This is the time to attend to the Saṅgha, venerable sirs.</span></p><h2><p class="p3"><span class="t5">[Thai]</span></p></h2><h2>Pubba-bhāga-nama-kāra-pāṭho</h2><p class="p3"><span class="t5">Namo tassa bhagavato arahato sammāsambuddhassa</span></p><p class="p3"><span class="t1">Homage to the Blessed, Worthy, and Perfectly Enlightened One</span></p><h2><p class="p3"><span class="t5">[DN 21]</span></p></h2><h2>Saraṇa-gamana-pāṭho</h2><p class="p3"><span class="t5">Buddhaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t5">Dhammaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t5">Saṅghaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t1">To the Buddha I go for refuge.</span></p><p class="p3"><span class="t1">To the Dhamma I go for refuge.</span></p><p class="p3"><span class="t1">To the Saṅgha I go for refuge.</span></p><p class="p3"><span class="t5">Dutiyam&#8217;pi buddhaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t5">Dutiyam&#8217;pi dhammaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t5">Dutiyam&#8217;pi saṅghaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t1">A second time, to the Buddha I go for refuge.</span></p><p class="p3"><span class="t1">A second time, to the Dhamma I go for refuge.</span></p><p class="p3"><span class="t1">A second time, to the Saṅgha I go for refuge.</span></p><p class="p3"><span class="t5">Tatiyam&#8217;pi buddhaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t5">Tatiyam&#8217;pi dhammaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t5">Tatiyam&#8217;pi saṅghaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t1">A third time, to the Buddha I go for refuge.</span></p><p class="p3"><span class="t1">A third time, to the Dhamma I go for refuge.</span></p><p class="p3"><span class="t1">A third time, to the Saṅgha I go for refuge.</span></p><h2><p class="p3"><span class="t5">[Khp 1.1]</span></p></h2><h2>Nama-kāra-siddhi-gāthā</h2><p class="p3"><span class="t5">Yo cakkhumā moha-malāpakaṭṭho</span></p><p class="p3"><span class="t5">Sāmaṃ va buddho sugato vimutto</span></p><p class="p3"><span class="t5">Mārassa pāsā vinimocayanto</span></p><p class="p3"><span class="t5">Pāpesi khemaṃ janataṃ vineyyaṃ</span></p><p class="p3"><span class="t5">Buddhaṃ varan&#8217;taṃ sirasā namāmi</span></p><p class="p3"><span class="t5">Lokassa nāthañ&#8217;ca vināyakañ&#8217;ca</span></p><p class="p3"><span class="t5">Tan&#8217;tejasā te jaya-siddhi hotu</span></p><p class="p3"><span class="t5">Sabb&#8217;antarāyā ca vināsam&#8217;entu</span></p><p class="p3"><span class="t1">The One with Vision, with the stain of delusion removed,</span></p><p class="p3"><span class="t1">Self-awakened, Well-Gone, &amp; Released,</span></p><p class="p3"><span class="t1">Freed from the snares of Mortal Temptation,</span></p><p class="p3"><span class="t1">He caused tameable people to reach security.</span></p><p class="p3"><span class="t1">I pay homage with my head to that excellent Buddha, the Protector &amp; Mentor for the world,</span></p><p class="p3"><span class="t1">By the power of this, may you have triumph &amp; success,</span></p><p class="p3"><span class="t1">And may all your dangers be destroyed.</span></p><p class="p3"><span class="t5">Dhammo dhajo yo viya tassa satthu</span></p><p class="p3"><span class="t5">Dassesi lokassa visuddhi-maggaṃ</span></p><p class="p3"><span class="t5">Niyyāniko dhamma-dharassa dhārī</span></p><p class="p3"><span class="t5">Sāt&#8217;āvaho santi-karo suciṇṇo</span></p><p class="p3"><span class="t5">Dhammaṃ varan&#8217;taṃ sirasā namāmi</span></p><p class="p3"><span class="t5">Moha-ppadālaṃ upasanta-dāhaṃ</span></p><p class="p3"><span class="t5">Tan&#8217;tejasā te jaya-siddhi hotu</span></p><p class="p3"><span class="t5">Sabb&#8217;antarāyā ca vināsam&#8217;entu</span></p><p class="p3"><span class="t1">The Teacher&#8217;s Dhamma, like a banner,</span></p><p class="p3"><span class="t1">Shows the path of purity to the world.</span></p><p class="p3"><span class="t1">Leading out, upholding those who uphold it</span></p><p class="p3"><span class="t1">Rightly accomplished, it brings pleasure, makes peace.</span></p><p class="p3"><span class="t1">I pay homage with my head to that excellent Dhamma,</span></p><p class="p3"><span class="t1">Which pierces delusion and makes fever grow calm.</span></p><p class="p3"><span class="t1">By the power of this, may you have triumph &amp; success,</span></p><p class="p3"><span class="t1">And may all your dangers be destroyed.</span></p><p class="p3"><span class="t5">Saddhamma-senā sugat&#8217;ānugo yo</span></p><p class="p3"><span class="t5">Lokassa pāp&#8217;ūpakilesa-jetā</span></p><p class="p3"><span class="t5">Santo sayaṃ santi-niyojako ca</span></p><p class="p3"><span class="t5">Svākkhāta-dhammaṃ viditaṃ karoti</span></p><p class="p3"><span class="t5">Saṅghaṃ varan&#8217;taṃ sirasā namāmi</span></p><p class="p3"><span class="t5">Buddh&#8217;ānubuddhaṃ sama-sīla-diṭṭhiṃ</span></p><p class="p3"><span class="t5">Tan&#8217;tejasā te jaya-siddhi hotu</span></p><p class="p3"><span class="t5">Sabb&#8217;antarāyā ca vināsam&#8217;entu</span></p><p class="p3"><span class="t1">The True Dhamma&#8217;s army, following the One Well-Gone,</span></p><p class="p3"><span class="t1">Is victor over the evils &amp; corruptions of the world.</span></p><p class="p3"><span class="t1">Virtuous, unifying itself in peace,</span></p><p class="p3"><span class="t1">And makes the well-taught Dhamma be known.</span></p><p class="p3"><span class="t1">I pay homage with my head to that excellent Saṅgha,</span></p><p class="p3"><span class="t1">Awakened after the Awakened, harmonious in virtue &amp; view.</span></p><p class="p3"><span class="t1">By the power of this, may you have triumph &amp; success,</span></p><p class="p3"><span class="t1">And may all your dangers be destroyed.</span></p><h2><p class="p3"><span class="t5">[Thai]</span></p></h2><h2>Namo-kāra-aṭṭhaka</h2><p class="p3"><span class="t5">Namo arahato Sammā-</span></p><p class="p3"><span class="t5">Sambuddhassa mah&#8217;esino</span></p><p class="p3"><span class="t5">Namo uttama-dhammassa</span></p><p class="p3"><span class="t5">Svākkhātass&#8217;eva ten&#8217;idha</span></p><p class="p3"><span class="t1">Homage to the Great Seer, the Worthy One, Perfectly Self-awakened; Homage to the highest Dhamma, well-taught by him here;</span></p><p class="p3"><span class="t5">Namo mahā-saṅghassā&#8217;pi</span></p><p class="p3"><span class="t5">Visuddha-sīla-diṭṭhino</span></p><p class="p3"><span class="t5">Namo omāty&#8217;āraddhassa</span></p><p class="p3"><span class="t5">Ratanattayassa sādhukaṃ</span></p><p class="p3"><span class="t1">And homage to the Great Saṅgha,</span></p><p class="p3"><span class="t1">Pure in virtue &amp; view.</span></p><p class="p3"><span class="t1">Homage to the Triple Gem</span></p><p class="p3"><span class="t1">Beginning auspiciously with AUM;</span></p><p class="p3"><span class="t5">Namo omak&#8217;ātītassa</span></p><p class="p3"><span class="t5">Tassa vatthuttayassa&#8217;pi</span></p><p class="p3"><span class="t5">Namo-kāra-ppabhāvena</span></p><p class="p3"><span class="t5">Vigacchantu upaddavā</span></p><p class="p3"><span class="t1">And homage to those three objects</span></p><p class="p3"><span class="t1">That have left base things behind.</span></p><p class="p3"><span class="t1">By the potency of this homage,</span></p><p class="p3"><span class="t1">May misfortunes disappear;</span></p><p class="p3"><span class="t5">Namo-kār&#8217;ānubhāvena</span></p><p class="p3"><span class="t5">Suvatthi hotu sabbadā</span></p><p class="p3"><span class="t5">Namo-kārassa tejena</span></p><p class="p3"><span class="t5">Vidhimhi homi tejavā</span></p><p class="p3"><span class="t1">By the potency of this homage,</span></p><p class="p3"><span class="t1">May there always be well-being;</span></p><p class="p3"><span class="t1">By the power of this homage,</span></p><p class="p3"><span class="t1">May success in this ceremony be mine.</span></p>', '-44-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Maṅgala-sutta', 'title', 45);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 45, '<h2>Maṅgala-sutta</h2><p class="p3"><span class="t5">Evaṃ me sutaṃ ekaṃ samayaṃ bhagavā sāvatthiyaṃ viharati jetavane anāthapiṇḍikassa ārāme. Atha kho aññatarā devatā abhikkantāya rattiyā abhikkantavaṇṇā kevalakappaṃ jetavanaṃ obhāsetvā yena bhagavā ten&#8217;upasaṅkami. Upasaṅkamitvā bhagavantaṃ abhivādetvā ekam&#8217;antaṃ aṭṭhāsi. Ekam&#8217;antaṃ ṭhitā kho sā devatā bhagavantaṃ gāthāya ajjhabhāsi</span></p><p class="p3"><span class="t1">Thus have I heard. On one occasion the Blessed One was dwelling at Sāvatthī in Jeta&#8217;s Grove, Anāthapiṇḍika&#8217;s Park. Then, when the night had advanced, a certain deity of stunning beauty, having illuminated the entire Jeta&#8217;s Grove, approached the Blessed One, paid homage to him, stood to one side, and addressed the Blessed One in verse</span></p><p class="p3"><span class="t5">Bahū devā manussā ca</span></p><p class="p3"><span class="t5">Maṅgalāni acintayuṃ</span></p><p class="p3"><span class="t5">Ākaṅkhamānā sotthānaṃ</span></p><p class="p3"><span class="t5">Brūhi maṅgalam&#8217;uttamaṃ</span></p><p class="p3"><span class="t1">Many devas and human beings</span></p><p class="p3"><span class="t1">Have reflected on blessings,</span></p><p class="p3"><span class="t1">Longing for safety,</span></p><p class="p3"><span class="t1">So declare the highest blessing.</span></p><p class="p3"><span class="t5">Asevanā ca bālānaṃ</span></p><p class="p3"><span class="t5">Paṇḍitānañ&#8217;ca sevanā</span></p><p class="p3"><span class="t5">Pūjā ca pūjanīyānaṃ</span></p><p class="p3"><span class="t5">Etam&#8217;maṅgalam&#8217;uttamaṃ</span></p><p class="p3"><span class="t1">Not associating with fools,</span></p><p class="p3"><span class="t1">Associating with the wise,</span></p><p class="p3"><span class="t1">And venerating those worthy of veneration:</span></p><p class="p3"><span class="t1">This is the highest blessing.</span></p><p class="p3"><span class="t5">Paṭirūpa-desa-vāso ca</span></p><p class="p3"><span class="t5">Pubbe ca kata-puññatā</span></p><p class="p3"><span class="t5">Atta-sammā-paṇidhi ca</span></p><p class="p3"><span class="t5">Etam&#8217;maṅgalam&#8217;uttamaṃ</span></p><p class="p3"><span class="t1">Residing in a suitable place</span></p><p class="p3"><span class="t1">Merit done in the past</span></p><p class="p3"><span class="t1">And directing oneself rightly</span></p><p class="p3"><span class="t1">This is the highest blessing</span></p><p class="p3"><span class="t5">Bāhu-saccañ&#8217;ca sippañ&#8217;ca</span></p><p class="p3"><span class="t5">Vinayo ca susikkhito</span></p><p class="p3"><span class="t5">Subhāsitā ca yā vācā</span></p><p class="p3"><span class="t5">Etam&#8217;maṅgalam&#8217;uttamaṃ</span></p><p class="p3"><span class="t1">Much learning, a craft,</span></p><p class="p3"><span class="t1">A well-trained discipline,</span></p><p class="p3"><span class="t1">And well-spoken speech:</span></p><p class="p3"><span class="t1">This is the highest blessing.</span></p><p class="p3"><span class="t5">Mātā-pitu upaṭṭhānaṃ</span></p><p class="p3"><span class="t5">Putta-dārassa saṅgaho</span></p><p class="p3"><span class="t5">Anākulā ca kammantā</span></p><p class="p3"><span class="t5">Etam&#8217;maṅgalam&#8217;uttamaṃ</span></p><p class="p3"><span class="t1">Serving one&#8217;s mother and father,</span></p><p class="p3"><span class="t1">Maintaining a wife and children,</span></p><p class="p3"><span class="t1">And an honest occupation:</span></p><p class="p3"><span class="t1">This is the highest blessing.</span></p><p class="p3"><span class="t5">Dānañ&#8217;ca dhamma-cariyā ca</span></p><p class="p3"><span class="t5">Ñātakānañ&#8217;ca saṅgaho</span></p><p class="p3"><span class="t5">Anavajjāni kammāni</span></p><p class="p3"><span class="t5">Etam&#8217;maṅgalam&#8217;uttamaṃ</span></p><p class="p3"><span class="t1">Giving and righteous conduct,</span></p><p class="p3"><span class="t1">Assistance to relatives,</span></p><p class="p3"><span class="t1">Blameless deeds:</span></p><p class="p3"><span class="t1">This is the highest blessing.</span></p><p class="p3"><span class="t5">Āratī viratī pāpā</span></p><p class="p3"><span class="t5">Majja-pānā ca saññamo</span></p><p class="p3"><span class="t5">Appamādo ca dhammesu</span></p><p class="p3"><span class="t5">Etam&#8217;maṅgalam&#8217;uttamaṃ</span></p><p class="p3"><span class="t1">Desisting and abstaining from evil,</span></p><p class="p3"><span class="t1">Refraining from intoxicating drink,</span></p><p class="p3"><span class="t1">Heedfulness in good qualities:</span></p><p class="p3"><span class="t1">This is the highest blessing.</span></p><p class="p3"><span class="t5">Gāravo ca nivāto ca</span></p><p class="p3"><span class="t5">Santuṭṭhī ca kataññutā</span></p><p class="p3"><span class="t5">Kālena dhamma-ssavanaṃ</span></p><p class="p3"><span class="t5">Etam&#8217;maṅgalam&#8217;uttamaṃ</span></p><p class="p3"><span class="t1">Reverence and humility,</span></p><p class="p3"><span class="t1">Contentment and gratitude,</span></p><p class="p3"><span class="t1">Timely listening to the Dhamma:</span></p><p class="p3"><span class="t1">This is the highest blessing.</span></p><p class="p3"><span class="t5">Khantī ca sovacassatā</span></p><p class="p3"><span class="t5">Samaṇānañ&#8217;ca dassanaṃ</span></p><p class="p3"><span class="t5">Kālena dhamma-sākacchā</span></p><p class="p3"><span class="t5">Etam&#8217;maṅgalam&#8217;uttamaṃ</span></p><p class="p3"><span class="t1">Patience, being amenable to advice,</span></p><p class="p3"><span class="t1">The seeing of ascetics,</span></p><p class="p3"><span class="t1">Timely discussion on the Dhamma:</span></p><p class="p3"><span class="t1">This is the highest blessing.</span></p><p class="p3"><span class="t5">Tapo ca brahmacariyañ&#8217;ca</span></p><p class="p3"><span class="t5">Ariya-saccāna-dassanaṃ</span></p><p class="p3"><span class="t5">Nibbāna-sacchikiriyā ca</span></p><p class="p3"><span class="t5">Etam&#8217;maṅgalam&#8217;uttamaṃ</span></p><p class="p3"><span class="t1">Austerity and the holy life,</span></p><p class="p3"><span class="t1">Seeing of the noble truths,</span></p><p class="p3"><span class="t1">And realization of Nibbāna:</span></p><p class="p3"><span class="t1">This is the highest blessing.</span></p><p class="p3"><span class="t5">Phuṭṭhassa loka-dhammehi</span></p><p class="p3"><span class="t5">Cittaṃ yassa na kampati</span></p><p class="p3"><span class="t5">Asokaṃ virajaṃ khemaṃ</span></p><p class="p3"><span class="t5">Etam&#8217;maṅgalam&#8217;uttamaṃ</span></p><p class="p3"><span class="t1">One whose mind does not shake</span></p><p class="p3"><span class="t1">When touched by worldly conditions,</span></p><p class="p3"><span class="t1">Sorrowless, dust-free, secure:</span></p><p class="p3"><span class="t1">This is the highest blessing.</span></p><p class="p3"><span class="t5">Etādisāni katvāna</span></p><p class="p3"><span class="t5">Sabbattham&#8217;aparājitā</span></p><p class="p3"><span class="t5">Sabbattha sotthiṃ gacchanti</span></p><p class="p3"><span class="t5">Tan&#8217;tesaṃ maṅgalam&#8217;uttaman&#8217;ti</span></p><p class="p3"><span class="t1">Those who have done these things</span></p><p class="p3"><span class="t1">Are victorious everywhere;</span></p><p class="p3"><span class="t1">Everywhere they go safely:</span></p><p class="p3"><span class="t1">Theirs is that highest blessing.</span></p><h2><p class="p3"><span class="t5">[Snp 2.4]</span></p></h2><h2>Ratana-sutta</h2><p class="p3"><span class="t5">Yān&#8217;īdha bhūtāni samāgatāni</span></p><p class="p3"><span class="t5">Bhummāni vā yāni va antalikkhe</span></p><p class="p3"><span class="t5">Sabb&#8217;eva bhūtā sumanā bhavantu</span></p><p class="p3"><span class="t5">Atho&#8217;pi sakkacca suṇantu bhāsitaṃ</span></p><p class="p3"><span class="t1">Whatever beings are gathered here,</span></p><p class="p3"><span class="t1">Whether of the earth or in the sky,</span></p><p class="p3"><span class="t1">May all beings indeed be happy</span></p><p class="p3"><span class="t1">And then listen carefully to what is said.</span></p><p class="p3"><span class="t5">Tasmā hi bhūtā nisāmetha sabbe</span></p><p class="p3"><span class="t5">Mettaṃ karotha mānusiyā pajāya</span></p><p class="p3"><span class="t5">Divā ca ratto ca haranti ye baliṃ</span></p><p class="p3"><span class="t5">Tasmā hi ne rakkhatha appamattā</span></p><p class="p3"><span class="t1">Therefore, O beings, all of you listen;</span></p><p class="p3"><span class="t1">Show loving-kindness to the human population,</span></p><p class="p3"><span class="t1">Who day and night bring you offerings;</span></p><p class="p3"><span class="t1">Therefore, being heedful, protect them.</span></p><p class="p3"><span class="t5">Yaṅ&#8217;kiñci vittaṃ idha vā huraṃ vā</span></p><p class="p3"><span class="t5">Saggesu vā yaṃ ratanaṃ paṇītaṃ</span></p><p class="p3"><span class="t5">Na no samaṃ atthi tathāgatena</span></p><p class="p3"><span class="t5">Idam&#8217;pi buddhe ratanaṃ paṇītaṃ</span></p><p class="p3"><span class="t5">Etena saccena suvatthi hotu</span></p><p class="p3"><span class="t1">Whatever the treasures are here or beyond,</span></p><p class="p3"><span class="t1">Whatever the precious jewel in the heavens,</span></p><p class="p3"><span class="t1">There is none equal to the Thus-gone.</span></p><p class="p3"><span class="t1">In the Buddha is this sublime jewel.</span></p><p class="p3"><span class="t1">By this truth, may there be well-being.</span></p><p class="p3"><span class="t5">Khayaṃ virāgaṃ amataṃ paṇītaṃ</span></p><p class="p3"><span class="t5">Yad&#8217;ajjhagā sakya-munī samāhito</span></p><p class="p3"><span class="t5">Na tena dhammena sam&#8217;atthi kiñci</span></p><p class="p3"><span class="t5">Idam&#8217;pi dhamme ratanaṃ paṇītaṃ</span></p><p class="p3"><span class="t5">Etena saccena suvatthi hotu</span></p><p class="p3"><span class="t1">Destruction, dispassion, the deathless, the sublime,</span></p><p class="p3"><span class="t1">Which Sakyamuni, concentrated, attained:</span></p><p class="p3"><span class="t1">There is nothing equal to that Dhamma.</span></p><p class="p3"><span class="t1">This too is the sublime gem in the Dhamma:</span></p><p class="p3"><span class="t1">By this truth, may there be safety!</span></p><p class="p3"><span class="t5">Yam&#8217;buddha-seṭṭho parivaṇṇayī suciṃ</span></p><p class="p3"><span class="t5">Samādhim&#8217;ānantarikañ&#8217;ñam&#8217;āhu</span></p><p class="p3"><span class="t5">Samādhinā tena samo na vijjati</span></p><p class="p3"><span class="t5">Idam&#8217;pi dhamme ratanaṃ paṇītaṃ</span></p><p class="p3"><span class="t5">Etena saccena suvatthi hotu</span></p><p class="p3"><span class="t1">The purity that the supreme Buddha praised,</span></p><p class="p3"><span class="t1">Which they call concentration without interval</span></p><p class="p3"><span class="t1">The equal of that concentration does not exist.</span></p><p class="p3"><span class="t1">This too is the sublime gem in the Dhamma:</span></p><p class="p3"><span class="t1">By this truth, may there be safety!</span></p><p class="p3"><span class="t5">Ye puggalā aṭṭha sataṃ pasatthā</span></p><p class="p3"><span class="t5">Cattāri etāni yugāni honti</span></p><p class="p3"><span class="t5">Te dakkhiṇeyyā sugatassa sāvakā</span></p><p class="p3"><span class="t5">Etesu dinnāni maha-pphalāni</span></p><p class="p3"><span class="t5">Idam&#8217;pi saṅghe ratanaṃ paṇītaṃ</span></p><p class="p3"><span class="t5">Etena saccena suvatthi hotu</span></p><p class="p3"><span class="t1">The eight persons praised by the good</span></p><p class="p3"><span class="t1">Constitute these four pairs.</span></p><p class="p3"><span class="t1">These, worthy of offerings, are the Fortunate One&#8217;s disciples;</span></p><p class="p3"><span class="t1">Gifts given to them yield abundant fruit.</span></p><p class="p3"><span class="t1">This too is the sublime gem in the Sangha:</span></p><p class="p3"><span class="t1">By this truth, may there be safety!</span></p><p class="p3"><span class="t5">Ye suppayuttā manasā daḷhena</span></p><p class="p3"><span class="t5">Nikkāmino gotama-sāsanamhi</span></p><p class="p3"><span class="t5">Te patti-pattā amataṃ vigayha</span></p><p class="p3"><span class="t5">Laddhā mudhā nibbutiṃ bhuñjamānā</span></p><p class="p3"><span class="t5">Idam&#8217;pi saṅghe ratanaṃ paṇītaṃ</span></p><p class="p3"><span class="t5">Etena saccena suvatthi hotu</span></p><p class="p3"><span class="t1">Those who strived well with a firm mind,</span></p><p class="p3"><span class="t1">Who are desireless in Gotama&#8217;s teaching,</span></p><p class="p3"><span class="t1">Have reached attainment, having plunged into the deathless,</span></p><p class="p3"><span class="t1">Enjoying perfect peace obtained free of charge.</span></p><p class="p3"><span class="t1">This too is the sublime gem in the Sangha:</span></p><p class="p3"><span class="t1">By this truth, may there be safety!</span></p><p class="p3"><span class="t5">Yath&#8217;inda-khīlo paṭhaviṃ sito siyā</span></p><p class="p3"><span class="t5">Catubbhi vātebhi asampakampiyo</span></p><p class="p3"><span class="t5">Tath&#8217;ūpamaṃ sappurisaṃ vadāmi</span></p><p class="p3"><span class="t5">Yo ariya-saccāni avecca passati</span></p><p class="p3"><span class="t5">Idam&#8217;pi saṅghe ratanaṃ paṇītaṃ</span></p><p class="p3"><span class="t5">Etena saccena suvatthi hotu</span></p><p class="p3"><span class="t1">As a gate post, planted in the ground,</span></p><p class="p3"><span class="t1">Would be unshakable by the four winds,</span></p><p class="p3"><span class="t1">Similarly I speak of the good person</span></p><p class="p3"><span class="t1">Who, having experienced them, sees the noble truths.</span></p><p class="p3"><span class="t1">This too is the sublime gem in the Sangha:</span></p><p class="p3"><span class="t1">By this truth, may there be safety!</span></p><p class="p3"><span class="t5">Ye ariya-saccāni vibhāvayanti</span></p><p class="p3"><span class="t5">Gambhīra-paññena sudesitāni</span></p><p class="p3"><span class="t5">Kiñ&#8217;c&#8217;āpi te honti bhusa&#8217;ppamattā</span></p><p class="p3"><span class="t5">Na te bhavaṃ aṭṭhamam&#8217;ādiyanti</span></p><p class="p3"><span class="t5">Idam&#8217;pi saṅghe ratanaṃ paṇītaṃ</span></p><p class="p3"><span class="t5">Etena saccena suvatthi hotu</span></p><p class="p3"><span class="t1">Those who have cognized the noble truths</span></p><p class="p3"><span class="t1">Well taught by the one of deep wisdom,</span></p><p class="p3"><span class="t1">Even if they are extremely heedless,</span></p><p class="p3"><span class="t1">Do not take an eighth existence.</span></p><p class="p3"><span class="t1">By body, speech, or mind,</span></p><p class="p3"><span class="t1">One is incapable of concealing it;</span></p><p class="p3"><span class="t1">Such inability is stated for one who has seen the state.</span></p><p class="p3"><span class="t1">This too is the sublime gem in the Sangha:</span></p><p class="p3"><span class="t1">By this truth, may there be safety!</span></p><p class="p3"><span class="t5">Vana-ppagumbe yathā phussit&#8217;agge</span></p><p class="p3"><span class="t5">Gimhāna-māse paṭhamasmiṃ gimhe</span></p><p class="p3"><span class="t5">Tath&#8217;ūpamaṃ dhamma-varaṃ adesayi</span></p><p class="p3"><span class="t5">Nibbāna-gāmiṃ paramaṃ hitāya</span></p><p class="p3"><span class="t5">Idam&#8217;pi buddhe ratanaṃ paṇītaṃ</span></p><p class="p3"><span class="t5">Etena saccena suvatthi hotu</span></p><p class="p3"><span class="t1">Like a woodland thicket with flowering crests</span></p><p class="p3"><span class="t1">In a summer month, in the first of the summer,</span></p><p class="p3"><span class="t1">Just so he taught the excellent Dhamma,</span></p><p class="p3"><span class="t1">Leading to nibbāna, for the supreme welfare.</span></p><p class="p3"><span class="t1">This too is the sublime gem in the Buddha:</span></p><p class="p3"><span class="t1">By this truth, may there be safety!</span></p><p class="p3"><span class="t5">Varo varaññū varado var&#8217;āharo</span></p><p class="p3"><span class="t5">Anuttaro dhamma-varaṃ adesayi</span></p><p class="p3"><span class="t5">Idam&#8217;pi buddhe ratanaṃ paṇītaṃ</span></p><p class="p3"><span class="t5">Etena saccena suvatthi hotu</span></p><p class="p3"><span class="t1">The excellent one, knower of the excellent,</span></p><p class="p3"><span class="t1">Giver of the excellent, bringer of the excellent,</span></p><p class="p3"><span class="t1">The unsurpassed one taught the excellent Dhamma.</span></p><p class="p3"><span class="t1">This too is the sublime gem in the Buddha:</span></p><p class="p3"><span class="t1">By this truth, may there be safety!</span></p><p class="p3"><span class="t5">Khīṇaṃ purāṇaṃ navaṃ n&#8217;atthi sambhavaṃ</span></p><p class="p3"><span class="t5">Viratta-citt&#8217;āyatike bhavasmiṃ</span></p><p class="p3"><span class="t5">Te khīṇa-bījā aviruḷhi-chandā</span></p><p class="p3"><span class="t5">Nibbanti dhīrā yathā&#8217;yam padīpo</span></p><p class="p3"><span class="t5">Idam&#8217;pi saṅghe ratanaṃ paṇītaṃ</span></p><p class="p3"><span class="t5">Etena saccena suvatthi hotu</span></p><p class="p3"><span class="t1">The old is destroyed, there is no new origination,</span></p><p class="p3"><span class="t1">Their minds are dispassionate toward future existence.</span></p><p class="p3"><span class="t1">With seeds destroyed, with no desire for growth,</span></p><p class="p3"><span class="t1">Those wise ones are extinguished like this lamp.</span></p><p class="p3"><span class="t1">This too is the sublime gem in the Sangha:</span></p><p class="p3"><span class="t1">By this truth, may there be safety!</span></p><p class="p3"><span class="t5">Yān&#8217;īdha bhūtāni samāgatāni</span></p><p class="p3"><span class="t5">Bhummāni vā yāni va antalikkhe</span></p><p class="p3"><span class="t5">Tathāgataṃ deva-manussa-pūjitaṃ</span></p><p class="p3"><span class="t5">Buddhaṃ namassāma suvatthi hotu</span></p><p class="p3"><span class="t1">Whatever beings are gathered here,</span></p><p class="p3"><span class="t1">Whether of the earth or in the sky,</span></p><p class="p3"><span class="t1">We pay homage to the thus-gone Buddha,</span></p><p class="p3"><span class="t1">Venerated by devas and humans: may there be safety!</span></p><p class="p3"><span class="t5">Yān&#8217;īdha bhūtāni samāgatāni</span></p><p class="p3"><span class="t5">Bhummāni vā yāni va antalikkhe</span></p><p class="p3"><span class="t5">Tathāgataṃ deva-manussa-pūjitaṃ</span></p><p class="p3"><span class="t5">Dhammaṃ namassāma suvatthi hotu</span></p><p class="p3"><span class="t1">Whatever beings are gathered here,</span></p><p class="p3"><span class="t1">Whether of the earth or in the sky,</span></p><p class="p3"><span class="t1">We pay homage to the thus-gone Dhamma,</span></p><p class="p3"><span class="t1">Venerated by devas and humans: may there be safety!</span></p><p class="p3"><span class="t5">Yān&#8217;īdha bhūtāni samāgatāni</span></p><p class="p3"><span class="t5">Bhummāni vā yāni va antalikkhe</span></p><p class="p3"><span class="t5">Tathāgataṃ deva-manussa-pūjitaṃ</span></p><p class="p3"><span class="t5">Saṅghaṃ namassāma suvatthi hotu</span></p><p class="p3"><span class="t1">Whatever beings are gathered here,</span></p><p class="p3"><span class="t1">Whether of the earth or in the sky,</span></p><p class="p3"><span class="t1">We pay homage to the thus-gone Sangha,</span></p><p class="p3"><span class="t1">Venerated by devas and humans: may there be safety!</span></p></h2><h2>Karaṇīya-metta-sutta</h2><p class="p3"><span class="t5">Karaṇīyam &#8217;attha-kusalena</span></p><p class="p3"><span class="t5">Yan&#8217;taṃ santaṃ padaṃ abhisamecca</span></p><p class="p3"><span class="t5">Sakko ujū ca suhujū ca</span></p><p class="p3"><span class="t5">Suvaco c&#8217;assa mudu anatimānī</span></p><p class="p3"><span class="t1">This is to be done by one skilled in the beneficial,</span></p><p class="p3"><span class="t1">Having understood the path of peace.</span></p><p class="p3"><span class="t1">He would be able, upright, very upright,</span></p><p class="p3"><span class="t1">Obedient, gentle, and not conceited.</span></p><p class="p3"><span class="t5">Santussako ca subharo ca</span></p><p class="p3"><span class="t5">Appakicco ca sallahuka-vutti</span></p><p class="p3"><span class="t5">Sant&#8217;indriyo ca nipako ca</span></p><p class="p3"><span class="t5">Appagabbho kulesu ananugiddho</span></p><p class="p3"><span class="t1">Content and easy to support,</span></p><p class="p3"><span class="t1">Having little duties and a light livelihood;</span></p><p class="p3"><span class="t1">Calm in faculties and prudent,</span></p><p class="p3"><span class="t1">Not impudent &amp; greedily attached to families.</span></p><p class="p3"><span class="t5">Na ca khuddaṃ samācare kiñci</span></p><p class="p3"><span class="t5">Yena viññū pare upavadeyyuṃ</span></p><p class="p3"><span class="t5">Sukhino vā khemino hontu</span></p><p class="p3"><span class="t5">Sabbe sattā bhavantu sukhit&#8217;attā</span></p><p class="p3"><span class="t1">He would not do the slightest thing,</span></p><p class="p3"><span class="t1">For which wise others would reproach.</span></p><p class="p3"><span class="t1">Well and secure may [all beings] be;</span></p><p class="p3"><span class="t1">May all beings be happy at heart.</span></p><p class="p3"><span class="t5">Ye keci pāṇa-bhūt&#8217;atthi</span></p><p class="p3"><span class="t5">Tasā vā thāvarā vā anavasesā</span></p><p class="p3"><span class="t5">Dīghā vā ye mahantā vā</span></p><p class="p3"><span class="t5">Majjhimā rassakā aṇuka-thūlā</span></p><p class="p3"><span class="t1">Whatever breathing beings are born,</span></p><p class="p3"><span class="t1">Whether timid or firm - without remainder</span></p><p class="p3"><span class="t1">Whether they are long or they are great,</span></p><p class="p3"><span class="t1">Whether they are medium, short, minute or fat.</span></p><p class="p3"><span class="t5">Diṭṭhā vā ye ca adiṭṭhā</span></p><p class="p3"><span class="t5">Ye ca dūre vasanti avidūre</span></p><p class="p3"><span class="t5">Bhūtā vā sambhavesī vā</span></p><p class="p3"><span class="t5">Sabbe sattā bhavantu sukhit&#8217;attā</span></p><p class="p3"><span class="t1">Whether they are seen or unseen,</span></p><p class="p3"><span class="t1">Whether they live far away or not far away;</span></p><p class="p3"><span class="t1">Already born or seeking rebirth</span></p><p class="p3"><span class="t1">May all beings be happy at heart.</span></p><p class="p3"><span class="t5">Na paro paraṃ nikubbetha</span></p><p class="p3"><span class="t5">N&#8217;ātimaññetha katthaci naṃ kiñci</span></p><p class="p3"><span class="t5">Byārosanā paṭīgha-saññā</span></p><p class="p3"><span class="t5">N&#8217;āññam&#8217;aññassa dukkham&#8217;iccheyya</span></p><p class="p3"><span class="t1">Another (i.e. one) would not deceive another,</span></p><p class="p3"><span class="t1">Nor look down upon anyone anywhere;</span></p><p class="p3"><span class="t1">Nor through anger or hateful perception,</span></p><p class="p3"><span class="t1">Would wish for one another&#8217;s suffering.</span></p><p class="p3"><span class="t5">Mātā yathā niyaṃ puttaṃ</span></p><p class="p3"><span class="t5">Āyusā eka-puttam&#8217;anurakkhe</span></p><p class="p3"><span class="t5">Evam&#8217;pi sabba-bhūtesu</span></p><p class="p3"><span class="t5">Mānasam&#8217;bhāvaye aparimāṇaṃ</span></p><p class="p3"><span class="t1">Just as a mother [her] own child,</span></p><p class="p3"><span class="t1">[Her] only child, would protect with [her] life;</span></p><p class="p3"><span class="t1">Thus also, towards all beings,</span></p><p class="p3"><span class="t1">He would develop the mind without measure.</span></p><p class="p3"><span class="t5">Mettañ&#8217;ca sabba-lokasmiṃ</span></p><p class="p3"><span class="t5">Mānasam&#8217;bhāvaye aparimāṇaṃ</span></p><p class="p3"><span class="t5">Uddhaṃ adho ca tiriyañ&#8217;ca</span></p><p class="p3"><span class="t5">Asambādhaṃ averaṃ asapattaṃ</span></p><p class="p3"><span class="t1">And [with] mettā to the whole world,</span></p><p class="p3"><span class="t1">He would develop the mind without measure;</span></p><p class="p3"><span class="t1">Above, and below and across,</span></p><p class="p3"><span class="t1">Unrestricted, without enmity or foe.</span></p><p class="p3"><span class="t5">Tiṭṭhañ&#8217;caraṃ nisinno vā</span></p><p class="p3"><span class="t5">Sayāno vā yāvat&#8217;assa vigata-middho</span></p><p class="p3"><span class="t5">Etaṃ satiṃ adhiṭṭheyya</span></p><p class="p3"><span class="t5">Brahmam&#8217;etaṃ vihāraṃ idham&#8217;āhu</span></p><p class="p3"><span class="t1">Whether standing, walking, sitting, or reclining,</span></p><p class="p3"><span class="t1">Whenever he is free from drowsiness,</span></p><p class="p3"><span class="t1">He would resolve on that mindfulness</span></p><p class="p3"><span class="t1"><q>That is a lofty dwelling,</q> here they say.</span></p><p class="p3"><span class="t5">Diṭṭhiñ&#8217;ca anupagamma</span></p><p class="p3"><span class="t5">Sīlavā dassanena sampanno</span></p><p class="p3"><span class="t5">Kāmesu vineyya gedhaṃ</span></p><p class="p3"><span class="t5">Na hi jātu gabbha-seyyaṃ punar-etī&#8217;ti</span></p><p class="p3"><span class="t1">Not having arrived at a [wrong] view,</span></p><p class="p3"><span class="t1">Being virtuous and possessed of vision,</span></p><p class="p3"><span class="t1">Having removed greed for sense pleasures,</span></p><p class="p3"><span class="t1">He never again returns to lie in a womb.</span></p><h2><p class="p3"><span class="t5">[Snp 1.8]</span></p>', '-45-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Khandha-parittaṃ', 'title', 46);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 46, '<h2>Khandha-parittaṃ</h2><p class="p3"><span class="t5">Virūpakkhehi me mettaṃ</span></p><p class="p3"><span class="t5">Mettaṃ erāpathehi me</span></p><p class="p3"><span class="t5">Chabyāputtehi me mettaṃ</span></p><p class="p3"><span class="t5">Mettaṃ kaṇhāgotamakehi ca</span></p><p class="p3"><span class="t1">With the Virūpakkhas is my loving-kindness.</span></p><p class="p3"><span class="t1">My loving-kindness is with the Erāpathas.</span></p><p class="p3"><span class="t1">With the Chabyāputtas is my loving-kindness.</span></p><p class="p3"><span class="t1">And loving-kindness is with the Kaṇhāgotamakas.</span></p><p class="p3"><span class="t5">Apādakehi me mettaṃ</span></p><p class="p3"><span class="t5">Mettaṃ dipādakehi me</span></p><p class="p3"><span class="t5">Catuppadehi me mettaṃ</span></p><p class="p3"><span class="t5">Mettaṃ bahuppadehi me</span></p><p class="p3"><span class="t1">With the footless is my loving-kindness.</span></p><p class="p3"><span class="t1">My loving-kindness is with the two-footed.</span></p><p class="p3"><span class="t1">With the four-footed is my loving-kindness.</span></p><p class="p3"><span class="t1">My loving-kindness is with the many-footed.</span></p><p class="p3"><span class="t5">Mā maṃ apādako hiṃsi</span></p><p class="p3"><span class="t5">Mā maṃ hiṃsi dipādako</span></p><p class="p3"><span class="t5">Mā maṃ catuppado hiṃsi</span></p><p class="p3"><span class="t5">Mā maṃ hiṃsi bahuppado</span></p><p class="p3"><span class="t1">May the footless not harm me.</span></p><p class="p3"><span class="t1">May the two-footed harm me not.</span></p><p class="p3"><span class="t1">May the four-footed not harm me.</span></p><p class="p3"><span class="t1">May the many-footed harm me not.</span></p><p class="p3"><span class="t5">Sabbe sattā sabbe pāṇā</span></p><p class="p3"><span class="t5">Sabbe bhūtā ca kevalā</span></p><p class="p3"><span class="t5">Sabbe bhadrāni passantu</span></p><p class="p3"><span class="t5">Mā kiñci pāpam&#8217;āgamā.</span></p><p class="p3"><span class="t1">All sentient beings, all who breathe,</span></p><p class="p3"><span class="t1">All the born — in totality — </span></p><p class="p3"><span class="t1">May they all meet with good fortune;</span></p><p class="p3"><span class="t1">May they not come to any evil.</span></p><p class="p3"><span class="t5">Appamāṇo buddho</span></p><p class="p3"><span class="t5">Appamāṇo dhammo</span></p><p class="p3"><span class="t5">Appamāṇo saṅgho</span></p><p class="p3"><span class="t5">Pamāṇavantāni siriṃsapāni</span></p><p class="p3"><span class="t5">Ahi-vicchikā satapadī</span></p><p class="p3"><span class="t5">Uṇṇānābhī sarabhū mūsikā</span></p><p class="p3"><span class="t1">Measureless is the Buddha,</span></p><p class="p3"><span class="t1">Measureless is the Dhamma,</span></p><p class="p3"><span class="t1">Measureless is the Saṅgha.</span></p><p class="p3"><span class="t1">Measureable are crawling creatures:</span></p><p class="p3"><span class="t1">Snakes and scorpions and centipedes,</span></p><p class="p3"><span class="t1">Spiders and lizards and mice and rats.</span></p><p class="p3"><span class="t5">Katā me rakkhā katā me parittā</span></p><p class="p3"><span class="t5">Paṭikkamantu bhūtāni</span></p><p class="p3"><span class="t5">So&#8217;haṃ namo bhagavato</span></p><p class="p3"><span class="t5">Namo sattannaṃ sammā-sambuddhānaṃ.</span></p><p class="p3"><span class="t1">A safeguard has been made by me,</span></p><p class="p3"><span class="t1">A protection has been made by me.</span></p><p class="p3"><span class="t1">Let the already born retreat.</span></p><p class="p3"><span class="t1">I pay homage to the Blessed One;</span></p><p class="p3"><span class="t1">Homage to the seven Perfectly Self-awakened Ones.</span></p><h2><p class="p3"><span class="t5">[AN 4.67]</span></p></h2><h2>Buddha-dhamma-saṅgha-guṇā</h2><p class="p3"><span class="t5">Iti&#8217;pi so bhagavā</span></p><p class="p3"><span class="t5">Arahaṃ sammā-sambuddho</span></p><p class="p3"><span class="t5">Vijjā-caraṇa-sampanno</span></p><p class="p3"><span class="t5">Sugato loka-vidū</span></p><p class="p3"><span class="t5">Anuttaro purisa-damma-sārathi</span></p><p class="p3"><span class="t5">Satthā deva-manussānaṃ</span></p><p class="p3"><span class="t5">Buddho bhagavā&#8217;ti</span></p><p class="p3"><span class="t1">Thus also is the Blessed One</span></p><p class="p3"><span class="t1">An arahant, fully self-awakened,</span></p><p class="p3"><span class="t1">Accomplished in knowledge and conduct,</span></p><p class="p3"><span class="t1">Fortunate, knower of the world,</span></p><p class="p3"><span class="t1">Unsurpassed leader of persons to be tamed,</span></p><p class="p3"><span class="t1">Teacher of deities and humans,</span></p><p class="p3"><span class="t1">Awakened and blessed.</span></p><p class="p3"><span class="t5">Svākkhāto bhagavatā dhammo</span></p><p class="p3"><span class="t5">Sandiṭṭhiko akāliko ehi-passiko</span></p><p class="p3"><span class="t5">Opanayiko paccattaṃ veditabbo viññūhī&#8217;ti</span></p><p class="p3"><span class="t1">The Dhamma is well-expounded by the Blessed One,</span></p><p class="p3"><span class="t1">Directly visible, immediate, inviting one to come and see,</span></p><p class="p3"><span class="t1">Applicable, to be personally experienced by the wise.</span></p><p class="p3"><span class="t5">Supaṭipanno bhagavato sāvaka-saṅgho</span></p><p class="p3"><span class="t5">Uju-paṭipanno bhagavato sāvaka-saṅgho</span></p><p class="p3"><span class="t5">Ñāya-paṭipanno bhagavato sāvaka-saṅgho</span></p><p class="p3"><span class="t5">Sāmīci-paṭipanno bhagavato sāvaka-saṅgho</span></p><p class="p3"><span class="t5">Yad&#8217;idaṃ cattāri purisa-yugāni aṭṭha purisa-puggalā</span></p><p class="p3"><span class="t5">Esa bhagavato sāvaka-saṅgho</span></p><p class="p3"><span class="t5">Āhuneyyo pāhuneyyo dakkhiṇeyyo añjali-karaṇīyo</span></p><p class="p3"><span class="t5">Anuttaraṃ puñña-kkhettaṃ lokassā&#8217;ti</span></p><p class="p3"><span class="t1">Practicing the good way is the Community of the Blessed One&#8217;s disciples;</span></p><p class="p3"><span class="t1">Practicing the straight way is the Community of the Blessed One&#8217;s disciples;</span></p><p class="p3"><span class="t1">Practicing the true way is the Community of the Blessed One&#8217;s disciples;</span></p><p class="p3"><span class="t1">Practicing the proper way is the Community of the Blessed One&#8217;s disciples;</span></p><p class="p3"><span class="t1">That is, the four pairs of persons, the eight kinds of individuals.</span></p><p class="p3"><span class="t1">This is the Community of the Blessed One&#8217;s disciples;</span></p><p class="p3"><span class="t1">Worthy of gifts, worthy of hospitality, worthy of offerings, worthy of reverential salutation,</span></p><p class="p3"><span class="t1">The unsurpassed field of merit for the world.</span></p><h2><p class="p3"><span class="t5">[SN 11.3]</span></p></h2><h2>Yaṅ&#8217;kiñci ratanaṃ loke</h2><p class="p3"><span class="t5">Yaṅ&#8217;kiñci ratanaṃ loke</span></p><p class="p3"><span class="t5">Vijjati vividhaṃ puthu</span></p><p class="p3"><span class="t5">Ratanaṃ buddha-samaṃ n&#8217;atthi</span></p><p class="p3"><span class="t5">Tasmā sotthī bhavantu te</span></p><p class="p3"><span class="t1">Whatever kind of jewel in the world</span></p><p class="p3"><span class="t1">There is found by a human being,</span></p><p class="p3"><span class="t1">A jewel comparable to the Buddha does not exist;</span></p><p class="p3"><span class="t1">Therefore may you be blessed.</span></p><p class="p3"><span class="t5">Yaṅ&#8217;kiñci ratanaṃ loke</span></p><p class="p3"><span class="t5">Vijjati vividhaṃ puthu</span></p><p class="p3"><span class="t5">Ratanaṃ dhamma-samaṃ n&#8217;atthi</span></p><p class="p3"><span class="t5">Tasmā sotthī bhavantu te</span></p><p class="p3"><span class="t1">Whatever kind of jewel in the world</span></p><p class="p3"><span class="t1">There is found by a human being,</span></p><p class="p3"><span class="t1">A jewel comparable to the Dhamma does not exist;</span></p><p class="p3"><span class="t1">Therefore may you be blessed.</span></p><p class="p3"><span class="t5">Yaṅ&#8217;kiñci ratanaṃ loke</span></p><p class="p3"><span class="t5">Vijjati vividhaṃ puthu</span></p><p class="p3"><span class="t5">Ratanaṃ saṅgha-samaṃ n&#8217;atthi</span></p><p class="p3"><span class="t5">Tasmā sotthī bhavantu te</span></p><p class="p3"><span class="t1">Whatever kind of jewel in the world</span></p><p class="p3"><span class="t1">There is found by a human being,</span></p><p class="p3"><span class="t1">A jewel comparable to the Saṅgha does not exist;</span></p><p class="p3"><span class="t1">Therefore may you be blessed.</span></p><p class="p3"><span class="t5">Sakkatvā buddha-ratanaṃ</span></p><p class="p3"><span class="t5">Osadhaṃ uttamaṃ varaṃ</span></p><p class="p3"><span class="t5">Hitaṃ deva-manussānaṃ</span></p><p class="p3"><span class="t5">Buddha-tejena sotthinā</span></p><p class="p3"><span class="t5">Nassant&#8217;upaddavā sabbe</span></p><p class="p3"><span class="t5">Dukkhā vūpasamentu te</span></p><p class="p3"><span class="t1">Having revered the jewel of the Buddha,</span></p><p class="p3"><span class="t1">The highest, most excellent medicine,</span></p><p class="p3"><span class="t1">The welfare of human &amp; heavenly beings:</span></p><p class="p3"><span class="t1">Through the Buddha&#8217;s might &amp; safety</span></p><p class="p3"><span class="t1">May all obstacles vanish,</span></p><p class="p3"><span class="t1">May your sufferings grow totally calm.</span></p><p class="p3"><span class="t5">Sakkatvā dhamma-ratanaṃ</span></p><p class="p3"><span class="t5">Osadhaṃ uttamaṃ varaṃ</span></p><p class="p3"><span class="t5">Pariḷāh&#8217;ūpasamanaṃ</span></p><p class="p3"><span class="t5">Dhamma-tejena sotthinā</span></p><p class="p3"><span class="t5">Nassant&#8217;upaddavā sabbe</span></p><p class="p3"><span class="t5">Bhayā vūpasamentu te</span></p><p class="p3"><span class="t1">Having revered the jewel of the Dhamma,</span></p><p class="p3"><span class="t1">The highest, most excellent medicine,</span></p><p class="p3"><span class="t1">The stiller of feverish passion:</span></p><p class="p3"><span class="t1">Through the Dhamma&#8217;s might &amp; safety</span></p><p class="p3"><span class="t1">May all obstacles vanish,</span></p><p class="p3"><span class="t1">May your fears grow totally calm.</span></p><p class="p3"><span class="t5">Sakkatvā saṅgha-ratanaṃ</span></p><p class="p3"><span class="t5">Osadhaṃ uttamaṃ varaṃ</span></p><p class="p3"><span class="t5">Āhuneyyaṃ pāhuneyyaṃ</span></p><p class="p3"><span class="t5">Saṅgha-tejena sotthinā</span></p><p class="p3"><span class="t5">Nassant&#8217;upaddavā sabbe</span></p><p class="p3"><span class="t5">Rogā vūpasamentu te</span></p><p class="p3"><span class="t1">Having revered the jewel of the Saṅgha,</span></p><p class="p3"><span class="t1">The highest, most excellent medicine,</span></p><p class="p3"><span class="t1">Worthy of gifts, worthy of hospitality:</span></p><p class="p3"><span class="t1">Through the Saṅgha&#8217;s might &amp; safety</span></p><p class="p3"><span class="t1">May all obstacles vanish,</span></p><p class="p3"><span class="t1">May your diseases grow totally calm.</span></p><h2><p class="p3"><span class="t5">[MJG]</span></p></h2><h2>Bojjh&#8217;aṅga-parittaṃ</h2><p class="p3"><span class="t5">Bojjh&#8217;aṅgo sati-saṅkhāto</span></p><p class="p3"><span class="t5">Dhammānaṃ vicayo tathā</span></p><p class="p3"><span class="t5">Viriyam&#8217;pīti-passaddhi</span></p><p class="p3"><span class="t5">Bojjh&#8217;aṅgā ca tathā&#8217;pare</span></p><p class="p3"><span class="t5">Samādh&#8217;upekkha-bojjh&#8217;aṅgā</span></p><p class="p3"><span class="t5">Satt&#8217;ete sabba-dassinā</span></p><p class="p3"><span class="t5">Muninā samma&#8217;d&#8217;akkhātā</span></p><p class="p3"><span class="t5">Bhāvitā bahulī-katā</span></p><p class="p3"><span class="t5">Saṃvattanti abhiññāya</span></p><p class="p3"><span class="t5">Nibbānāya ca bodhiyā</span></p><p class="p3"><span class="t5">Etena sacca-vajjena</span></p><p class="p3"><span class="t5">Sotthi te hotu sabbadā</span></p><p class="p3"><span class="t1">The Factors for Awakening include mindfulness,</span></p><p class="p3"><span class="t1">Investigation of qualities,</span></p><p class="p3"><span class="t1">Persistence, rapture, &amp; serenity,</span></p><p class="p3"><span class="t1">Plus concentration &amp; equanimity factors for Awakening.</span></p><p class="p3"><span class="t1">These seven, which the All-seeing Sage has perfectly taught,</span></p><p class="p3"><span class="t1">When developed &amp; matured bring about heightened knowledge, Liberation, &amp; awakening.</span></p><p class="p3"><span class="t1">By the saying of this truth,</span></p><p class="p3"><span class="t1">May you always be well.</span></p><p class="p3"><span class="t5">Ekasmiṃ samaye nātho</span></p><p class="p3"><span class="t5">Moggallānañ&#8217;ca kassapaṃ</span></p><p class="p3"><span class="t5">Gilāne dukkhite disvā</span></p><p class="p3"><span class="t5">Bojjh&#8217;aṅge satta desayi</span></p><p class="p3"><span class="t5">Te ca taṃ abhinanditvā</span></p><p class="p3"><span class="t5">Rogā mucciṃsu taṅ&#8217;khaṇe</span></p><p class="p3"><span class="t5">Etena sacca-vajjena</span></p><p class="p3"><span class="t5">Sotthi te hotu sabbadā</span></p><p class="p3"><span class="t1">At one time, our Protector seeing that Moggallana &amp; Kassapa</span></p><p class="p3"><span class="t1">Were sick &amp; in pain, taught them the seven factors for Awakening.</span></p><p class="p3"><span class="t1">They, delighting in that, were instantly freed from their illness.</span></p><p class="p3"><span class="t1">By the saying of this truth, may you always be well.</span></p><p class="p3"><span class="t5">Ekadā dhamma-rājā&#8217;pi</span></p><p class="p3"><span class="t5">Gelaññen&#8217;ābhipīḷito</span></p><p class="p3"><span class="t5">Cundattherena taññ&#8217;eva</span></p><p class="p3"><span class="t5">Bhaṇāpetvāna sādaraṃ</span></p><p class="p3"><span class="t5">Sammoditvā ca ābādhā</span></p><p class="p3"><span class="t5">Tamhā vuṭṭhāsi ṭhānaso</span></p><p class="p3"><span class="t5">Etena sacca-vajjena</span></p><p class="p3"><span class="t5">Sotthi te hotu sabbadā</span></p><p class="p3"><span class="t1">Once, when the Dhamma King was afflicted with fever,</span></p><p class="p3"><span class="t1">He had the Elder Cunda recite that very teaching with devotion.</span></p><p class="p3"><span class="t1">And as he approved, he rose up from that disease.</span></p><p class="p3"><span class="t1">By the saying of this truth, may you always be well.</span></p><p class="p3"><span class="t5">Pahīnā te ca ābādhā</span></p><p class="p3"><span class="t5">Tiṇṇannam&#8217;pi mah&#8217;esinaṃ</span></p><p class="p3"><span class="t5">Magg&#8217;āhata-kilesā va</span></p><p class="p3"><span class="t5">Patt&#8217;ānuppatti-dhammataṃ</span></p><p class="p3"><span class="t5">Etena sacca-vajjena</span></p><p class="p3"><span class="t5">Sotthi te hotu sabbadā</span></p><p class="p3"><span class="t1">Those diseases were abandoned by the three great seers,</span></p><p class="p3"><span class="t1">Just as defilements are demolished by the Path</span></p><p class="p3"><span class="t1">In accordance with step-by-step attainment.</span></p><p class="p3"><span class="t1">By the saying of this truth, may you always be well.</span></p><h2><p class="p3"><span class="t5">[Thai]</span></p></h2><h2>Abhaya-parittaṃ</h2><p class="p3"><span class="t5">Yan&#8217;dunnimittaṃ avamaṅgalañ&#8217;ca</span></p><p class="p3"><span class="t5">Yo c&#8217;āmanāpo sakuṇassa saddo</span></p><p class="p3"><span class="t5">Pāpaggaho dussupinaṃ akantaṃ</span></p><p class="p3"><span class="t5">Buddh&#8217;ānubhāvena vināsam&#8217;entu</span></p><p class="p3"><span class="t1">May bad omens, inauspiciousness,</span></p><p class="p3"><span class="t1">Undesirable sounds of birds,</span></p><p class="p3"><span class="t1">Unlucky planets and unpleasant bad dreams</span></p><p class="p3"><span class="t1">Go to ruin by the power of the Buddha.</span></p><p class="p3"><span class="t5">Yan&#8217;dunnimittaṃ avamaṅgalañ&#8217;ca</span></p><p class="p3"><span class="t5">Yo c&#8217;āmanāpo sakuṇassa saddo</span></p><p class="p3"><span class="t5">Pāpaggaho dussupinaṃ akantaṃ</span></p><p class="p3"><span class="t5">Dhamm&#8217;ānubhāvena vināsam&#8217;entu</span></p><p class="p3"><span class="t1">May bad omens, inauspiciousness,</span></p><p class="p3"><span class="t1">Undesirable sounds of birds,</span></p><p class="p3"><span class="t1">Unlucky planets and unpleasant bad dreams</span></p><p class="p3"><span class="t1">Go to ruin by the power of the Dhamma.</span></p><p class="p3"><span class="t5">Yan&#8217;dunnimittaṃ avamaṅgalañ&#8217;ca</span></p><p class="p3"><span class="t5">Yo c&#8217;āmanāpo sakuṇassa saddo</span></p><p class="p3"><span class="t5">Pāpaggaho dussupinaṃ akantaṃ</span></p><p class="p3"><span class="t5">Saṅgh&#8217;ānubhāvena vināsam&#8217;entu</span></p><p class="p3"><span class="t1">May bad omens, inauspiciousness,</span></p><p class="p3"><span class="t1">Undesirable sounds of birds,</span></p><p class="p3"><span class="t1">Unlucky planets and unpleasant bad dreams</span></p><p class="p3"><span class="t1">Go to ruin by the power of the Saṅgha.</span></p><h2><p class="p3"><span class="t5">[Trad]</span></p></h2><h2>Devatā-uyyojana-gāthā</h2><p class="p3"><span class="t5">Dukkhappattā ca niddukkhā</span></p><p class="p3"><span class="t5">Bhayappattā ca nibbhayā</span></p><p class="p3"><span class="t5">Sokappattā ca nissokā</span></p><p class="p3"><span class="t5">Hontu sabbe&#8217;pi pāṇino</span></p><p class="p3"><span class="t1">[May] sufferers be without suffering,</span></p><p class="p3"><span class="t1">The fear-struck be without fear,</span></p><p class="p3"><span class="t1">The grief-stricken be without grief</span></p><p class="p3"><span class="t1">[Thus] may all beings be.</span></p><p class="p3"><span class="t5">Ettāvatā ca amhehi</span></p><p class="p3"><span class="t5">Sambhataṃ puñña-sampadaṃ</span></p><p class="p3"><span class="t5">Sabbe devā anumodantu</span></p><p class="p3"><span class="t5">Sabba-sampatti-siddhiyā</span></p><p class="p3"><span class="t1">To the extent that all of us</span></p><p class="p3"><span class="t1">Have accumulated a wealth of merits;</span></p><p class="p3"><span class="t1">In this may all devas rejoice,</span></p><p class="p3"><span class="t1">For the attainment of all fortunes.</span></p><p class="p3"><span class="t5">Dānaṃ dadantu saddhāya</span></p><p class="p3"><span class="t5">Sīlaṃ rakkhantu sabbadā</span></p><p class="p3"><span class="t5">Bhāvan&#8217;ābhiratā hontu</span></p><p class="p3"><span class="t5">Gacchantu devatā-gatā</span></p><p class="p3"><span class="t1">May they give gifts with faith.</span></p><p class="p3"><span class="t1">May they guard moral precepts always.</span></p><p class="p3"><span class="t1">May they delight in mind-development.</span></p><p class="p3"><span class="t1">May the deities who have come go [back].</span></p><p class="p3"><span class="t5">Sabbe buddhā balappattā</span></p><p class="p3"><span class="t5">Paccekānañ&#8217;ca yaṃ balaṃ</span></p><p class="p3"><span class="t5">Arahantānañ&#8217;ca tejena</span></p><p class="p3"><span class="t5">Rakkhaṃ bandhāmi sabbaso</span></p><p class="p3"><span class="t1">All Buddhas possess [supernormal] strength.</span></p><p class="p3"><span class="t1">And [there is] the power of Paccekabuddhas.</span></p><p class="p3"><span class="t1">By the power of the arahants too,</span></p><p class="p3"><span class="t1">I create protection for all times.</span></p><p class="p3"><span class="t5">[MJG]</span></p>', '-46-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Jaya-maṅgala-aṭṭha-gāthā', 'title', 47);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 47, '<h2>Jaya-maṅgala-aṭṭha-gāthā</h2><p class="p3"><span class="t5">Bāhuṃ sahassam&#8217;abhinimmita sāvudhan&#8217;taṃ</span></p><p class="p3"><span class="t5">Grīmekhalaṃ udita-ghora-sasena-māraṃ</span></p><p class="p3"><span class="t5">Dān&#8217;ādi-dhamma-vidhinā jitavā mun&#8217;indo</span></p><p class="p3"><span class="t5">Tan&#8217;tejasā bhavatu te jaya-maṅgalāni</span></p><p class="p3"><span class="t1">Creating a form with 1,000 arms, each equipped with a weapon,</span></p><p class="p3"><span class="t1">Māra, on the elephant Girimekhala, uttered a frightening roar together with his troops.</span></p><p class="p3"><span class="t1">The Lord of Sages defeated him by means of such qualities as generosity:</span></p><p class="p3"><span class="t1">By the power of this, may you have victory blessings.</span></p><p class="p3"><span class="t5">Mār&#8217;ātirekam&#8217;abhiyujjhita-sabba-rattiṃ</span></p><p class="p3"><span class="t5">Ghoram&#8217;pan&#8217;āḷavakam&#8217;akkhama-thaddha-yakkhaṃ</span></p><p class="p3"><span class="t5">Khantī-sudanta-vidhinā jitavā mun&#8217;indo</span></p><p class="p3"><span class="t5">Tan&#8217;tejasā bhavatu te jaya-maṅgalāni</span></p><p class="p3"><span class="t1">Even more frightful than Māra making war all night</span></p><p class="p3"><span class="t1">Was Āḷavaka, the arrogant unstable ogre.</span></p><p class="p3"><span class="t1">The Lord of Sages defeated him by means of well-trained endurance:</span></p><p class="p3"><span class="t1">By the power of this, may you have victory blessings.</span></p><p class="p3"><span class="t5">Nāḷāgiriṃ gaja-varaṃ atimatta-bhūtaṃ</span></p><p class="p3"><span class="t5">Dāv&#8217;aggi-cakkam&#8217;asanīva sudāruṇan&#8217;taṃ</span></p><p class="p3"><span class="t5">Mett&#8217;ambu-seka-vidhinā jitavā mun&#8217;indo</span></p><p class="p3"><span class="t5">Tan&#8217;tejasā bhavatu te jaya-maṅgalāni</span></p><p class="p3"><span class="t1">Nāḷāgiri, the excellent elephant, when maddened,</span></p><p class="p3"><span class="t1">Was very horrific, like a forest fire, a flaming discus, a lightning bolt.</span></p><p class="p3"><span class="t1">The Lord of Sages defeated him by sprinkling the water of good will:</span></p><p class="p3"><span class="t1">By the power of this, may you have victory blessings.</span></p><p class="p3"><span class="t5">Ukkhitta-khaggam&#8217;atihattha-sudāruṇan&#8217;taṃ</span></p><p class="p3"><span class="t5">Dhāvan&#8217;ti-yojana-path&#8217;aṅguli-mālavantaṃ</span></p><p class="p3"><span class="t5">Iddhī&#8217;bhisaṅkhata-mano jitavā mun&#8217;indo</span></p><p class="p3"><span class="t5">Tan&#8217;tejasā bhavatu te jaya-maṅgalāni</span></p><p class="p3"><span class="t1">Very horrific, with a sword upraised in his expert hand,</span></p><p class="p3"><span class="t1">Garlanded-with-Fingers ran three leagues along the path.</span></p><p class="p3"><span class="t1">The Lord of Sages defeated him with mind-fashioned marvels:</span></p><p class="p3"><span class="t1">By the power of this, may you have victory blessings.</span></p><p class="p3"><span class="t5">Katvāna kaṭṭham&#8217;udaraṃ iva gabbhinīyā</span></p><p class="p3"><span class="t5">Ciñcāya duṭṭha-vacanaṃ jana-kāya majjhe</span></p><p class="p3"><span class="t5">Santena soma-vidhinā jitavā mun&#8217;indo</span></p><p class="p3"><span class="t5">Tan&#8217;tejasā bhavatu te jaya-maṅgalāni</span></p><p class="p3"><span class="t1">Having made a wooden belly to appear pregnant,</span></p><p class="p3"><span class="t1">Ciñca made a lewd accusation in the midst of the gathering.</span></p><p class="p3"><span class="t1">The Lord of Sages defeated her with peaceful, gracious means:</span></p><p class="p3"><span class="t1">By the power of this, may you have victory blessings.</span></p><p class="p3"><span class="t5">Saccaṃ vihāya mati-saccaka-vāda-ketuṃ</span></p><p class="p3"><span class="t5">Vād&#8217;ābhiropita-manaṃ ati-andha-bhūtaṃ</span></p><p class="p3"><span class="t5">Paññā-padīpa-jalito jitavā mun&#8217;indo</span></p><p class="p3"><span class="t5">Tan&#8217;tejasā bhavatu te jaya-maṅgalāni</span></p><p class="p3"><span class="t1">Saccaka, whose provocative views had abandoned the truth,</span></p><p class="p3"><span class="t1">Delighting in argument, had become thoroughly blind.</span></p><p class="p3"><span class="t1">The Lord of Sages defeated him with the light of discernment:</span></p><p class="p3"><span class="t1">By the power of this, may you have victory blessings.</span></p><p class="p3"><span class="t5">Nandopananda-bhujagaṃ vibudhaṃ mah&#8217;iddhiṃ</span></p><p class="p3"><span class="t5">Puttena thera-bhujagena dam&#8217;āpayanto</span></p><p class="p3"><span class="t5">Iddh&#8217;ūpadesa-vidhinā jitavā mun&#8217;indo</span></p><p class="p3"><span class="t5">Tan&#8217;tejasā bhavatu te jaya-maṅgalāni</span></p><p class="p3"><span class="t1">Nandopananda was a serpent with great power but wrong views.</span></p><p class="p3"><span class="t1">The Lord of Sages defeated him by means of a display of marvels,</span></p><p class="p3"><span class="t1">Sending his son, the serpent-elder, to tame him:</span></p><p class="p3"><span class="t1">By the power of this, may you have victory blessings.</span></p><p class="p3"><span class="t5">Duggāha-diṭṭhi-bhujagena sudaṭṭha-hatthaṃ</span></p><p class="p3"><span class="t5">Brahmaṃ visuddhi-jutim&#8217;iddhi-bak&#8217;ābhidhānaṃ</span></p><p class="p3"><span class="t5">Ñāṇāgadena vidhinā jitavā mun&#8217;indo</span></p><p class="p3"><span class="t5">Tan&#8217;tejasā bhavatu te jaya-maṅgalāni</span></p><p class="p3"><span class="t1">His hands bound tight by the serpent of wrongly held views,</span></p><p class="p3"><span class="t1">Baka, the Brahma, thought himself pure in his radiance &amp; power.</span></p><p class="p3"><span class="t1">The Lord of Sages defeated him by means of his words of knowledge:</span></p><p class="p3"><span class="t1">By the power of this, may you have victory blessings.</span></p><p class="p3"><span class="t5">Etā&#8217;pi buddha-jaya-maṅgala-aṭṭha-gāthā</span></p><p class="p3"><span class="t5">Yo vācano dina-dine sarate&#8217;m&#8217;atandī</span></p><p class="p3"><span class="t5">Hitvān&#8217;aneka-vividhāni c&#8217;upaddavāni</span></p><p class="p3"><span class="t5">Mokkhaṃ sukhaṃ adhigameyya naro sapañño</span></p><p class="p3"><span class="t1">These eight verses of the Buddha&#8217;s victory blessings:</span></p><p class="p3"><span class="t1">Whatever person of discernment recites or recalls them day after day without lapsing,</span></p><p class="p3"><span class="t1">Destroying all kinds of obstacles,</span></p><p class="p3"><span class="t1">Will attain emancipation &amp; happiness.</span></p><h2><p class="p3"><span class="t5">[Trad]</span></p></h2><h2>Jaya-parittaṃ</h2><p class="p3"><span class="t5">Mahā-kāruṇiko nātho</span></p><p class="p3"><span class="t5">Hitāya sabba-pāṇinaṃ</span></p><p class="p3"><span class="t5">Pūretvā pāramī sabbā</span></p><p class="p3"><span class="t5">Patto sambodhim&#8217;uttamaṃ</span></p><p class="p3"><span class="t5">Etena sacca-vajjena</span></p><p class="p3"><span class="t5">Hotu te jaya-maṅgalaṃ</span></p><p class="p3"><span class="t1">The Great Compassionate Protector,</span></p><p class="p3"><span class="t1">For the benefit of all beings,</span></p><p class="p3"><span class="t1">Fulfilled all the Perfections</span></p><p class="p3"><span class="t1">And reached the highest Awakening.</span></p><p class="p3"><span class="t1">By this utterance of truth,</span></p><p class="p3"><span class="t1">May there be well-being for you always.</span></p><p class="p3"><span class="t5">Jayanto bodhiyā mūle</span></p><p class="p3"><span class="t5">Sakyānaṃ nandi-vaḍḍhano</span></p><p class="p3"><span class="t5">Evaṃ tvaṃ vijayo hohi</span></p><p class="p3"><span class="t5">Jayassu jaya-maṅgale</span></p><p class="p3"><span class="t1">Victorious at the foot of the bodhi tree</span></p><p class="p3"><span class="t1">Was the joy-enhancer of the Sakyas.</span></p><p class="p3"><span class="t1">Even so, may there be victory.</span></p><p class="p3"><span class="t1">May you attain auspicious victory.</span></p><p class="p3"><span class="t5">Aparājita-pallaṅke</span></p><p class="p3"><span class="t5">Sīse paṭhavi-pokkhare</span></p><p class="p3"><span class="t5">Abhiseke sabba-buddhānaṃ</span></p><p class="p3"><span class="t5">Aggappatto pamodati</span></p><p class="p3"><span class="t1">At the invincible seat,</span></p><p class="p3"><span class="t1">The best on Earth,</span></p><p class="p3"><span class="t1">The consecration place of all Buddhas,</span></p><p class="p3"><span class="t1">Having reached the highest, he rejoices.</span></p><h2><p class="p3"><span class="t5">[MJG]</span></p></h2><p class="p3"><span class="t5">Sunakkhattaṃ sumaṅgalaṃ</span></p><p class="p3"><span class="t5">Supabhātaṃ suhuṭṭhitaṃ</span></p><p class="p3"><span class="t5">Sukhaṇo sumuhutto ca</span></p><p class="p3"><span class="t5">Suyiṭṭhaṃ brahmacārisu</span></p><p class="p3"><span class="t1">Good constellations, good blessings,</span></p><p class="p3"><span class="t1">Good daybreak, good waking,</span></p><p class="p3"><span class="t1">Good moment and good time</span></p><p class="p3"><span class="t1">When offerings are made to holy practitioners.</span></p><p class="p3"><span class="t5">Padakkhiṇaṃ kāya-kammaṃ</span></p><p class="p3"><span class="t5">Vācā-kammaṃ padakkhiṇaṃ</span></p><p class="p3"><span class="t5">Padakkhiṇaṃ mano-kammaṃ</span></p><p class="p3"><span class="t5">Paṇīdhi te padakkhiṇe</span></p><p class="p3"><span class="t5">Padakkhiṇāni katvāna</span></p><p class="p3"><span class="t5">Labhant&#8217;atthe padakkhiṇe</span></p><p class="p3"><span class="t1">Felicitous is bodily kamma</span></p><p class="p3"><span class="t1">Verbal kamma is felicitous</span></p><p class="p3"><span class="t1">Felicitous is mental kamma</span></p><p class="p3"><span class="t1">When aspiring for felicity.</span></p><p class="p3"><span class="t1">Having done the felicitous</span></p><p class="p3"><span class="t1">They get felicitous rewards</span></p><h2><p class="p3"><span class="t5">[AN 3.155]</span></p></h2><h2>Bhavatu-sabba-maṅgalaṃ</h2><p class="p3"><span class="t5">Bhavatu sabba-maṅgalaṃ</span></p><p class="p3"><span class="t5">Rakkhantu sabba-devatā</span></p><p class="p3"><span class="t5">Sabba-buddh&#8217;ānubhāvena</span></p><p class="p3"><span class="t5">Sadā sotthī bhavantu te</span></p><p class="p3"><span class="t1">May every blessing come to be.</span></p><p class="p3"><span class="t1">And all good spirits guard you well.</span></p><p class="p3"><span class="t1">Through the power of all Buddhas,</span></p><p class="p3"><span class="t1">May you always be at ease.</span></p><p class="p3"><span class="t5">Bhavatu sabba-maṅgalaṃ</span></p><p class="p3"><span class="t5">Rakkhantu sabba-devatā</span></p><p class="p3"><span class="t5">Sabba-dhamm&#8217;ānubhāvena</span></p><p class="p3"><span class="t5">Sadā sotthī bhavantu te</span></p><p class="p3"><span class="t1">May every blessing come to be.</span></p><p class="p3"><span class="t1">And all good spirits guard you well.</span></p><p class="p3"><span class="t1">Through the power of all Dhammas,</span></p><p class="p3"><span class="t1">May you always be at ease.</span></p><p class="p3"><span class="t5">Bhavatu sabba-maṅgalaṃ</span></p><p class="p3"><span class="t5">Rakkhantu sabba-devatā</span></p><p class="p3"><span class="t5">Sabba-saṅgh&#8217;ānubhāvena</span></p><p class="p3"><span class="t5">Sadā sotthī bhavantu te</span></p><p class="p3"><span class="t1">May every blessing come to be.</span></p><p class="p3"><span class="t1">And all good spirits guard you well.</span></p><p class="p3"><span class="t1">Through the power of all Sanghas,</span></p><p class="p3"><span class="t1">May you always be at ease.</span></p><h2><p class="p3"><span class="t5">[Trad]</span></p></h2>', '-47-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Funeral Recitations', 'chapter', 48);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Pubba-bhāga-nama-kāra-pāṭho', 'title', 48);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 48, '<h2>Pubba-bhāga-nama-kāra-pāṭho</h2><p class="p3"><span class="t5"></span></p><p class="p3"><span class="t5">Namo tassa bhagavato arahato sammāsambuddhassa</span></p><p class="p3"><span class="t1">Homage to the Blessed, Worthy, and Perfectly Enlightened One</span></p><h2><p class="p3"><span class="t5">[DN 21]</span></p></h2><h2>Dhamma-saṅgaṇī-mātikā</h2><p class="p3"><span class="t5">Kusalā dhammā</span></p><p class="p3"><span class="t5">Akusalā dhammā</span></p><p class="p3"><span class="t5">Abyākatā dhammā</span></p><p class="p3"><span class="t1">Wholesome dhammas</span></p><p class="p3"><span class="t1">Unwholesome dhammas</span></p><p class="p3"><span class="t1">Undetermined dhammas</span></p><p class="p3"><span class="t5">Sukhāya vedanāya sampayuttā dhammā</span></p><p class="p3"><span class="t5">Dukkhāya vedanāya sampayuttā dhammā</span></p><p class="p3"><span class="t5">Adukkham&#8217;asukhāya vedanāya sampayuttā dhammā</span></p><p class="p3"><span class="t1">Dhammas associated with pleasant feeling</span></p><p class="p3"><span class="t1">Dhammas associated with painful feeling</span></p><p class="p3"><span class="t1">Dhammas associated with neither-painful-nor-pleasant feeling</span></p><p class="p3"><span class="t5">Vipākā dhammā</span></p><p class="p3"><span class="t5">Vipāka-dhamma-dhammā</span></p><p class="p3"><span class="t5">N&#8217;eva vipāka na vipāka-dhamma-dhammā</span></p><p class="p3"><span class="t1">Consequential dhamma</span></p><p class="p3"><span class="t1">Subject to consequential dhamma</span></p><p class="p3"><span class="t1">Neither consequential nor subject to consequential dhamma</span></p><p class="p3"><span class="t5">Upādinn&#8217;upādāniyā dhammā</span></p><p class="p3"><span class="t5">Anupādinn&#8217;upādāniyā dhammā</span></p><p class="p3"><span class="t5">Anupādinn&#8217;ānupādāniyā dhammā</span></p><p class="p3"><span class="t1">Clung dhammas which can be clung to</span></p><p class="p3"><span class="t1">Unclung dhammas which can be clung to</span></p><p class="p3"><span class="t1">Unclung dhammas which cannot be clung to</span></p><p class="p3"><span class="t5">Saṅkiliṭṭha-saṅkilesikā dhammā</span></p><p class="p3"><span class="t5">Asaṅkiliṭṭha-saṅkilesikā dhammā</span></p><p class="p3"><span class="t5">Asaṅkiliṭṭh&#8217;āsaṅkilesikā dhammā</span></p><p class="p3"><span class="t1">Dhammas defiled and subject to defilements</span></p><p class="p3"><span class="t1">Dhammas undefiled but subject to defilements</span></p><p class="p3"><span class="t1">Dhammas neither defiled nor subject to defilements</span></p><p class="p3"><span class="t5">Savitakka-savicārā dhammā</span></p><p class="p3"><span class="t5">Avitakka-vicāra-mattā dhammā</span></p><p class="p3"><span class="t5">Avitakk&#8217;āvicārā dhammā</span></p><p class="p3"><span class="t1">Dhammas with thought and examination</span></p><p class="p3"><span class="t1">Dhammas without thought but with examination</span></p><p class="p3"><span class="t1">Dhammas with neither thought nor examination</span></p><p class="p3"><span class="t5">Pīti-sahagatā dhammā</span></p><p class="p3"><span class="t5">Sukha-sahagatā dhammā</span></p><p class="p3"><span class="t5">Upekkhā-sahagatā dhammā</span></p><p class="p3"><span class="t1">Dhammas accompanied by rapture</span></p><p class="p3"><span class="t1">Dhammas accompanied by pleasure</span></p><p class="p3"><span class="t1">Dhammas accompanied by equanimity</span></p><p class="p3"><span class="t5">Dassanena pahātabbā dhammā</span></p><p class="p3"><span class="t5">Bhāvanāya pahātabbā dhammā</span></p><p class="p3"><span class="t5">N&#8217;eva dassanena na bhāvanāya pahātabbā dhammā</span></p><p class="p3"><span class="t1">Dhammas abandoned by seeing</span></p><p class="p3"><span class="t1">Dhammas abandoned by development</span></p><p class="p3"><span class="t1">Dhammas abandoned by neither seeing nor development</span></p><p class="p3"><span class="t5">Dassanena pahātabba-hetukā dhammā</span></p><p class="p3"><span class="t5">Bhāvanāya pahātabba-hetukā dhammā</span></p><p class="p3"><span class="t5">N&#8217;eva dassanena na bhāvanāya pahātabba-hetukā dhammā</span></p><p class="p3"><span class="t1">Conditioned dhammas abandoned by seeing</span></p><p class="p3"><span class="t1">Conditioned dhammas abandoned by development</span></p><p class="p3"><span class="t1">Conditioned dhammas abandoned by neither seeing nor development</span></p><p class="p3"><span class="t5">Ācaya-gāmino dhammā</span></p><p class="p3"><span class="t5">Apacaya-gāmino dhammā</span></p><p class="p3"><span class="t5">N&#8217;ev&#8217;ācaya-gāmino n&#8217;āpacaya-gāmino dhammā</span></p><p class="p3"><span class="t1">Dhammas leading to building up</span></p><p class="p3"><span class="t1">Dhamma leading to dismantling</span></p><p class="p3"><span class="t1">Dhammas leading to neither building up nor dismantling</span></p><p class="p3"><span class="t5">Sekkhā dhammā</span></p><p class="p3"><span class="t5">Asekkhā dhammā</span></p><p class="p3"><span class="t5">N&#8217;eva sekkhā n&#8217;āsekkhā dhammā</span></p><p class="p3"><span class="t1">Dhammas of one in training</span></p><p class="p3"><span class="t1">Dhammas of one beyond training</span></p><p class="p3"><span class="t1">Dhammas of neither one in training nor one beyond training</span></p><p class="p3"><span class="t5">Parittā dhammā</span></p><p class="p3"><span class="t5">Mahaggatā dhammā</span></p><p class="p3"><span class="t5">Appamāṇā dhammā</span></p><p class="p3"><span class="t1">Limited dhammas</span></p><p class="p3"><span class="t1">Exhalted dhammas</span></p><p class="p3"><span class="t1">Immeasurable dhammas</span></p><p class="p3"><span class="t5">Paritt&#8217;ārammaṇā dhammā</span></p><p class="p3"><span class="t5">Mahaggat&#8217;ārammaṇā dhammā</span></p><p class="p3"><span class="t5">Appamāṇ&#8217;ārammaṇā dhammā</span></p><p class="p3"><span class="t1">Dhammas from limited sense-obejcts</span></p><p class="p3"><span class="t1">Dhammas from exhalted sense-objects</span></p><p class="p3"><span class="t1">Dhammas from immeasurable sense-objects</span></p><p class="p3"><span class="t5">Hīnā dhammā</span></p><p class="p3"><span class="t5">Majjhimā dhammā</span></p><p class="p3"><span class="t5">Paṇītā dhammā</span></p><p class="p3"><span class="t1">Inferior dhammas</span></p><p class="p3"><span class="t1">Moderate dhammas</span></p><p class="p3"><span class="t1">Superior dhammas</span></p><p class="p3"><span class="t5">Micchatta-niyatā dhammā</span></p><p class="p3"><span class="t5">Sammatta-niyatā dhammā</span></p><p class="p3"><span class="t5">Aniyatā dhammā</span></p><p class="p3"><span class="t1">Certain wrong dhammas</span></p><p class="p3"><span class="t1">Certain correct dhammas</span></p><p class="p3"><span class="t1">Uncertain dhammas</span></p><p class="p3"><span class="t5">Magg&#8217;ārammaṇā dhammā</span></p><p class="p3"><span class="t5">Magga-hetukā dhammā</span></p><p class="p3"><span class="t5">Magg&#8217;ādhipatino dhammā</span></p><p class="p3"><span class="t1">Dhammas with the path as object</span></p><p class="p3"><span class="t1">Dhammas with the path as cause</span></p><p class="p3"><span class="t1">Dhammas with the path as predominant factor</span></p><p class="p3"><span class="t5">Uppannā dhammā</span></p><p class="p3"><span class="t5">Anuppannā dhammā</span></p><p class="p3"><span class="t5">Uppādino dhammā</span></p><p class="p3"><span class="t1">Arisen dhammas</span></p><p class="p3"><span class="t1">Unarisen dhammas</span></p><p class="p3"><span class="t1">Bound to arise dhammas</span></p><p class="p3"><span class="t5">Atītā dhammā</span></p><p class="p3"><span class="t5">Anāgatā dhammā</span></p><p class="p3"><span class="t5">Paccuppannā dhammā</span></p><p class="p3"><span class="t1">Past dhammas</span></p><p class="p3"><span class="t1">Future dhammas</span></p><p class="p3"><span class="t1">Present dhammas</span></p><p class="p3"><span class="t5">Atīt&#8217;ārammaṇā dhammā</span></p><p class="p3"><span class="t5">Anāgat&#8217;ārammaṇā dhammā</span></p><p class="p3"><span class="t5">Paccuppann&#8217;ārammaṇā dhammā</span></p><p class="p3"><span class="t1">Dhammas with past sense-objects</span></p><p class="p3"><span class="t1">Dhammas with future sense-objects</span></p><p class="p3"><span class="t1">Dhammas with present sense-objects</span></p><p class="p3"><span class="t5">Ajjhattā dhammā</span></p><p class="p3"><span class="t5">Bahiddhā dhammā</span></p><p class="p3"><span class="t5">Ajjhatta-bahiddhā dhammā</span></p><p class="p3"><span class="t1">Internal dhammas</span></p><p class="p3"><span class="t1">External dhammas</span></p><p class="p3"><span class="t1">Internal and external dhamams</span></p><p class="p3"><span class="t5">Ajjhatt&#8217;ārammaṇā dhammā</span></p><p class="p3"><span class="t5">Bahiddh&#8217;ārammaṇā dhammā</span></p><p class="p3"><span class="t5">Ajjhatta-bahiddh&#8217;ārammaṇā dhammā</span></p><p class="p3"><span class="t1">Dhammas with internal sense-objects</span></p><p class="p3"><span class="t1">Dhammas with external sense-objects</span></p><p class="p3"><span class="t1">Dhammas with internal and external sense-objects</span></p><p class="p3"><span class="t5">Sanidassana-sappaṭighā dhammā</span></p><p class="p3"><span class="t5">Anidassana-sappaṭighā dhammā</span></p><p class="p3"><span class="t5">Anidassan&#8217;āppaṭighā dhammā</span></p><p class="p3"><span class="t1">Visible and impinging dhammas</span></p><p class="p3"><span class="t1">Non-visible and impinging dhammas</span></p><p class="p3"><span class="t1">Non-visible and unimpinging dhammas</span></p><h2><p class="p3"><span class="t5">[Dhs 1]</span></p></h2><h2>Vipassanā-bhūmi-pāṭho</h2><p class="p3"><span class="t5">Pañca-kkhandhā:</span></p><p class="p3"><span class="t5">Rūpa-kkhandho vedanā-kkhandho saññā-kkhandho saṅkhāra-kkhandho viññāṇa-kkhandho</span></p><p class="p3"><span class="t1">Five aggregates:</span></p><p class="p3"><span class="t1">The aggregate of form, the aggregate of feeling, the aggregate of perception, the aggregate of volitional formations, the aggregate of consciousness.</span></p><h2><p class="p3"><span class="t5">[MN 109]</span></p></h2><p class="p3"><span class="t5">Dvā-das&#8217;āyatanāni:</span></p><p class="p3"><span class="t5">Cakkhv&#8217;āyatanaṃ rūp&#8217;āyatanaṃ sot&#8217;āyatanaṃ sadd&#8217;āyatanaṃ ghān&#8217;āyatanaṃ gandh&#8217;āyatanaṃ jivh&#8217;āyatanaṃ ras&#8217;āyatanaṃ kāy&#8217;āyatanaṃ phoṭṭhabb&#8217;āyatanaṃ man&#8217;āyatanaṃ dhamm&#8217;āyatanaṃ</span></p><p class="p3"><span class="t1">Twelve sense bases:</span></p><p class="p3"><span class="t1">The eye-base, the form base, the ear-base, the sound-base, the nose-base, the odour-base, the tongue-base, the flavour-base, the body-base, the tangible-base, the mind-base, the mind-object base.</span></p><h2><p class="p3"><span class="t5">[MN 148]</span></p></h2><p class="p3"><span class="t5">Aṭṭhārasa dhātuyo:</span></p><p class="p3"><span class="t5">Cakkhu-dhātu rūpa-dhātu cakkhu-viññāṇa-dhātu sota-dhātu sadda-dhātu sota-viññāṇa-dhātu ghāna-dhātu gandha-dhātu ghāna-viññāṇa-dhātu jivhā-dhātu rasa-dhātu jivhā-viññāṇa-dhātu kāya-dhātu phoṭṭhabba-dhātu kāya-viññāṇa-dhātu mano-dhātu dhamma-dhātu mano-viññāṇa-dhātu</span></p><p class="p3"><span class="t1">Eighteen elements:</span></p><p class="p3"><span class="t1">The eye element, the form element, the eye-consciousness element; The ear element, the sound element, the ear-consciousness element; The nose element, the odour element, the nose-consciousness element; The tongue element, the flavour element, the tongue-consciousness element; The body element, the tangible element, the body-consciousness element; The mind element, the mind-object element, the mind-consciousness element.</span></p><h2><p class="p3"><span class="t5">[MN 115]</span></p></h2><p class="p3"><span class="t5">Bā-vīsat&#8217;indriyāni:</span></p><p class="p3"><span class="t5">Cakkhu&#8217;ndriyaṃ sot&#8217;indriyaṃ ghān&#8217;indriyaṃ jivh&#8217;indriyaṃ kāy&#8217;indriyaṃ man&#8217;indriyaṃ itth&#8217;indriyaṃ puris&#8217;indriyaṃ jīvit&#8217;indriyaṃ sukh&#8217;indriyaṃ dukkh&#8217;indriyaṃ somanass&#8217;indriyaṃ domanass&#8217;indriyaṃ upekkh&#8217;indriyaṃ saddh&#8217;indriyaṃ viriy&#8217;indriyaṃ sat&#8217;indriyaṃ samādh&#8217;indriyaṃ paññ&#8217;indriyaṃ anaññātañ&#8217;ñassāmī&#8217;t&#8217;indriyaṃ aññ&#8217;indriyaṃ aññātāv&#8217;indriyaṃ</span></p><p class="p3"><span class="t1">Twenty-two faculties:</span></p><p class="p3"><span class="t1">The eye faculty, ear faculty, nose faculty, tongue faculty, body faculty, mind faculty, faculty of feminity, faculty of masculinity, life faculty, pleasure faculty, pain faculty, happiness faculty, displeasure faculty, equanimity faculty, conviction faculty, energy faculty, mindfulness faculty, concentration faculty, wisdom faculty, the &#8216;I am knowing the unknown&#8217; faculty, knowledge faculty, the faculty of one with complete knowledge.</span></p><h2><p class="p3"><span class="t5">[Vibh]</span></p></h2><p class="p3"><span class="t5">Cattāri ariya-saccāni:</span></p><p class="p3"><span class="t5">Dukkhaṃ ariya-saccaṃ</span></p><p class="p3"><span class="t5">Dukkha-samudayo ariya-saccaṃ</span></p><p class="p3"><span class="t5">Dukkha-nirodho ariya-saccaṃ</span></p><p class="p3"><span class="t5">Dukkha-nirodha-gāminī paṭipadā ariya-saccaṃ</span></p><p class="p3"><span class="t1">The four noble truths:</span></p><p class="p3"><span class="t1">The noble truth of dukkha;</span></p><p class="p3"><span class="t1">The noble truth of the origin of dukkha;</span></p><p class="p3"><span class="t1">The noble truth of the cessation of dukkha;</span></p><p class="p3"><span class="t1">The noble truth of the way leading to the cessation of dukkha.</span></p><h2><p class="p3"><span class="t5">[SN 56.24]</span></p></h2><p class="p3"><span class="t5">Avijjā-paccayā saṅkhārā</span></p><p class="p3"><span class="t5">Saṅkhāra-paccayā viññāṇaṃ</span></p><p class="p3"><span class="t5">Viññāṇa-paccayā nāma-rūpaṃ</span></p><p class="p3"><span class="t5">Nāma-rūpa-paccayā saḷ&#8217;āyatanaṃ</span></p><p class="p3"><span class="t5">Saḷ&#8217;āyatana-paccayā phasso</span></p><p class="p3"><span class="t5">Phassa-paccayā vedanā</span></p><p class="p3"><span class="t5">Vedanā-paccayā taṇhā</span></p><p class="p3"><span class="t5">Taṇhā-paccayā upādānaṃ</span></p><p class="p3"><span class="t5">Upādāna-paccayā bhavo</span></p><p class="p3"><span class="t5">Bhava-paccayā jāti</span></p><p class="p3"><span class="t5">Jāti-paccayā jarā-maraṇaṃ soka-parideva-dukkha-domanass&#8217;upāyāsā sambhavanti</span></p><p class="p3"><span class="t5">Evam&#8217;etassa kevalassa dukkha-kkhandhassa samudayo hoti</span></p><p class="p3"><span class="t1">With ignorance as condition, volitional formations;</span></p><p class="p3"><span class="t1">With volitional formations as condition, consciousness;</span></p><p class="p3"><span class="t1">With consciousness as condition, name-and-form;</span></p><p class="p3"><span class="t1">With name-and-form as condition, the six sense bases;</span></p><p class="p3"><span class="t1">With the six sense bases as condition, contact;</span></p><p class="p3"><span class="t1">With contact as condition, feeling; With feeling as condition, craving;</span></p><p class="p3"><span class="t1">With craving as condition, clinging;</span></p><p class="p3"><span class="t1">With clinging as condition, existence; With existence as condition, birth;</span></p><p class="p3"><span class="t1">With birth as condition, ageing-and-death, sorrow, lamentation, pain, displeasure, and despair come to be.</span></p><p class="p3"><span class="t1">Such is the origin of this whole mass of suffering.</span></p><p class="p3"><span class="t5">Avijjāya tv&#8217;eva asesa-virāga-nirodhā saṅkhāra-nirodho</span></p><p class="p3"><span class="t5">Saṅkhāra-nirodhā viññāṇa-nirodho</span></p><p class="p3"><span class="t5">Viññāṇa-nirodhā nāma-rūpa-nirodho</span></p><p class="p3"><span class="t5">Nāma-rūpa-nirodhā saḷ&#8217;āyatana-nirodho</span></p><p class="p3"><span class="t5">Saḷ&#8217;āyatana-nirodhā phassa-nirodho</span></p><p class="p3"><span class="t5">Phassa-nirodhā vedanā-nirodho</span></p><p class="p3"><span class="t5">Vedanā-nirodhā taṇhā-nirodho</span></p><p class="p3"><span class="t5">Taṇhā-nirodhā upādāna-nirodho</span></p><p class="p3"><span class="t5">Upādāna-nirodhā bhava-nirodho</span></p><p class="p3"><span class="t5">Bhava-nirodhā jāti-nirodho</span></p><p class="p3"><span class="t5">Jāti-nirodhā jarā-maraṇaṃ soka-parideva-dukkha-domanass&#8217;upāyāsā nirujjhanti</span></p><p class="p3"><span class="t5">Evam&#8217;etassa kevalassa dukkha-kkhandhassa nirodho hoti</span></p><p class="p3"><span class="t1">But with the remainderless fading away and cessation of ignorance comes cessation of volitional formations;</span></p><p class="p3"><span class="t1">With the cessation of volitional formations, cessation of consciousness;</span></p><p class="p3"><span class="t1">With the cessation of consciousness, cessation of name-and-form;</span></p><p class="p3"><span class="t1">With the cessation of name-and-form, cessation of the six sense bases;</span></p><p class="p3"><span class="t1">With the cessation of the six sense bases, cessation of contact;</span></p><p class="p3"><span class="t1">With the cessation of contact, cessation of feeling;</span></p><p class="p3"><span class="t1">With the cessation of feeling, cessation of craving;</span></p><p class="p3"><span class="t1">With the cessation of craving, cessation of clinging;</span></p><p class="p3"><span class="t1">With the cessation of clinging, cessation of existence;</span></p><p class="p3"><span class="t1">With the cessation of existence, cessation of birth;</span></p><p class="p3"><span class="t1">With the cessation of birth, ageing-and-death, sorrow, lamentation, pain, displeasure, and despair cease.</span></p><p class="p3"><span class="t1">Such is the cessation of this whole mass of suffering.</span></p><h2><p class="p3"><span class="t5">[SN 12.1]</span></p></h2><h2>Paṭṭhāna-mātikā-pāṭho</h2><p class="p3"><span class="t5">Hetu-paccayo ārammaṇa-paccayo adhipati-paccayo anantara-paccayo samanantara-paccayo saha-jāta-paccayo aññam&#8217;añña-paccayo nissaya-paccayo upanissaya-paccayo pure-jāta-paccayo pacchā-jāta-paccayo āsevana-paccayo kamma-paccayo vipāka-paccayo āhāra-paccayo indriya-paccayo jhāna-paccayo magga-paccayo sampayutta-paccayo vippayutta-paccayo atthi-paccayo n&#8217;atthi-paccayo vigata-paccayo avigata-paccayo</span></p><p class="p3"><span class="t1">Root condition, sense-object condition, predominant condition, immediate condition, directly immediate condition, coexistent condition, reciprocity condition, dependence condition, sufficing condition, pre-existent condition, post-existent condition, repetition condition, action condition, result condition, nutriment condition, faculty condition, jhāna condition, path condition, associated condition, separated condition, existence condition, non-existence condition, disappeared condition, non-dissappeared condition.</span></p><h2><p class="p3"><span class="t5">[Dhs A]</span></p></h2><h2>Adāsi-me ādi gāthā</h2><p class="p3"><span class="t5">Adāsi me akāsi me</span></p><p class="p3"><span class="t5">Ñāti-mittā sakhā ca me</span></p><p class="p3"><span class="t5">Petānaṃ dakkhiṇaṃ dajjā</span></p><p class="p3"><span class="t5">Pubbe katam&#8217;anussaraṃ</span></p><p class="p3"><span class="t1">&#8220;He gave to me [gifts], he did [things] for me.</span></p><p class="p3"><span class="t1">They&#8217;re my relatives, friends and pals&#8221;.</span></p><p class="p3"><span class="t1">To the deceased one should give offerings,</span></p><p class="p3"><span class="t1">Remembering what was done before.</span></p><p class="p3"><span class="t5">Na hi ruṇṇaṃ vā soko vā</span></p><p class="p3"><span class="t5">Yā v&#8217;aññā paridevanā</span></p><p class="p3"><span class="t5">Na taṃ petānam&#8217;atthāya</span></p><p class="p3"><span class="t5">Evaṃ tiṭṭhanti ñātayo</span></p><p class="p3"><span class="t1">For neither weeping nor sorrow,</span></p><p class="p3"><span class="t1">Nor any form of lamentation</span></p><p class="p3"><span class="t1">Benefits the departed ones.</span></p><p class="p3"><span class="t1">Such is how the relatives remain.</span></p><p class="p3"><span class="t5">Ayañ&#8217;ca kho dakkhinā dinnā</span></p><p class="p3"><span class="t5">Saṅghamhi supatiṭṭhitā</span></p><p class="p3"><span class="t5">Dīgharattaṃ hitāy&#8217;assa</span></p><p class="p3"><span class="t5">Ṭhānaso upakappati</span></p><p class="p3"><span class="t1">And this offering that has been given</span></p><p class="p3"><span class="t1">And firmly established in the Saṅgha,</span></p><p class="p3"><span class="t1">Would be for their long-term welfare</span></p><p class="p3"><span class="t1">And arrives there immediately.</span></p><p class="p3"><span class="t5">So ñāti-dhammo ca ayaṃ nidassito</span></p><p class="p3"><span class="t5">Petāna&#8217;pūjā ca katā uḷārā</span></p><p class="p3"><span class="t5">Balañ&#8217;ca bhikkhūnam&#8217;anuppadinnaṃ</span></p><p class="p3"><span class="t5">Tumhehi puññaṃ pasutaṃ anappakan&#8217;ti</span></p><p class="p3"><span class="t1">And the duty of relatives has been shown,</span></p><p class="p3"><span class="t1">And lofty honouring of the departed done;</span></p><p class="p3"><span class="t1">Strength has also been given to the bhikkhus,</span></p><p class="p3"><span class="t1">And much merit accumulated by you all.</span></p><h2><p class="p3"><span class="t5">[Khp 7]</span></p></h2><h2>Paṃsu-kūla for the Dead</h2><p class="p3"><span class="t5">Aniccā vata saṅkhārā</span></p><p class="p3"><span class="t5">Uppāda-vaya-dhammino</span></p><p class="p3"><span class="t5">Uppajjitvā nirujjhanti</span></p><p class="p3"><span class="t5">Tesaṃ vūpasamo sukho</span></p><p class="p3"><span class="t1">Indeed, conditioned things cannot last</span></p><p class="p3"><span class="t1">Their nature is to rise and cease;</span></p><p class="p3"><span class="t1">Having arisen things must cease;</span></p><p class="p3"><span class="t1">Their stilling is true happiness.</span></p><h2><p class="p3"><span class="t5">[DN 16]</span></p></h2><p class="p3"><span class="t5">Sabbe sattā maranti ca</span></p><p class="p3"><span class="t5">Mariṃsu ca marissare</span></p><p class="p3"><span class="t5">Tath&#8217;ev&#8217;āhaṃ marissāmi</span></p><p class="p3"><span class="t5">N&#8217;atthi me ettha saṃsayo</span></p><p class="p3"><span class="t1">All living beings are dying,</span></p><p class="p3"><span class="t1">Have died, and will die.</span></p><p class="p3"><span class="t1">In the same way, I will die,</span></p><p class="p3"><span class="t1">I have no doubt about this.</span></p><h2><p class="p3"><span class="t5">[Thai]</span></p></h2><h2>Paṃsu-kūla for the Living</h2><p class="p3"><span class="t5">Aciraṃ vat&#8217;ayaṃ kāyo</span></p><p class="p3"><span class="t5">Paṭhaviṃ adhisessati</span></p><p class="p3"><span class="t5">Chuḍḍho apeta-viññāṇo</span></p><p class="p3"><span class="t5">Niratthaṃ va kaliṅgaraṃ</span></p><p class="p3"><span class="t1">All too soon, this body</span></p><p class="p3"><span class="t1">Will lie on the ground cast off,</span></p><p class="p3"><span class="t1">Bereft of consciousness,</span></p><p class="p3"><span class="t1">Like a useless scrap of wood.</span></p><h2><p class="p3"><span class="t5">[Dhp 41]</span></p></h2><h2>Bhavatu-sabba-maṅgalaṃ</h2><p class="p3"><span class="t5">Bhavatu sabba-maṅgalaṃ</span></p><p class="p3"><span class="t5">Rakkhantu sabba-devatā</span></p><p class="p3"><span class="t5">Sabba-buddh&#8217;ānubhāvena</span></p><p class="p3"><span class="t5">Sadā sotthī bhavantu te</span></p><p class="p3"><span class="t1">May every blessing come to be.</span></p><p class="p3"><span class="t1">And all good spirits guard you well.</span></p><p class="p3"><span class="t1">Through the power of all Buddhas,</span></p><p class="p3"><span class="t1">May you always be at ease.</span></p><p class="p3"><span class="t5">Bhavatu sabba-maṅgalaṃ</span></p><p class="p3"><span class="t5">Rakkhantu sabba-devatā</span></p><p class="p3"><span class="t5">Sabba-dhamm&#8217;ānubhāvena</span></p><p class="p3"><span class="t5">Sadā sotthī bhavantu te</span></p><p class="p3"><span class="t1">May every blessing come to be.</span></p><p class="p3"><span class="t1">And all good spirits guard you well.</span></p><p class="p3"><span class="t1">Through the power of all Dhammas,</span></p><p class="p3"><span class="t1">May you always be at ease.</span></p><p class="p3"><span class="t5">Bhavatu sabba-maṅgalaṃ</span></p><p class="p3"><span class="t5">Rakkhantu sabba-devatā</span></p><p class="p3"><span class="t5">Sabba-saṅgh&#8217;ānubhāvena</span></p><p class="p3"><span class="t5">Sadā sotthī bhavantu te</span></p><p class="p3"><span class="t1">May every blessing come to be.</span></p><p class="p3"><span class="t1">And all good spirits guard you well.</span></p><p class="p3"><span class="t1">Through the power of all Sanghas,</span></p><p class="p3"><span class="t1">May you always be at ease.</span></p>', '-48-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Sharing of Merits', 'chapter', 49);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Sharing and Aspirations', 'title', 49);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 49, '<h2>Sharing and Aspirations</h2><h2>Uddissan&#8217;ādhiṭṭhānā</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ uddissan&#8217;ādhiṭṭhāna-gāthāyo bhaṇāmase</span></p><p class="p3"><span class="t5">Iminā puñña-kammena upajjhāyā guṇ&#8217;uttarā</span></p><p class="p3"><span class="t5">Ācariy&#8217;ūpakārā ca mātāpitā ca ñātakā</span></p><p class="p3"><span class="t5">Suriyo candimā rājā guṇavantā narā&#8217;pi ca</span></p><p class="p3"><span class="t5">Brahma-mārā ca indā ca lokapālā ca devatā</span></p><p class="p3"><span class="t5">Yamo mittā manussā ca majjhattā verikā&#8217;pi ca</span></p><p class="p3"><span class="t5">Sabbe sattā sukhī hontu puññāni pakatāni me</span></p><p class="p3"><span class="t5">Sukhañ&#8217;ca tividhaṃ dentu khippaṃ pāpetha vo&#8217;mataṃ</span></p><p class="p3"><span class="t5">Iminā puñña-kammena iminā uddissena ca</span></p><p class="p3"><span class="t5">Khipp&#8217;āhaṃ sulabhe c&#8217;eva taṇh&#8217;ūpādāna-chedanaṃ</span></p><p class="p3"><span class="t5">Ye santāne hīnā dhammā yāva nibbānato mamaṃ</span></p><p class="p3"><span class="t5">Nassantu sabbadā yeva yattha jāto bhave bhave</span></p><p class="p3"><span class="t5">Ujucittaṃ satipaññā sallekho viriyamhinā</span></p><p class="p3"><span class="t5">Mārā labhantu nokāsaṃ kātuñ&#8217;ca viriyesu me</span></p><p class="p3"><span class="t5">Buddh&#8217;ādhipavaro nātho dhammo nātho varuttamo</span></p><p class="p3"><span class="t5">Nātho paccekabuddho ca saṅgho nāthottaro mamaṃ</span></p><p class="p3"><span class="t5">Tes&#8217;ottam&#8217;ānubhāvena mār&#8217;okāsaṃ labhantu mā</span></p>', '-49-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Sharing of All Merits', 'title', 50);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 50, '<h2>Sharing of All Merits</h2><h2>Sabba-patti-dāna</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ sabba-patti-dāna-gāthāyo bhaṇāmase</span></p><p class="p3"><span class="t5">Puññass&#8217;idāni katassa yān&#8217;aññāni katāni me</span></p><p class="p3"><span class="t5">Tesañ&#8217;ca bhāgino hontu satt&#8217;ānant&#8217;āppamāṇakā</span></p><p class="p3"><span class="t1">May whatever living beings</span></p><p class="p3"><span class="t1">Without measure without end</span></p><p class="p3"><span class="t1">Partake of all the merit</span></p><p class="p3"><span class="t1">From the good deeds I have done</span></p><p class="p3"><span class="t5">Ye piyā guṇavantā ca mayhaṃ mātā-pitā-dayo</span></p><p class="p3"><span class="t5">Diṭṭhā me c&#8217;āpy&#8217;adiṭṭhā vā aññe majjhatta-verino</span></p><p class="p3"><span class="t1">Those loved and full of goodness</span></p><p class="p3"><span class="t1">My mother and my father dear</span></p><p class="p3"><span class="t1">Beings seen by me and those unseen</span></p><p class="p3"><span class="t1">Those neutral and averse</span></p><p class="p3"><span class="t5">Sattā tiṭṭhanti lokasmiṃ te bhummā catu-yonikā</span></p><p class="p3"><span class="t5">Pañc&#8217;eka-catu-vokārā saṃsarantā bhav&#8217;ābhave</span></p><p class="p3"><span class="t1">Beings established in the world</span></p><p class="p3"><span class="t1">From the three planes and four grounds of birth</span></p><p class="p3"><span class="t1">With five aggregates or one or four</span></p><p class="p3"><span class="t1">Wandering on from realm to realm</span></p><p class="p3"><span class="t5">Ñātaṃ ye patti-dānam&#8217;me anumodantu te sayaṃ</span></p><p class="p3"><span class="t5">Ye c&#8217;imaṃ nappajānanti devā tesaṃ nivedayuṃ</span></p><p class="p3"><span class="t1">Those who know my act of dedication</span></p><p class="p3"><span class="t1">May they all rejoice in it</span></p><p class="p3"><span class="t1">And as for those yet unaware</span></p><p class="p3"><span class="t1">May the devas let them know</span></p><p class="p3"><span class="t5">Mayā dinnāna-puññānaṃ anumodana-hetunā</span></p><p class="p3"><span class="t5">Sabbe sattā sadā hontu averā sukha-jīvino</span></p><p class="p3"><span class="t5">Khema-ppadañ&#8217;ca pappontu tesāsā sijjhataṃ subhā</span></p><p class="p3"><span class="t1">By rejoicing in my sharing</span></p><p class="p3"><span class="t1">May all beings live at ease</span></p><p class="p3"><span class="t1">In freedom from hostility</span></p><p class="p3"><span class="t1">May their good wishes be fulfilled</span></p><p class="p3"><span class="t1">And may they all reach safety</span></p><h2><p class="p3"><span class="t5">[Thai]</span></p></h2>', '-50-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Sharing of Merits with the Departed', 'title', 51);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 51, '<h2>Sharing of Merits with the Departed</h2><h2>Peta-patti-dāna</h2><hr/><p class="p3"><span class="t5">Idaṃ me ñātinaṃ hotu</span></p><p class="p3"><span class="t5">Sukhitā hontu ñātayo<br/> Idaṃ no ñātinaṃ hotu sukhitā hontu ñātayo<br/> Idaṃ vo ñātinaṃ hotu sukhitā hontu ñātayo</span></p><p class="p3"><span class="t1">May this be for my relatives well and happy may the relatives be</span></p><p class="p3"><span class="t1">May this be for our relatives well and happy may the relatives be</span></p><p class="p3"><span class="t1">May this be for your relatives well and happy may the relatives be</span></p><h2><p class="p3"><span class="t5">[Pv 422]</span></p></h2>', '-51-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Sharing of Merits with the Devas', 'title', 52);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 52, '<h2>Sharing of Merits with the Devas</h2><h2>Devata-patti-dāna</h2><hr/><p class="p3"><span class="t5">Ettāvatā ca amhehi</span></p><p class="p3"><span class="t5">Sambhataṃ puñña-sampadaṃ</span></p><p class="p3"><span class="t5">Sabbe devā anumodantu</span></p><p class="p3"><span class="t5">Sabba-sampatti-siddhiyā</span></p><p class="p3"><span class="t5">Ettāvatā ca amhehi</span></p><p class="p3"><span class="t5">Sambhataṃ puñña-sampadaṃ</span></p><p class="p3"><span class="t5">Sabbe bhūtā anumodantu</span></p><p class="p3"><span class="t5">Sabba-sampatti-siddhiyā</span></p><p class="p3"><span class="t5">Ettāvatā ca amhehi</span></p><p class="p3"><span class="t5">Sambhataṃ puñña-sampadaṃ</span></p><p class="p3"><span class="t5">Sabbe sattā anumodantu</span></p><p class="p3"><span class="t5">Sabba-sampatti-siddhiyā</span></p><p class="p3"><span class="t1">To the extent that all of us</span></p><p class="p3"><span class="t1">Have accumulated a wealth of merits;</span></p><p class="p3"><span class="t1">In this may all devas rejoice,</span></p><p class="p3"><span class="t1">For the attainment of all fortunes.</span></p><p class="p3"><span class="t1">To the extent that all of us</span></p><p class="p3"><span class="t1">Have accumulated a wealth of merits;</span></p><p class="p3"><span class="t1">In this may all beings rejoice,</span></p><p class="p3"><span class="t1">For the attainment of all fortunes.</span></p><p class="p3"><span class="t1">To the extent that all of us</span></p><p class="p3"><span class="t1">Have accumulated a wealth of merits;</span></p><p class="p3"><span class="t1">In this may all creatures rejoice,</span></p><p class="p3"><span class="t1">For the attainment of all fortunes.</span></p><h2><p class="p3"><span class="t5">[MJG]</span></p></h2>', '-52-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Highest Honour and Aspirations', 'title', 53);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 53, '<h2>The Highest Honour and Aspirations</h2><h2>Paramāya pūjāyañ&#8217;ca paṇidhiñ&#8217;ca</h2><hr/><p class="p3"><span class="t5">Handa mayaṃ buddhapūjañ&#8217;ca paṇidhiñ&#8217;ca karomase</span></p><p class="p3"><span class="t5">Buddhaṃ jīvita-pariyantaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t1">Until life ends I go to the Buddha for refuge</span></p><p class="p3"><span class="t5">Dhammaṃ jīvita-pariyantaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t1">Until life ends I go to the Dhamma for refuge</span></p><p class="p3"><span class="t5">Saṅghaṃ jīvita-pariyantaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t1">Until life ends I go to the Saṅgha for refuge</span></p><p class="p3"><span class="t5">Iminā puñña-kammena</span></p><p class="p3"><span class="t1">By this meritorious action</span></p><p class="p3"><span class="t5">Mā me bāla-samāgamo</span></p><p class="p3"><span class="t1">May I not associate with fools</span></p><p class="p3"><span class="t5">Sataṃ samāgamo hotu</span></p><p class="p3"><span class="t1">With the wise may I associate</span></p><p class="p3"><span class="t5">Yāva nibbānapattiyā</span></p><p class="p3"><span class="t1">Until the attainment of Nibbāna</span></p><h2><p class="p3"><span class="t5">[Sri Lanka]</span></p></h2><p class="p3"><span class="t5">Yo kho bhikkhu vā bhikkhunī vā upāsako vā upāsikā vā</span></p><p class="p3"><span class="t1">Any bhikkhu bhikkhunī male or female lay follower</span></p><p class="p3"><span class="t5">Dhamm&#8217;ānudhamma-paṭipanno viharati</span></p><p class="p3"><span class="t1">Who dwells practicing according to the Dhamma</span></p><p class="p3"><span class="t5">Sāmīci-paṭipanno</span></p><p class="p3"><span class="t1">Practicing properly</span></p><p class="p3"><span class="t5">Anudhammacārī</span></p><p class="p3"><span class="t1">Behaving according to the Dhamma</span></p><p class="p3"><span class="t5">So tathāgataṃ sakkaroti garuṃ karoti māneti pūjeti apaciyati</span></p><p class="p3"><span class="t1">Respects esteems cherishes honours and pays homage to the Tathāgata</span></p><p class="p3"><span class="t5">Paramāya pūjāya</span></p><p class="p3"><span class="t1">With the highest honour</span></p><h2><p class="p3"><span class="t5">[DN 16]</span></p></h2><p class="p3"><span class="t5">Tasmā</span></p><p class="p3"><span class="t1">Therefore</span></p><p class="p3"><span class="t5">Imāya dhamm&#8217;ānudhamma-paṭipattiyā buddhaṃ pūjemi</span></p><p class="p3"><span class="t5">Paramāya pūjāya</span></p><p class="p3"><span class="t1">By this Dhamma practice according to the Dhamma</span></p><p class="p3"><span class="t1">I honour the Buddha with the highest honour</span></p><p class="p3"><span class="t5">Addhā imāya paṭipadāya jāti-jarā-byādhi-maraṇamhā parimuccissāmi</span></p><p class="p3"><span class="t1">Surely by this way of practice</span></p><p class="p3"><span class="t1">I will be free from birth ageing sickness and death</span></p><p class="p3"><span class="t5">Idaṃ me puññaṃ āsavakkhayā-vahaṃ hotu</span></p><p class="p3"><span class="t1">May my merit lead to the destruction of the taints</span></p><p class="p3"><span class="t5">Idaṃ me puññaṃ nibbānassa paccayo hotu</span></p><p class="p3"><span class="t1">May my merit be a condition for the attainment of Nibbāna</span></p><h2><p class="p3"><span class="t5">[Sri Lanka]</span></p></h2>', '-53-');
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'Appendix', 'chapter', 54);
INSERT INTO tocs (book_id, name, type, page_number) VALUES ('annya_sbs_chants', 'The Refuges and Trainings', 'title', 54);
INSERT INTO pages (bookid, page, content, paranum) VALUES  ('annya_sbs_chants', 54, '<h2>The Refuges and Trainings</h2><hr/><p class="p3"><span class="t5">Namo tassa bhagavato arahato sammāsambuddhassa</span></p><p class="p3"><span class="t1">Homage to the Blessed, Worthy, and Perfectly Enlightened One.</span></p><p class="p3"><span class="t5">Buddhaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t5">Dhammaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t5">Saṅghaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t1">To the Buddha I go for refuge.</span></p><p class="p3"><span class="t1">To the Dhamma I go for refuge.</span></p><p class="p3"><span class="t1">To the Saṅgha I go for refuge.</span></p><p class="p3"><span class="t5">Dutiyam&#8217;pi buddhaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t5">Dutiyam&#8217;pi dhammaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t5">Dutiyam&#8217;pi saṅghaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t1">For the second time, to the Buddha I go for refuge.</span></p><p class="p3"><span class="t1">For the second time, to the Dhamma I go for refuge.</span></p><p class="p3"><span class="t1">For the second time, to the Saṅgha I go for refuge.</span></p><p class="p3"><span class="t5">Tatiyam&#8217;pi buddhaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t5">Tatiyam&#8217;pi dhammaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t5">Tatiyam&#8217;pi saṅghaṃ saraṇaṃ gacchāmi</span></p><p class="p3"><span class="t1">For the third time, to the Buddha I go for refuge.</span></p><p class="p3"><span class="t1">For the third time, to the Dhamma I go for refuge.</span></p><p class="p3"><span class="t1">For the third time, to the Saṅgha I go for refuge.</span></p><p class="p3"><span class="t5">Tisaraṇa-gamanaṃ niṭṭhitaṃ</span></p><p class="p3"><span class="t1">This completes the going to the Three Refuges.</span></p><p class="p3"><span class="t5">Āma bhante</span></p><p class="p3"><span class="t1">Yes, venerable sir.</span></p>', '-54-');
